Property.Value:
set nocount on;
declare @DlgHandle UNIQUEIDENTIFIER, @Msg VARBINARY(MAX), @MsgType sysname, @StrErr NVARCHAR(3000), @Msgxml XML;
declare @tt datetime2(2), @event_type nvarchar(128), @db nvarchar(2000), @login varchar(128), @spid smallint, @sqltext nvarchar(max), @textdata nvarchar(2000), @host nvarchar(128)=null, @program nvarchar(128)=null;
while (1=1)
begin
	WAITFOR (
		RECEIVE TOP(1)
			@DlgHandle = [conversation_handle],
			@MsgType = message_type_name
		,	@Msg = message_body                    
		FROM	event_notification_q                 
	), TIMEOUT 1000
	IF (@@ROWCOUNT = 0)
	BEGIN
		--IF EXISTS(SELECT conversation_id FROM sys.conversation_endpoints WHERE conversation_handle=@DlgHandle AND state<>'CD')
		--	END CONVERSATION @DlgHandle;
		BREAK;
	END
	if (@MsgType='http://schemas.microsoft.com/SQL/Notifications/EventNotification')
	begin
		begin try
			set @Msgxml=cast(@Msg as XML);
			select 
				@tt=@Msgxml.value('(/EVENT_INSTANCE/PostTime)[1]', 'datetime2(2)'),
				@event_type=@Msgxml.value('(/EVENT_INSTANCE/EventType)[1]', 'nvarchar(128)'),
				@db=@Msgxml.value('(/EVENT_INSTANCE/DatabaseName)[1]', 'nvarchar(2000)'),
				@login=@Msgxml.value('(/EVENT_INSTANCE/LoginName)[1]', 'nvarchar(128)'),
				@spid=@Msgxml.value('(/EVENT_INSTANCE/SPID)[1]', 'smallint');
			select
				@sqltext=CASE WHEN @event_type in ('CREATE_DATABASE', 'ALTER_DATABASE', 'DROP_DATABASE') then @Msgxml.value('(/EVENT_INSTANCE/TSQLCommand/CommandText)[1]','nvarchar(max)') else @Msgxml.value('(/EVENT_INSTANCE/TextData)[1]','nvarchar(max)') END,
				@textdata=CASE WHEN @event_type in ('CREATE_DATABASE', 'ALTER_DATABASE', 'DROP_DATABASE') then @Msgxml.value('(/EVENT_INSTANCE/TextData)[1]', 'nvarchar(2000)') WHEN @event_type='AUDIT_BACKUP_RESTORE_EVENT' THEN CASE WHEN @Msgxml.value('(/EVENT_INSTANCE/Success)[1]','smallint') <> 1 THEN 'not completed' ELSE NULL END END ;
			if @event_type in ('CREATE_DATABASE', 'ALTER_DATABASE', 'DROP_DATABASE')
			begin
				if datediff(minute,@tt,sysdatetime())<=15
				begin
					select @host=[host_name], @program=[program_name] from sys.dm_exec_sessions where session_id=@spid and login_name=@login and login_time<=@tt;
				end
				else
					print('  host  program:    15 !');
			end
			else if @event_type='AUDIT_BACKUP_RESTORE_EVENT'
			begin
				select @host=@Msgxml.value('(/EVENT_INSTANCE/HostName)[1]', 'nvarchar(128)'),
					@program=@Msgxml.value('(/EVENT_INSTANCE/ApplicationName)[1]', 'nvarchar(128)')						
			end

			if (@event_type<>'AUDIT_BACKUP_RESTORE_EVENT' or @sqltext like 'restore%database%')
			begin
				declare @pr_event nvarchar(128);
				select @pr_event = CASE WHEN @event_type='AUDIT_BACKUP_RESTORE_EVENT' then 'restore_database' else @event_type end;
				print('event: '+@pr_event+'. database ['+@db+']. login: '+coalesce(@login,'')+' ; host: '+coalesce(@host,'')+' ; program: '+coalesce(@program,'')+' ; spid: '+coalesce(cast(@spid as varchar(10)),''));
			end

			--    :     ,          !
			declare @last_tt datetime2(2); 
			SELECT @last_tt=max(tt)
			FROM [sputnik].[adt].[instance_hs]
			WHERE tt between dateadd(hour,-24,@tt) and @tt 
				and db=@db;

			insert into sputnik.adt.instance_hs (tt,event_type,db,spid,[login],[host],program,sqltext,textdata)
			values(@tt,@event_type,@db,@spid,@login,@host,@program,@sqltext,@textdata);

			if (@event_type='CREATE_DATABASE' or (@event_type='AUDIT_BACKUP_RESTORE_EVENT' and @sqltext like 'restore%database%')) and @last_tt is null
				exec [sputnik].[adt].[usp_mail_eventdb] @dbname=@db, @tt=@tt, @login=@login, @hostname=@host, @program=@program, @sqlcommand=@sqltext;

			if (@event_type='DROP_DATABASE')
				exec [sputnik].[adt].[usp_mail_eventdb] @dbname=@db, @tt=@tt, @login=@login, @hostname=@host, @program=@program, @sqlcommand=@sqltext, @dropdb=1;

			set @host=null;set @program=null;
		end try
		begin catch
			SET @StrErr=N'    event_notification_q   [adt].[usp_eventnots_processor].  : ['+@db+'];  : ['+@event_type+'],  (tt): ['+convert(varchar(30),@tt,120)+'].  : '+ERROR_MESSAGE();
			--       ,       !
			--       SQL Server ( print)    (break):
			PRINT(@StrErr);
			BREAK;
			--END CONVERSATION @DlgHandle WITH ERROR = 10 DESCRIPTION=@StrErr;
		end catch
	end
	----     ,   !
	--ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
	--	END CONVERSATION @DlgHandle;
	--ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
	--	END CONVERSATION @DlgHandle;
end

begin
	set nocount on;
	declare @strsubject varchar(100),@dbowner nvarchar(200),@StrErr NVARCHAR(3000);

	begin try
		select @strsubject=case when @dropdb=0 then 'New' else 'DROP' end +' database detected on ' + @@SERVERNAME;
		select @dbowner=suser_sname(owner_sid) from sys.databases where name=@dbname;

		declare @tableHTML  nvarchar(max);
		set @tableHTML =
			N'<H1>'+case when @dropdb=0 then 'Create new' else 'Drop' end + ' database on - ' + @@SERVERNAME +'</H1>' +
			N'<table border="1">' +
			N'<tr><th>Create date&nbsp;&nbsp;&nbsp;</th>'+
			N'<th>DB Name&nbsp;&nbsp;&nbsp;</th><th>Creator&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Owner&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Hostname&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Program&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>SQL command&nbsp;&nbsp;&nbsp;</th></tr>' +
			CAST ( ( SELECT td = @tt, '',
							td = @dbname, '',
							td = @login, '',
							td = coalesce(@dbowner,''), '',
							td = coalesce(@hostname,''), '',
							td = coalesce(@program,''), '',
							td = coalesce(@sqlcommand,'') 
						FOR XML PATH('tr'), TYPE 
			) AS NVARCHAR(MAX) ) +
			N'</table>' ;

			EXEC msdb.dbo.sp_send_dbmail
			--@from_address='test@test.com',
			@recipients='dba-info@ntsmail.ru',
			@subject = @strsubject,
			@body = @tableHTML,
			@body_format = 'HTML' ,
			@profile_name='sql-info'
	end try
	begin catch
		SET @StrErr=N'     e-mail,  [adt].[usp_mail_eventdb].  : ['+@dbname+']; : ['+@login+'],  (tt): ['+convert(varchar(30),@tt,120)+'];  suser_name: ['+cast(suser_name() as nvarchar(500))+/*'];  suser_Sname: ['+cast(suser_sname() as nvarchar(500))+*/'].  : '+ERROR_MESSAGE();
				--       ,       !
				--       SQL Server ( print)    (break):
		PRINT(@StrErr);
	end catch
end

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 14.11.2018 (1.000)
-- Description: 
-- Update:		      .         !
				
				
-- ============================================= */
create procedure [adt].[usp_mail_eventdb]
	@dbname nvarchar(2000),
	@tt datetime2(2),
	@login nvarchar(600),
	@hostname nvarchar(300)=null,
	@program nvarchar(300)=null,
	@sqlcommand nvarchar(max)='',
	@dropdb bit = 0
as

begin
	set nocount on;
	declare @strsubject varchar(100),@dbowner nvarchar(200),@StrErr NVARCHAR(3000);

	begin try
		select @strsubject='New database detected on ' + @@SERVERNAME;
		select @dbowner=suser_sname(owner_sid) from sys.databases where name=@dbname;

		declare @tableHTML  nvarchar(max);
		set @tableHTML =
			N'<H1>Create new database on - ' + @@SERVERNAME +'</H1>' +
			N'<table border="1">' +
			N'<tr><th>Create date&nbsp;&nbsp;&nbsp;</th>'+
			N'<th>DB Name&nbsp;&nbsp;&nbsp;</th><th>Creator&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Owner&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Hostname&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>Program&nbsp;&nbsp;&nbsp;</th>' +
			N'<th>SQL command&nbsp;&nbsp;&nbsp;</th></tr>' +
			CAST ( ( SELECT td = @tt, '',
							td = @dbname, '',
							td = @login, '',
							td = coalesce(@dbowner,''), '',
							td = coalesce(@hostname,''), '',
							td = coalesce(@program,''), '',
							td = coalesce(@sqlcommand,'') 
						FOR XML PATH('tr'), TYPE 
			) AS NVARCHAR(MAX) ) +
			N'</table>' ;

			EXEC msdb.dbo.sp_send_dbmail
			--@from_address='test@test.com',
			@recipients='dba-info@ntsmail.ru',
			@subject = @strsubject,
			@body = @tableHTML,
			@body_format = 'HTML' ,
			@profile_name='sql-info'
	end try
	begin catch
		SET @StrErr=N'     e-mail,  [adt].[usp_mail_newdb].  : ['+@dbname+']; : ['+@login+'],  (tt): ['+convert(varchar(30),@tt,120)+'];  suser_name: ['+cast(suser_name() as nvarchar(500))+/*'];  suser_Sname: ['+cast(suser_sname() as nvarchar(500))+*/'].  : '+ERROR_MESSAGE();
				--       ,       !
				--       SQL Server ( print)    (break):
		PRINT(@StrErr);
	end catch
end

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 27.04.2014 (1.000)
-- Description: 
-- Update:		      .     !
				
				
-- ============================================= */
create procedure [adt].[usp_mail_newdb]
	@dbname nvarchar(2000),
	@tt datetime2(2),
	@login nvarchar(600),
	@hostname nvarchar(300)=null,
	@program nvarchar(300)=null,
	@sqlcommand nvarchar(max)=''
as

BEGIN

	--**********************************************
	--!         1 ,     1!!!
	--**********************************************

	SET NOCOUNT ON;

		--  , 
	declare @spid varchar(30), @login_time varchar(30), @host varchar(100), @host_ip varchar(100),
	@host_Pid varchar(30), @login nvarchar(400), @program nvarchar(600);

	select top 1 
		@spid=cast(a.session_id as varchar(30)), 
		@login_time=convert(varchar(30),a.login_time,126), 
		@host=a.host_name, 
		@host_pid=a.host_process_id, 
		@host_ip=b.client_net_address,
		@login=a.login_name,
		@program=a.program_name
	from sys.dm_exec_sessions a , sys.dm_exec_connections b 
	where a.session_id=b.session_id and a.session_id=@@SPID;

	PRINT(' <<<   [anz].[usp_dyn_check]. tt='+convert(varchar(30),sysdatetime(),126));

	PRINT('  : SPID='+@spid+'; Login=['+@login+']; LoginTime='+@login_time+'; HostName='+@host+'; HostIP='+@host_ip+'; HostProcessID='+@host_pid+'; Program=['+@program+'];');


	-- :
	IF DB_ID(@DB_Name) IS NULL
	BEGIN
		PRINT('    : '+@DB_Name);
		PRINT ('      @DB_Name');
		PRINT(' >>>   [anz].[usp_dyn_check]. tt='+convert(varchar(30),sysdatetime(),126));
		RETURN -1;
	END

	declare @StrErr nvarchar(2000);
	declare @sqlstr nvarchar(max),@sqlstr2 nvarchar(max);
	declare @tt datetime2(2),@tt_end datetime2(2),@elapsed_format varchar(20), @tt_str varchar(60);
	set @tt=sysdatetime();
	set @tt_str=replace(convert(varchar(60),@tt,120),' ','_');
	set @tt_str=replace(@tt_str,'-','');
	set @tt_str=replace(@tt_str,':','');
	--print @tt_str;

	set @sqlstr='use ['+@DB_Name+']; 
	IF NOT EXISTS (SELECT TOP 1 [FileName] FROM [dbo].[Config] WHERE [FileName] = ''DynamicallyUpdated'')
	BEGIN
		IF OBJECT_ID(''dbo.Config_cleaned'') IS NOT NULL
			DROP TABLE dbo.Config_cleaned;
		SELECT * INTO dbo.Config_cleaned FROM dbo.Config; 
		IF EXISTS (SELECT TOP 1 [FileName] FROM [dbo].[Config] WHERE [FileName] LIKE ''%dyn%'')
		BEGIN
			DELETE FROM dbo.Config WHERE [FileName] LIKE ''%dyn%'';
			PRINT(''--  dbo.Config  .'');
		END
		ELSE
		BEGIN
			PRINT(''--  dbo.Config   : '');
			PRINT(''--- "%dyn%"    dbo.Config, : ['+@DB_Name+']'');
		END
	END
	ELSE
	BEGIN
		PRINT(''--  dbo.Config   : '');
		PRINT(''---  "DynamicallyUpdated"   dbo.Config, : ['+@DB_Name+']'');
	END
	';

	IF @debug=1
	BEGIN 
		PRINT @sqlstr;
	END
	ELSE 
	BEGIN
		BEGIN TRY
			EXEC(@sqlstr);
		END TRY
		BEGIN CATCH
			set @StrErr='    dbo.Config   ['+@DB_Name+'].  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
			RAISERROR(@StrErr,11,1) WITH LOG
			set @StrErr='  !            !';
			RAISERROR(@StrErr,11,1) WITH LOG
			PRINT(' >>>   [anz].[usp_dyn_check]. tt='+convert(varchar(30),sysdatetime(),126));
			RETURN -2;
		END CATCH
	END
	PRINT(' >>>   [anz].[usp_dyn_check]. tt='+convert(varchar(30),sysdatetime(),126));
END

BEGIN
	-- 1  !
	--**********************************************
	--!         1 (  )!!!
	--**********************************************

	SET NOCOUNT ON;

	--  , 
	declare @spid varchar(30), @login_time varchar(30), @host varchar(100), @host_ip varchar(100),
	@host_Pid varchar(30), @login nvarchar(400), @program nvarchar(600);

	select top 1 
		@spid=cast(a.session_id as varchar(30)), 
		@login_time=convert(varchar(30),a.login_time,126), 
		@host=a.host_name, 
		@host_pid=a.host_process_id, 
		@host_ip=b.client_net_address,
		@login=a.login_name,
		@program=a.program_name
	from sys.dm_exec_sessions a , sys.dm_exec_connections b 
	where a.session_id=b.session_id and a.session_id=@@SPID;

	PRINT(' <<<   anz.usp_retab_step1. tt='+convert(varchar(30),sysdatetime(),126));

	PRINT('  : SPID='+@spid+'; Login=['+@login+']; LoginTime='+@login_time+'; HostName='+@host+'; HostIP='+@host_ip+'; HostProcessID='+@host_pid+'; Program=['+@program+'];');

	-- :
	IF DB_ID(@DB_Name) IS NULL
	BEGIN
		PRINT('    : '+@DB_Name);
		PRINT ('      @DB_Name');
		PRINT(' >>>   anz.usp_retab_step1. tt='+convert(varchar(30),sysdatetime(),126));
		RETURN -1;
	END

	declare @tt datetime2(2),@tt_end datetime2(2),@elapsed_format varchar(20);
	declare @tablesql nvarchar(600);
	declare @StrErr nvarchar(2000);
	declare @tt_str varchar(60);
	declare @sqlstr nvarchar(4000),@paramsstr nvarchar(800);
	set @tt=sysdatetime();
	set @tt_str=replace(convert(varchar(60),@tt,126),' ','_');
	set @tt_str=replace(@tt_str,'-','');
	set @tt_str=replace(@tt_str,':','');
	--print @tt_str;

	set @sqlstr=N'use ['+@DB_Name+']; select @tablesql_OUT=TABLE_NAME from INFORMATION_SCHEMA.VIEW_TABLE_USAGE WHERE VIEW_NAME = @table1c_IN ;';
	set @paramsstr='@table1c_IN nvarchar(1000), @tablesql_OUT nvarchar(600) OUT';
	exec sp_executesql
		@stmt=@sqlstr,
		@params=@paramsstr,
		@table1c_IN=@table1c,
		@tablesql_OUT=@tablesql OUT;

	IF @tablesql is not null
	BEGIN
		--:    *_prod.
		--        ...
		declare @Tname_prod nvarchar(800);
		set @Tname_prod=QUOTENAME(@DB_Name)+'.[dbo].'+QUOTENAME(@tablesql+CASE WHEN @tablesql LIKE '%#_prod' ESCAPE '#' THEN '' ELSE '_prod' END);
		IF OBJECT_ID(@Tname_prod) IS NOT NULL
		BEGIN
			declare @prod_cnt bigint;
			set @sqlstr=N'select @prod_cnt_OUT=count_big(*) from '+@Tname_prod;
			set @paramsstr='@prod_cnt_OUT bigint OUT';
			exec sp_executesql
				@stmt=@sqlstr,
				@params=@paramsstr,
				@prod_cnt_OUT=@prod_cnt OUT;
			IF @prod_cnt>0 
			BEGIN
				PRINT(' '+@Tname_prod+'  . _: '+cast(@prod_cnt as varchar(36)));
				PRINT(' :   1 ['+@table1c+']    !     anz.usp_retab_step2    !    ,   !');
				PRINT(' >>>   anz.usp_retab_step1. tt='+convert(varchar(30),sysdatetime(),126));
				RETURN -2;
			END
		END
		--    _prod
		set @sqlstr='use ['+@DB_Name+'];
			if object_id(''[dbo].['+@tablesql+'_prod]'') is not null
				exec sp_rename @objname = '''+@tablesql+'_prod'' ,  @newname =  '''+@tablesql+'_archive_prod_'+@tt_str+''' ,
				@objtype = ''object'' ;
			exec sp_rename @objname = '''+@tablesql+''' ,  @newname =  '''+@tablesql+'_prod'' ,
				@objtype = ''object'' ;
		'
		IF @debug=1
		BEGIN
			PRINT '/*     _prod */';
			print @sqlstr;
		END
		ELSE
		BEGIN TRY
			set @tt=SYSDATETIME() ;
			PRINT ':     ['+@tablesql+'] >>> ['+@tablesql+'_prod]';
			EXEC (@sqlstr);
			set @tt_end=SYSDATETIME() ;
			set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
			PRINT ' ['+@elapsed_format+']:     ['+@tablesql+'_prod]';
		END TRY
		BEGIN CATCH
			set @StrErr='        [sp_rename]!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
			RAISERROR(@StrErr,11,1) WITH LOG
		END CATCH

		--     (    )
		set @sqlstr='use ['+@DB_Name+'];
			SELECT *
			INTO [dbo].['+@tablesql+']
			FROM [dbo].['+@tablesql+'_prod]
			WHERE 1=0;
		';
		IF @debug=1
		BEGIN
			PRINT '/*      (    ) */';
			print @sqlstr;
		END
		ELSE
		BEGIN
			BEGIN TRY
				set @tt=SYSDATETIME() ;
				PRINT ':    ['+@tablesql+'] ';
				EXEC (@sqlstr);
				set @tt_end=SYSDATETIME() ;
				set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
				PRINT ' ['+@elapsed_format+']:    ['+@tablesql+']';
			END TRY
			BEGIN CATCH
				set @StrErr='       (select * from ... where 1=0)!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
				RAISERROR(@StrErr,11,1) WITH LOG
			END CATCH
		END
	END;
	ELSE
	BEGIN
		PRINT (' ['+@table1c+']  SQL  !!!');
		PRINT ('      @Table1C');
	END
	PRINT(' >>>   anz.usp_retab_step1. tt='+convert(varchar(30),sysdatetime(),126));
END

BEGIN

	-- 2 -  
	--**********************************************
	--!         1     1 (  )!!!
	--**********************************************

	SET NOCOUNT ON;

		--  , 
	declare @spid varchar(30), @login_time varchar(30), @host varchar(100), @host_ip varchar(100),
	@host_Pid varchar(30), @login nvarchar(400), @program nvarchar(600);

	select top 1 
		@spid=cast(a.session_id as varchar(30)), 
		@login_time=convert(varchar(30),a.login_time,126), 
		@host=a.host_name, 
		@host_pid=a.host_process_id, 
		@host_ip=b.client_net_address,
		@login=a.login_name,
		@program=a.program_name
	from sys.dm_exec_sessions a , sys.dm_exec_connections b 
	where a.session_id=b.session_id and a.session_id=@@SPID;

	PRINT(' <<<   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));

	PRINT('  : SPID='+@spid+'; Login=['+@login+']; LoginTime='+@login_time+'; HostName='+@host+'; HostIP='+@host_ip+'; HostProcessID='+@host_pid+'; Program=['+@program+'];');


	-- :
	IF DB_ID(@DB_Name) IS NULL
	BEGIN
		PRINT('    : '+@DB_Name);
		PRINT ('      @DB_Name');
		PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
		RETURN -1;
	END

	declare @StrErr nvarchar(2000);
	declare @sqlstr nvarchar(max),@sqlstr2 nvarchar(max);
	declare @tt datetime2(2),@tt_end datetime2(2),@elapsed_format varchar(20), @tt_str varchar(60);
	set @tt=sysdatetime();
	set @tt_str=replace(convert(varchar(60),@tt,120),' ','_');
	set @tt_str=replace(@tt_str,'-','');
	set @tt_str=replace(@tt_str,':','');
	--print @tt_str;

	declare @Tname nvarchar(600), @paramsstr nvarchar(800);

	set @sqlstr='use ['+@DB_Name+']; select @tablesql_OUT=REPLACE(TABLE_NAME,''_prod'','''') from INFORMATION_SCHEMA.VIEW_TABLE_USAGE WHERE VIEW_NAME = @table1c_IN ;';
	set @paramsstr='@table1c_IN nvarchar(1000), @tablesql_OUT nvarchar(600) OUT';
	exec sp_executesql
		@stmt=@sqlstr,
		@params=@paramsstr,
		@table1c_IN=@table1c,
		@tablesql_OUT=@Tname OUT;


	IF @Tname IS NOT NULL
	BEGIN
		--:    *_prod:
		declare @Tname_prod nvarchar(800);
		set @Tname_prod=QUOTENAME(@DB_Name)+'.[dbo].'+QUOTENAME(@Tname+'_prod');
		IF OBJECT_ID(@Tname_prod) IS NULL
		BEGIN
			PRINT('   : '+@Tname_prod);
			PRINT(' :     1,    anz.usp_retab_step1,   anz.usp_retab_step2  .    ,   !');
			PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
			RETURN -2;
		END	

		--  
		--  *_prod     *
		--    

		if object_id('tempdb.dbo.#tmp_clmns') is not null
			drop table #tmp_clmns;
		create table #tmp_clmns(ColumnName nvarchar(600), cmd_SQL nvarchar(max));
		SET @sqlstr='use ['+@DB_Name+'];
		INSERT INTO #tmp_clmns(ColumnName,cmd_SQL)
		SELECT c_new.name as ColumnName,
			CASE 
				WHEN c_prod.column_id IS NULL THEN ''ALTER TABLE [dbo].['+@Tname+'_prod] ADD [''+c_new.name+''] ''+c_new.type+c_new.type_length+c_new.ColNull+c_new.ColDef+'';''
				ELSE ''ALTER TABLE [dbo].['+@Tname+'_prod] ALTER COLUMN [''+c_new.name+''] ''+c_new.type+c_new.type_length+c_prod.ColNull+'';''
			END as cmd_SQL
		FROM
			(select c.name,c.column_id,ct.name as type,
				CASE 
						WHEN ct.name like ''n%char'' THEN ''(''+CAST(c.max_length/2 as varchar(30))+'')''
						WHEN ct.name IN (''char'',''varchar'',''binary'') THEN ''(''+CAST(c.max_length as varchar(30))+'')''
						WHEN ct.name like ''numeric'' THEN ''(''+CAST(c.precision as varchar(30))+'',''+CAST(c.scale as varchar(30))+'')''
						ELSE ''''
					END AS type_length,
				CASE 
					WHEN c.is_nullable=0 THEN '' NOT NULL ''
					ELSE '' NULL ''
				END AS ColNull
			 from sys.columns c inner join sys.types ct on c.user_type_id=ct.user_type_id  where object_id=OBJECT_ID('''+@Tname+'_prod'')) c_prod
		FULL OUTER JOIN 
			(select c.name,c.column_id, ct.name as type, 
				CASE 
					WHEN ct.name like ''n%char'' THEN ''(''+CAST(c.max_length/2 as varchar(30))+'')''
					WHEN ct.name IN (''char'',''varchar'',''binary'') THEN ''(''+CAST(c.max_length as varchar(30))+'')''
					WHEN ct.name like ''numeric'' THEN ''(''+CAST(c.precision as varchar(30))+'',''+CAST(c.scale as varchar(30))+'')''
					ELSE ''''
				END AS type_length,
				CASE 
						WHEN c.is_nullable=1 THEN ''''
						WHEN ct.name like ''date%'' THEN '' default ''''1753-01-01 00:00:00.000'''' ''
						WHEN ct.name like ''binary'' THEN '' default 0x0 ''
						WHEN ct.name like ''%char'' THEN '' default '''''''' ''
						WHEN ct.name like ''%int'' OR ct.name in (''numeric'', ''bit'') THEN '' default 0 ''
						ELSE ''''
				END AS ColDef,
				CASE 
					WHEN c.is_nullable=0 THEN '' NOT NULL ''
					ELSE '' NULL ''
				END AS ColNull
					from sys.columns c
					inner join sys.types ct
					on c.user_type_id=ct.user_type_id
				where object_id=OBJECT_ID('''+@Tname+''')
		)c_new
			ON c_prod.name=c_new.name
		WHERE
			(c_prod.column_id IS NULL OR (c_prod.type<>c_new.type or c_prod.type_length<>c_new.type_length))
			AND OBJECT_ID('''+@Tname+''') IS NOT NULL;
		';
		EXEC(@sqlstr);

		IF EXISTS (select top 1 * from #tmp_clmns)
		BEGIN
			IF @debug=1
			BEGIN
				PRINT '/*    ::*/';
			END

				declare @c_name nvarchar(200), @c_sqlcmd nvarchar(2000);
				declare C CURSOR FOR
				select * from #tmp_clmns;
				OPEN C;
				FETCH NEXT FROM C INTO @c_name, @c_sqlcmd;
				WHILE @@FETCH_STATUS=0
				BEGIN
					set @c_sqlcmd='use ['+@DB_Name+'];
					'+@c_sqlcmd;

					IF @debug=1
					BEGIN 
						PRINT @c_sqlcmd;
					END
					ELSE 
					BEGIN
						BEGIN TRY
							set @tt=SYSDATETIME() ;
							PRINT ':   ['+@c_name+']    ['+@Tname+'_prod]';
							EXEC(@c_sqlcmd);
							set @tt_end=SYSDATETIME() ;
							set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
							PRINT ' ['+@elapsed_format+']:   ['+@c_name+']    ['+@Tname+'_prod]';
						END TRY
						BEGIN CATCH
							set @StrErr='    ['+@c_name+']    ['+@Tname+'_prod]!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
							RAISERROR(@StrErr,11,1) WITH LOG
							set @StrErr='  !            !';
							RAISERROR(@StrErr,11,1) WITH LOG
							PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
							RETURN -3;
						END CATCH
					END
					FETCH NEXT FROM C INTO @c_name, @c_sqlcmd;
				END
				CLOSE C;
				DEALLOCATE C;

		END
		ELSE
		BEGIN
			PRINT ('    !');
		END

			--     !:
		if object_id('tempdb.dbo.#tmp_idxs') is not null
			drop table #tmp_idxs;
		create table #tmp_idxs(DB nvarchar(400), TableName nvarchar(600), index_create_statement nvarchar(max), index_id int, index_name nvarchar(600));
		SET @sqlstr='use ['+@DB_Name+'];
		declare @t1 nvarchar(600), @t2 nvarchar(600);
		select @t1='''+@Tname+'_prod'', @t2='''+@Tname+''';

		;with src1 as(
			select OBJECT_NAME(object_id) as TableName, count(*) over (partition by object_id) as Index_cnt, name as IndexName, index_id as IndexID, type as IndexType
			from sys.indexes 
			where object_id=OBJECT_ID(@t1) 
				and index_id>1 
				and name not like ''_Add%''
			--order by name;
		),
		src2 as(
			select OBJECT_NAME(object_id) as TableName, count(*) over (partition by object_id) as Index_cnt, name as IndexName, index_id as IndexID, type as IndexType
			from sys.indexes 
			where object_id=OBJECT_ID(@t2) 
				and index_id>1 
				and name not like ''_Add%''
			--order by name;
		)
		INSERT INTO #tmp_idxs (DB, TableName, index_create_statement,index_id,index_name)
		SELECT 
			DB_NAME() AS DB,
			sc.name + ''.'' + @t1 AS TableName,
			CASE si.index_id WHEN 0 THEN ''/* No create statement (Heap) */''
			ELSE 
				CASE is_primary_key WHEN 1 THEN
					''ALTER TABLE '' + QUOTENAME(sc.name) + ''.'' + QUOTENAME(@t1) + '' ADD CONSTRAINT '' + QUOTENAME(si.name) + '' PRIMARY KEY '' +
						CASE WHEN si.index_id > 1 THEN ''NON'' ELSE '''' END + ''CLUSTERED ''
					ELSE ''CREATE '' + 
						CASE WHEN si.is_unique = 1 then ''UNIQUE '' ELSE '''' END +
						CASE WHEN si.index_id > 1 THEN ''NON'' ELSE '''' END + ''CLUSTERED '' +
						''INDEX '' + QUOTENAME(si.name) + '' ON '' + QUOTENAME(sc.name) + ''.'' + QUOTENAME(@t1) + '' ''
				END +
				/* key def */ ''('' + key_definition + '')'' +
				/* includes */ CASE WHEN include_definition IS NOT NULL THEN 
					'' INCLUDE ('' + include_definition + '')''
					ELSE ''''
				END +
				/* filters */ CASE WHEN filter_definition IS NOT NULL THEN 
					'' WHERE '' + filter_definition ELSE ''''
				END +
				/* with clause - compression goes here */
				CASE WHEN row_compression_partition_list IS NOT NULL OR page_compression_partition_list IS NOT NULL 
					THEN '' WITH ('' +
						CASE WHEN row_compression_partition_list IS NOT NULL THEN
							''DATA_COMPRESSION = ROW '' + CASE WHEN psc.name IS NULL THEN '''' ELSE + '' ON PARTITIONS ('' + row_compression_partition_list + '')'' END
						ELSE '''' END +
						CASE WHEN row_compression_partition_list IS NOT NULL AND page_compression_partition_list IS NOT NULL THEN '', '' ELSE '''' END +
						CASE WHEN page_compression_partition_list IS NOT NULL THEN
							''DATA_COMPRESSION = PAGE '' + CASE WHEN psc.name IS NULL THEN '''' ELSE + '' ON PARTITIONS ('' + page_compression_partition_list + '')'' END
						ELSE '''' END
					+ '')''
					ELSE ''''
				END +
				/* ON where? filegroup? partition scheme? */
				'' ON '' + CASE WHEN psc.name is null 
					THEN ISNULL(QUOTENAME(fg.name),'''')
					ELSE psc.name + '' ('' + partitioning_column.column_name + '')'' 
					END
				+ '';''
			END AS index_create_statement,
			si.index_id,
			si.name AS index_name
		FROM 
		(
			select s2.TableName, s2.IndexName, s2.IndexID, s2.IndexType
				--, s1.IndexID as IndexID_old
			from src2 as s2
			left join src1 as s1
				on s2.IndexName=s1.IndexName
			where
				s1.IndexName IS NULL
				--OR s2.IndexID<>s1.IndexID
		) src_fin';
		set @sqlstr2='
		INNER JOIN sys.indexes AS si
			ON src_fin.IndexID=si.index_id AND src_fin.TableName=OBJECT_NAME(si.object_id)
		JOIN sys.tables AS t ON si.object_id=t.object_id
		JOIN sys.schemas AS sc ON t.schema_id=sc.schema_id
		LEFT JOIN sys.dm_db_index_usage_stats AS stat ON 
			stat.database_id = DB_ID() 
			and si.object_id=stat.object_id 
			and si.index_id=stat.index_id
		LEFT JOIN sys.partition_schemes AS psc ON si.data_space_id=psc.data_space_id
		LEFT JOIN sys.partition_functions AS pf ON psc.function_id=pf.function_id
		LEFT JOIN sys.filegroups AS fg ON si.data_space_id=fg.data_space_id
		/* Key list */ OUTER APPLY ( SELECT STUFF (
			(SELECT '', '' + QUOTENAME(c.name) +
				CASE ic.is_descending_key WHEN 1 then '' DESC'' ELSE '''' END
			FROM sys.index_columns AS ic 
			JOIN sys.columns AS c ON 
				ic.column_id=c.column_id  
				and ic.object_id=c.object_id
			WHERE ic.object_id = si.object_id
				and ic.index_id=si.index_id
				and ic.key_ordinal > 0
			ORDER BY ic.key_ordinal FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''),1,2,'''')) AS keys ( key_definition )
		/* Partitioning Ordinal */ OUTER APPLY (
			SELECT MAX(QUOTENAME(c.name)) AS column_name
			FROM sys.index_columns AS ic 
			JOIN sys.columns AS c ON 
				ic.column_id=c.column_id  
				and ic.object_id=c.object_id
			WHERE ic.object_id = si.object_id
				and ic.index_id=si.index_id
				and ic.partition_ordinal = 1) AS partitioning_column
		/* Include list */ OUTER APPLY ( SELECT STUFF (
			(SELECT '', '' + QUOTENAME(c.name)
			FROM sys.index_columns AS ic 
			JOIN sys.columns AS c ON 
				ic.column_id=c.column_id  
				and ic.object_id=c.object_id
			WHERE ic.object_id = si.object_id
				and ic.index_id=si.index_id
				and ic.is_included_column = 1
			ORDER BY c.name FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''),1,2,'''')) AS includes ( include_definition )
		/* Partitions */ OUTER APPLY ( 
			SELECT 
				COUNT(*) AS partition_count,
				CAST(SUM(ps.in_row_reserved_page_count)*8./1024./1024. AS NUMERIC(32,1)) AS reserved_in_row_GB,
				CAST(SUM(ps.lob_reserved_page_count)*8./1024./1024. AS NUMERIC(32,1)) AS reserved_LOB_GB,
				SUM(ps.row_count) AS row_count
			FROM sys.partitions AS p
			JOIN sys.dm_db_partition_stats AS ps ON
				p.partition_id=ps.partition_id
			WHERE p.object_id = si.object_id
				and p.index_id=si.index_id
			) AS partition_sums
		/* row compression list by partition */ OUTER APPLY ( SELECT STUFF (
			(SELECT '', '' + CAST(p.partition_number AS VARCHAR(32))
			FROM sys.partitions AS p
			WHERE p.object_id = si.object_id
				and p.index_id=si.index_id
				and p.data_compression = 1
			ORDER BY p.partition_number FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''),1,2,'''')) AS row_compression_clause ( row_compression_partition_list )
		/* data compression list by partition */ OUTER APPLY ( SELECT STUFF (
			(SELECT '', '' + CAST(p.partition_number AS VARCHAR(32))
			FROM sys.partitions AS p
			WHERE p.object_id = si.object_id
				and p.index_id=si.index_id
				and p.data_compression = 2
			ORDER BY p.partition_number FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''),1,2,'''')) AS page_compression_clause ( page_compression_partition_list )
		WHERE 
			si.type IN (/*0,1,*/2) /* heap, clustered, nonclustered */
		OPTION (RECOMPILE);';
		--SELECT(@sqlstr+@sqlstr2);
		EXEC(@sqlstr+@sqlstr2);
		IF NOT EXISTS(select top 1 * from #tmp_idxs)
		BEGIN
			PRINT ('    !');
		END
		ELSE
		BEGIN
			IF @debug=1
			BEGIN
				PRINT '/*    ::*/';
			END
			DECLARE @TableName nvarchar(600),@index_name nvarchar(600), @index_cmd nvarchar(max);
			DECLARE ix CURSOR FOR
			SELECT DISTINCT 
				TableName,index_name, index_create_statement
			FROM #tmp_idxs;
			OPEN ix;
			FETCH NEXT FROM ix INTO @TableName,@index_name, @index_cmd;
			WHILE @@FETCH_STATUS=0
			BEGIN
				set @index_cmd='use ['+@DB_Name+'];
				'+@index_cmd;
				IF @debug=1
				BEGIN
					PRINT('--  '+QUOTENAME(@index_name)+'   '+QUOTENAME(@TableName));
					PRINT(@index_cmd);
				END
				ELSE
				BEGIN
					BEGIN TRY
						set @tt=SYSDATETIME() ;
						PRINT ':   '+QUOTENAME(@index_name)+'    '+QUOTENAME(@TableName);
						EXEC(@index_cmd);
						set @tt_end=SYSDATETIME() ;
						set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
						PRINT ' ['+@elapsed_format+']:   '+QUOTENAME(@index_name)+'    '+QUOTENAME(@TableName);
					END TRY
					BEGIN CATCH
						set @StrErr='    '+QUOTENAME(@index_name)+'    '+QUOTENAME(@TableName)+'!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
						RAISERROR(@StrErr,11,1) WITH LOG
						set @StrErr='  !            !';
							RAISERROR(@StrErr,11,1) WITH LOG
							PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
							RETURN -4;
					END CATCH
				END
				FETCH NEXT FROM ix INTO @TableName,@index_name, @index_cmd;
			END
			CLOSE ix;
			DEALLOCATE ix;
		END

		IF @debug=1
		BEGIN
			PRINT ' ';
			PRINT '/*      */';
		END
		--  ,    !
		SET @sqlstr='use ['+@DB_Name+'];
				if object_id(''[dbo].['+@Tname+'_empty]'') is not null
					exec sp_rename @objname = '''+@Tname+'_empty'' ,  @newname =  '''+@Tname+'_archive_empty_'+@tt_str+''',
					@objtype = ''object'' ;
				exec sp_rename @objname = '''+@Tname+''' ,  @newname =  '''+@Tname+'_empty'',
					@objtype = ''object'' ;
			';
		IF @debug=1
		BEGIN
			PRINT @sqlstr;
			PRINT ' ';
			PRINT '/*     */';
		END
		ELSE
		BEGIN 
			BEGIN TRY
				set @tt=SYSDATETIME() ;
				PRINT ':     '+QUOTENAME(@Tname)+' >>> ['+@Tname+'_empty]';
				EXEC(@sqlstr);
				set @tt_end=SYSDATETIME() ;
				set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
				PRINT ' ['+@elapsed_format+']:     ['+@Tname+'_empty]';
			END TRY
			BEGIN CATCH
				set @StrErr='        [sp_rename]!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
				RAISERROR(@StrErr,11,1) WITH LOG
				set @StrErr='  !            !';
				RAISERROR(@StrErr,11,1) WITH LOG
				PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
				RETURN -5;
			END CATCH
		END

		--   :  *_prod  *
		SET @sqlstr='use ['+@DB_Name+'];
			IF OBJECT_ID('''+@Tname+'_prod'') IS NOT NULL
				exec sp_rename @objname = '''+@Tname+'_prod'' ,  @newname =  '''+@Tname+''' ,
					@objtype = ''object'' ;
		';
		IF @debug=1
		BEGIN
			PRINT @sqlstr;
		END
		ELSE
		BEGIN 
			BEGIN TRY
				set @tt=SYSDATETIME() ;
				PRINT ':     () ['+@Tname+'_prod] >>> '+QUOTENAME(@Tname);
				EXEC(@sqlstr);
				set @tt_end=SYSDATETIME() ;
				set @elapsed_format=CAST(DATEADD(second,(datediff(second,@tt,@tt_end)), CAST('00:00:00' AS TIME(0))) as VARCHAR(20));
				PRINT ' ['+@elapsed_format+']:     () '+QUOTENAME(@Tname);
			END TRY
			BEGIN CATCH
				set @StrErr='      ()   [sp_rename]!  : '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
				RAISERROR(@StrErr,11,1) WITH LOG
				set @StrErr='  !            !';
				RAISERROR(@StrErr,11,1) WITH LOG
				PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
				RETURN -5;
			END CATCH
		END
	END
	ELSE
	BEGIN
		PRINT (' ['+@table1c+']  SQL  !!!');
		PRINT ('      @Table1C');
	END
	PRINT(' >>>   anz.usp_retab_step2. tt='+convert(varchar(30),sysdatetime(),126));
END

begin SET NOCOUNT ON; IF OBJECT_ID('Tempdb..#T') IS NOT NULL DROP TABLE #T; CREATE TABLE #T (DB nvarchar(300), CreateDate datetime2(2), UsedType varchar(4), LastUsed datetime2(2), Cnt numeric(19,0));
declare @DB nvarchar(300);
declare DB cursor for select distinct d.[name] AS DB from sys.databases d where d.database_id>4 and d.name<>'sputnik' and d.[state]=0 and (d.name like @dbfilter or @dbfilter is null) ;
open DB; fetch next from DB into @DB; while @@FETCH_STATUS=0 BEGIN INSERT INTO #T (DB, CreateDate , UsedType , LastUsed , Cnt ) EXEC(N' SET NOCOUNT ON; USE ['+@DB+'];
-- ! IF OBJECT_ID(''tempdb..#T_LastUsed'') IS NOT NULL DROP TABLE #T_LastUsed; SELECT DISTINCT max(coalesce(last_user_seek,''19000101 00:00:00'')) over() as Seek, max(coalesce(last_user_scan,''19000101 00:00:00'')) over() as Scan, max(coalesce(last_user_lookup,''19000101 00:00:00'')) over() as Lkp, max(coalesce(last_user_update,''19000101 00:00:00'')) over() as Upd, sum(coalesce(user_seeks,0)) over () as Seek_cnt, sum(coalesce(user_scans,0)) over () as Scan_cnt, sum(coalesce(user_lookups,0)) over () as Lkp_cnt, sum(coalesce(user_updates,0)) over () as Upd_cnt INTO #T_LastUsed FROM sys.dm_db_index_usage_stats AS stat INNER JOIN sys.objects AS o ON stat.[object_id] = o.[object_id] WHERE [database_id] = DB_ID() AND [type]=''U'';
-- select * from #T_LastUsed
;WITH cte_1 AS ( SELECT '''+@DB+''' AS DB, UsedType, LastUsed, Cnt FROM #T_LastUsed UNPIVOT ( LastUsed FOR UsedType IN ([Seek], [Scan], [Lkp], [Upd]) ) unpvt_1 UNPIVOT ( Cnt FOR UsedType_cnt IN ([Seek_cnt], [Scan_cnt], [Lkp_cnt], [Upd_cnt]) ) unpvt_2 WHERE UsedType+''_cnt''=UsedType_cnt ) SELECT d.name as DB, d.create_date as CreateDate, c.UsedType, case when c.LastUsed is null then ''19000101 00:00:00'' else c.LastUsed end as LastUsed, case when c.Cnt is null then 0 else c.Cnt end as cnt FROM sys.databases d LEFT JOIN cte_1 c ON d.name=c.DB WHERE d.name='''+@DB+'''; '); fetch next from DB into @DB; END close DB; deallocate DB; declare @tt datetime2(2)=getdate(); ;MERGE [awr].[db_usage_stats] as target USING ( SELECT DISTINCT @tt as tt, (select cast(create_date as datetime2(2)) from sys.databases where name='tempdb') as StartUp, DB, db_id(DB) as [dbid], CreateDate, max(LastUsed) over (partition by DB) as LastUsed, sum(Cnt) over (partition by DB) as cntcalls FROM #T ) as source (tt, StartUp, DB, [dbid], CreateDate, LastUsed, cntcalls) ON (target.StartUp=source.StartUp and target.[dbid]=source.[dbid] and target.CreateDate=source.CreateDate) WHEN MATCHED THEN UPDATE SET LastUsed=source.LastUsed, cntcalls=source.cntcalls, tt=source.tt WHEN NOT MATCHED BY TARGET THEN INSERT (tt, StartUp, DB, [dbid], CreateDate, LastUsed, cntcalls) VALUES (source.tt, source.StartUp, source.DB, source.[dbid], source.CreateDate, source.LastUsed, source.cntcalls) ; end

begin set nocount on; set LOCK_TIMEOUT 10000; declare @tt datetime, @instance_name nvarchar(128); declare @T1 table(id smallint, counter_type int, value numeric(19,2)); IF NOT EXISTS (SELECT * FROM sys.server_event_sessions WHERE name = 'xe_DiskInfo') BEGIN -- Extended Events! --xEvents Logical Disk ( 15 .) -- . -- awr sputnik. CREATE EVENT SESSION [xe_DiskInfo] ON SERVER ADD EVENT sqlserver.perfobject_logicaldisk ADD TARGET package0.ring_buffer(SET max_events_limit=(128),max_memory=(32768)) WITH (MAX_MEMORY=4096 KB,EVENT_RETENTION_MODE=ALLOW_SINGLE_EVENT_LOSS,MAX_DISPATCH_LATENCY=10 SECONDS,MAX_EVENT_SIZE=0 KB,MEMORY_PARTITION_MODE=NONE,TRACK_CAUSALITY=OFF,STARTUP_STATE=ON); ALTER EVENT SESSION [xe_DiskInfo] ON SERVER STATE = START; -- 30 , ! -- , . ! waitfor delay '00:00:30.000'; END declare @xml_data xml; SELECT @xml_data=CAST(xet.target_data AS xml) FROM sys.dm_xe_session_targets AS xet JOIN sys.dm_xe_sessions AS xe ON (xe.address = xet.event_session_address) WHERE xe.name = 'xe_DiskInfo' and xet.target_name = 'ring_buffer'; --select @xml_data; IF @xml_data is null -- NULL, ! ALTER EVENT SESSION [xe_DiskInfo] ON SERVER STATE = START;
set @tt=getdate(); select @instance_name=IIF(serverproperty('instancename') IS NULL, 'SQLServer', 'MSSQL$'+CAST(serverproperty('instancename') as varchar(128))); insert into @T1 (id, counter_type, value) select ph.id, ph.counter_type, CAST(pd.cntr_value as numeric(19,2)) as value from ( select id, [object_name], counter_name, instance_name, counter_type from awr.pfc_handle where counter_type is not null union select id, [object_name], counter_name+' base', instance_name, null as counter_type from awr.pfc_handle where counter_type=537003264 -- ! ) ph inner join sys.dm_os_performance_counters pd on REPLACE(ph.[object_name],'#instance#',@instance_name)=rtrim(pd.[object_name]) and ph.[counter_name]=rtrim(pd.[counter_name]) and (/*ph.counter_name like 'CPU usage %' or */ph.instance_name=pd.instance_name);
-- : --1. - sp_whoisactive; declare @T2 table(tt datetime null,id int null,cnt numeric(19,2)); declare @T_tmp table(tt datetime null, counter_name nvarchar(300) null, instance_name nvarchar(300), value numeric(19,2)); declare @id_spwhoisactive_count smallint=null, @id_sleeptran_count smallint=null; select @id_spwhoisactive_count=id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='Active sessions (sp_whoisactive)' and instance_name='_Total'; select @id_sleeptran_count=id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='Active sessions (sp_whoisactive)' and instance_name='Sleep_transactions'; if @id_spwhoisactive_count is not null begin tran insert into @T2(cnt) exec info.sp_who3 @busy_minutes=0,@get_count=1; update @T2 set id=@id_spwhoisactive_count,tt=@tt where id is null commit if @id_sleeptran_count is not null begin tran insert into @T2(cnt) exec info.sp_who3 @busy_minutes=0,@get_count=1, @only_sleep_tran=1;; update @T2 set id=@id_sleeptran_count,tt=@tt where id is null commit
--2. dmv: if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name IN ('Physical Memory (Gb)','Physical Memory (Mb)')) begin ;with cte_1 AS( SELECT @tt as tt, cast(total_physical_memory_kb/(1024.00*1024.00) as numeric(19,2)) as _Total, cast((total_physical_memory_kb-available_physical_memory_kb)/(1024.00*1024.00) as numeric(19,2)) as _Used, cast(available_physical_memory_kb/1024.00 as numeric(19,2)) as _Free FROM sys.dm_os_sys_memory ), cte_2 AS( SELECT tt, instance_name, value FROM cte_1 unpivot(value for instance_name in ([_Total],[_Used],[_Free]) )unpvt ), cte_3 AS ( select id,h.instance_name from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name IN ('Physical Memory (Gb)','Physical Memory (Mb)') ) insert into @T2(tt,id,cnt) select cte_2.tt, cte_3.id, cte_2.value from cte_2 inner join cte_3 on cte_2.instance_name=cte_3.instance_name ; end
--3. (sp_whoisactive) if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='Run_time_query_min') begin delete @T_tmp; insert into @T_tmp (tt,counter_name,instance_name, value) exec info.usp_SQLMon @Filter_Reads=0, @Filter_DurSec=0, @interval_sec=0, @get_runtime_metr=1;
;with cte_src as ( select tt, instance_name, value from @T_tmp ),cte_pfc as ( select distinct h.id,h.instance_name,max(d.tt) over (partition by h.id) as tt from awr.pfc_handle h left join awr.pfc_data d on h.id=d.pfc_id and d.tt>dateadd(minute,-10,@tt) -- - , ! where [object_name]='sputnik.awr' and counter_name='Run_time_query_min' ) insert into @T2(tt,id,cnt) select cte_src.tt, cte_pfc.id, cte_src.value from cte_src inner join cte_pfc on cte_src.instance_name=cte_pfc.instance_name and (cte_src.tt>cte_pfc.tt or cte_pfc.tt is null) ; end
--4. CPU SQL Server Extended Events if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='CPU usage %') begin DECLARE @ts_now bigint = (SELECT cpu_ticks/(cpu_ticks/ms_ticks)FROM sys.dm_os_sys_info); ;WITH cte_1 AS( SELECT [timestamp], convert(xml, record) AS [record] FROM sys.dm_os_ring_buffers WHERE ring_buffer_type = N'RING_BUFFER_SCHEDULER_MONITOR' AND record LIKE '%<SystemHealth>%' ), cte_2 AS( SELECT 100-record.value('(./Record/SchedulerMonitorEvent/SystemHealth/SystemIdle)[1]', 'int') AS [_Total], record.value('(./Record/SchedulerMonitorEvent/SystemHealth/ProcessUtilization)[1]','int') AS [SQL], CAST(DATEADD(ms, -1 * (@ts_now - [timestamp]), GETDATE()) as datetime2(0)) AS [tt] FROM cte_1 WHERE [timestamp]=(SELECT MAX([timestamp]) FROM cte_1) ), cte_3 AS( select tt, instance_name, value from cte_2 unpivot(value for instance_name in ([SQL],[_Total]) )unpvt ), cte_4 AS( select distinct h.id,h.instance_name,max(d.tt) as tt from awr.pfc_handle h left join awr.pfc_data d on h.id=d.pfc_id and d.tt>dateadd(minute,-10,@tt) -- - , ! where [object_name]='sputnik.awr' and counter_name='CPU usage %' group by h.id, h.instance_name ) insert into @T2(tt,id,cnt) select cte_3.tt, cte_4.id, cte_3.value from cte_3 inner join cte_4 on cte_3.instance_name=cte_4.instance_name and (cte_3.tt>cte_4.tt or cte_4.tt is null) ; end
--5. (, ) declare @T_dyn table(tt datetime null, id smallint null, cnt numeric(19,2)); if exists(select top 1 id from awr.pfc_handle where [object_name]='sputnik.awr' and instance_name='#dynamic#') begin delete @T_tmp; insert into @T_tmp (tt,counter_name,instance_name, value) exec info.usp_get_DiskMetr; -- ( pfc_handle_dyn), , ! ;with cte_dyn_h as ( select h.id, T.instance_name from @T_tmp T inner join awr.pfc_handle h on T.counter_name=h.counter_name and h.[object_name]='sputnik.awr' and h.instance_name='#dynamic#' ) MERGE INTO awr.pfc_handle_dyn as Target USING cte_dyn_h as Source ON (Source.id=Target.pfc_id and Source.instance_name=Target.instance_name) WHEN NOT MATCHED THEN INSERT (pfc_id,instance_name) VALUES (Source.id,Source.instance_name) ;
-- ( @T_dyn, pfc_data_dyn)! ;with cte_1 as ( select T.tt, dyn_h.id, T.value from @T_tmp T inner join awr.pfc_handle h on T.counter_name=h.counter_name and h.[object_name]='sputnik.awr' and h.instance_name='#dynamic#' inner join awr.pfc_handle_dyn dyn_h on h.id=dyn_h.pfc_id and T.instance_name=dyn_h.instance_name ), cte_2 as ( select distinct h.id, max(d.tt) as tt from cte_1 h left join awr.pfc_data_dyn d on h.id=d.pfc_dyn_id and d.tt>dateadd(minute,-10,@tt) -- - , ! group by h.id ) insert into @T_dyn(tt,id,cnt) select cte_1.tt, cte_1.id, cte_1.value from cte_1 inner join cte_2 on cte_2.id=cte_1.id and (cte_1.tt>cte_2.tt or cte_2.tt is null) ; end
--6. Uptime SQL Server if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='SQL Server Uptime (hours)') begin
;with cte_1 AS( select top 1 datediff(hour,create_date,getdate()) as value from sys.databases where name='tempdb' ), cte_2 AS ( select id from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name IN ('SQL Server Uptime (hours)') ) insert into @T2(tt,id,cnt) select @tt, (select id from cte_2) as id, (select value from cte_1) as value from cte_1 ; end
--7. ( .) if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='Response time (ms)') begin declare @tt_start datetime2, @tt_end datetime2, @t float, @i smallint=0; set @tt_start=SYSDATETIME(); if object_id('tempdb.dbo.#t') is not null drop table #t; create table #t(t float); while (@i<100) begin insert into #t(t) select square((rand()/PI()) * log(rand()) * cos(rand())) as t option(maxdop 0); insert into #t(t) select square((rand()/2) * log10(rand()) * sin(rand())) as t option(maxdop 0); insert into #t(t) select square((rand()/3) * log(rand()) * tan(rand())) as t option(maxdop 0); insert into #t(t) select square((rand()/4) * log10(rand()) * abs(rand())) as t option(maxdop 0); insert into #t(t) select square((rand()/5) * log(rand()) * cos(rand())) as t option(maxdop 0); set @i+=1; end select @t=avg(t) from #t option(maxdop 0, recompile); select @t=max(t) from #t option(maxdop 0, recompile); select @t=min(t) from #t option(maxdop 0, recompile); select @t=sum(t) from #t option(maxdop 0, recompile); set @tt_end=SYSDATETIME();
;with cte_1 AS( select datediff(MILLISECOND,@tt_start, @tt_end) as value --option(maxdop 0) ), cte_2 AS ( select id from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name IN ('Response time (ms)') ) insert into @T2(tt,id,cnt) select @tt, (select id from cte_2) as id, (select value from cte_1) as value from cte_1 ; end
--8. (sys.dm_tran_active_transactions) if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='Tran_RunTime_min') begin delete @T_tmp; ;with cte_src1 as( select transaction_id as tran_id, [name],transaction_type as tran_type,transaction_state as tran_state, datediff(minute,transaction_begin_time,@tt) as dur_sec, row_number() over (order by transaction_begin_time asc, transaction_id asc) as rnk from sys.dm_tran_active_transactions where transaction_id>1000 and transaction_type<>2 ) , cte_src2 as ( select distinct sum(dur_sec) over () as top5 from cte_src1 where rnk<=5 ) insert into @T_tmp (tt,counter_name,instance_name, value) select distinct @tt as tt, 'Tran_RunTime_min' as counter_name, '_All' as instance_name, sum(dur_sec) over () as [value] from cte_src1 UNION ALL select distinct @tt as tt, 'Tran_RunTime_min' as counter_name, '_Max' as instance_name, max(dur_sec) over () as [value] from cte_src1 UNION ALL select distinct @tt as tt, 'Tran_RunTime_min' as counter_name, '_Avg' as instance_name, avg(dur_sec) over () as [value] from cte_src1 UNION ALL select distinct @tt as tt, 'Tran_RunTime_min' as counter_name, '_Top5' as instance_name, top5 as [value] from cte_src2;
;with cte_src as ( select tt, instance_name, value from @T_tmp ),cte_pfc as ( select distinct h.id,h.instance_name from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name='Tran_RunTime_min' ) insert into @T2(tt,id,cnt) select cte_src.tt, cte_pfc.id, cte_src.value from cte_src inner join cte_pfc on cte_src.instance_name=cte_pfc.instance_name ; end
--9. sp_whoisactive: , . if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name='sp_whoisactive') begin delete @T_tmp; declare @cnt bigint; -- [tempdb].[dbo].[sp_whoisactive_collect] --drop table [tempdb].[dbo].[sp_whoisactive_collect]; if OBJECT_ID('[tempdb].[dbo].[sp_whoisactive_collect]') IS NULL BEGIN declare @schema nvarchar(MAX); exec sp_whoisactive @schema=@schema output, @return_schema=1,@output_column_list = '[start_time][dd hh:mm:ss.mss][session_id][sql_text][host_name][login_name][program_name][database_name][wait_info][CPU][tempdb%][block%][reads][writes][physical_reads][used_memory][status][open_tran_count][percent_complete][collection_time]' set @schema=replace(@schema,'<table_name>', '[tempdb].[dbo].[sp_whoisactive_collect]'); set @schema=replace(@schema,'[dd hh:mm:ss.mss] varchar(8000)', '[dd hh:mm:ss.mss] varchar(50)'); set @schema=replace(@schema,'varchar(4000)', 'varchar(500)'); --select @schema; exec(@schema); END; TRUNCATE TABLE [tempdb].[dbo].[sp_whoisactive_collect]; set @tt_start=SYSDATETIME(); EXEC sp_WhoIsActive @output_column_list = '[start_time][dd hh:mm:ss.mss][session_id][sql_text][host_name][login_name][program_name][database_name][wait_info][CPU][tempdb%][block%][reads][writes][physical_reads][used_memory][status][open_tran_count][percent_complete][collection_time]' ,@destination_table = '[tempdb].[dbo].[sp_whoisactive_collect]'; select @cnt=COUNT_BIG(*) from [tempdb].[dbo].[sp_whoisactive_collect]; set @tt_end=SYSDATETIME();
insert into @T_tmp (tt,counter_name,instance_name, value) select distinct @tt as tt, 'sp_whoisactive' as counter_name, 'cnt' as instance_name, @cnt as [value] UNION ALL select distinct @tt as tt, 'sp_whoisactive' as counter_name, 'elapsed_time_ms' as instance_name, datediff(MILLISECOND,@tt_start, @tt_end) as [value];
;with cte_src as ( select tt, instance_name, value from @T_tmp ),cte_pfc as ( select distinct h.id,h.instance_name from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name='sp_whoisactive' ) insert into @T2(tt,id,cnt) select cte_src.tt, cte_pfc.id, cte_src.value from cte_src inner join cte_pfc on cte_src.instance_name=cte_pfc.instance_name ; end
--10. (Jobs) sputnik if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name LIKE 'Job$_%' ESCAPE '$') begin delete @T_tmp; declare @TJ TABLE (Job nvarchar(2000), Step nvarchar(2000),RUN_STATUS varchar(50), Duration_min numeric(19,2)); insert into @TJ (Job,Step,RUN_STATUS,Duration_min) exec sputnik.info.usp_JobMonitor @Lite=1, @OnlyEnabled=0;
insert into @T_tmp (tt,counter_name,instance_name, [value]) select distinct @tt as tt, 'Job_Backup_min' as counter_name, 'Full_or_Diff' as instance_name, CASE WHEN RUN_STATUS='Running' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '%BackupFull %' UNION ALL select distinct @tt as tt, 'Job_Backup_min' as counter_name, 'Log' as instance_name, CASE WHEN RUN_STATUS='Running' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '%BackupLog %' UNION ALL select distinct @tt as tt, 'Job_Index_Stats_min' as counter_name, 'GetInfo' as instance_name, CASE WHEN RUN_STATUS='Running' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '% . %' UNION ALL select distinct @tt as tt, 'Job_Index_Stats_min' as counter_name, 'ReIndex' as instance_name, CASE WHEN RUN_STATUS='Running' AND Step='1.rebuild indexes' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '% . %' UNION ALL select distinct @tt as tt, 'Job_Index_Stats_min' as counter_name, 'UpdStats' as instance_name, CASE WHEN RUN_STATUS='Running' AND Step='2.recompute stats' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '% . %' UNION ALL select distinct @tt as tt, 'Job_Index_Stats_min' as counter_name, 'UpdStatsOpt' as instance_name, CASE WHEN RUN_STATUS='Running' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '% . Optimizing updatestats%' UNION ALL select distinct @tt as tt, 'Job_RM_min' as counter_name, '' as instance_name, CASE WHEN RUN_STATUS='Running' THEN sum([Duration_min]) over() ELSE 0 END as [value] from @TJ where Job LIKE 'RM%' UNION ALL select distinct @tt as tt, 'Job_ShrinkTempDB_min' as counter_name, '' as instance_name, CASE WHEN RUN_STATUS='Running' THEN [Duration_min] ELSE 0 END as [value] from @TJ where Job LIKE '% TempDb%' UNION ALL select distinct @tt as tt, 'Job_Cntr' as counter_name, 'All' as instance_name, COUNT_BIG(DISTINCT Job) as [value] from @TJ UNION ALL select distinct @tt as tt, 'Job_Cntr' as counter_name, 'Active' as instance_name, COUNT_BIG(DISTINCT Job) as [value] from @TJ where RUN_STATUS='Running' and Job not like '% awr. pfc%' UNION ALL select distinct @tt as tt, 'Job_Cntr' as counter_name, 'Failed_or_Cancel' as instance_name, COUNT_BIG(DISTINCT Job) as [value] from @TJ where RUN_STATUS IN ('Failed','Canceled by user') ;
;with cte_pfc as ( select distinct h.id, h.instance_name, h.counter_name from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name LIKE 'Job$_%' ESCAPE '$' ) insert into @T2(tt,id,cnt) select src.tt, cte_pfc.id, src.[value] from @T_tmp as src inner join cte_pfc on src.counter_name=cte_pfc.counter_name AND src.instance_name=cte_pfc.instance_name ; end
--11. TempDB if exists(select id from awr.pfc_handle where [object_name]='sputnik.awr' and counter_name = 'tempdb_using_Mb') begin delete @T_tmp; ;with cte_src1 as( select distinct CASE pr WHEN 'rowver_mb' THEN 'Row-versions' WHEN 'user_mb' THEN 'User-objects' WHEN 'internal_mb' THEN 'Internal' WHEN 'mixed_mb' THEN 'Mixed-extents' WHEN 'total_mb' THEN '_Total' ELSE NULL END as instance_name, vl as [value] from sputnik.info.vtempusing ) insert into @T_tmp (tt,counter_name,instance_name, value) select @tt as tt, 'tempdb_using_Mb' as counter_name, instance_name, [value] from cte_src1;
;with cte_src as ( select tt, instance_name, [value] from @T_tmp ),cte_pfc as ( select distinct h.id,h.instance_name from awr.pfc_handle h where [object_name]='sputnik.awr' and counter_name='tempdb_using_Mb' ) insert into @T2(tt,id,cnt) select cte_src.tt, cte_pfc.id, cte_src.value from cte_src inner join cte_pfc on cte_src.instance_name=cte_pfc.instance_name ; end
-- sputnik dm_os_performance_counters insert into awr.pfc_data(tt,pfc_id,value) select distinct @tt as tt, T.id as pfc_id, case when T_base.value_base=0.00 then 0.00 when T.counter_type=537003264 and T_base.value_base is not null then cast(t.value / T_base.value_base * 100.00 as numeric(19,2)) else value end as value from ( select distinct id, counter_type, case when counter_type=537003264 then SUM(value) over (partition by id) else value end as value from @T1 where counter_type is not null ) as T left join ( select id, MAX(value) over (partition by id,counter_type) as value_base from @T1 where counter_type is null ) T_base on T.id=T_base.id UNION select tt, id as pfc_id, cnt as value from @T2 ;
-- sputnik insert into awr.pfc_data_dyn(tt,pfc_dyn_id,value) select distinct tt, id as pfc_dyn_id, cnt as value from @T_dyn;
end

/* ============================================= -- Author: (sqland1c) -- Create date: 24.08.2015 (1.0) -- Description: sputnik SQL SERVER.
-- Update: 01.09.2015 (1.1) ( @instance_name). [object_name] , SQLServer. 01.09.2015 (1.15) CPU Usage % Extended Event ( : CPU CPU SQL Server). 14.09.2015 (1.2) , sp_whoisactive. 23.09.2015 (1.3) , Logical Disk ( ). 28.09.2015 (1.31) xEvents [xe_DiskInfo] ( ) usp_get_DiskMetr. 19.11.2015 (1.32) Uptime SQL Server . 10.03.2016 (1.33) - awr.pfc_data tt ( ). : : ~25 , : ~2 . - ! 27.05.2016 (1.34) Response time(ms) 12.12.2016 (1.350) Tran_RunTime_min ( .) 24.04.2017 (1.371) ( SQL). - awr.pfc_data - , ? 19.03.2018 (1.380) ( TempDB). 23.03.2018 (1.382) Sleeping_tran- - . 02.04.2018 (1.383) TempDB. -- ============================================= */ CREATE PROCEDURE awr.usp_pfc_collect
AS

BEGIN
	set nocount on;
	set QUOTED_IDENTIFIER ON;
	declare @tt datetime2(2)=SYSDATETIME(), @spid smallint,@cmd nvarchar(100);
	declare @kill_enabled bit, @Kill_tempusing_gb numeric(9,0), @Kill_opentran0_tempusing_gb numeric(9,0), @kill_rq_elapsed_sec numeric(9,0), @kill_userinternalusing_gb numeric(9,0),@kill_rowver_tranactive_sec numeric(9,0);
	select @kill_enabled=cast(vl as bit)
	from config.params where obj='awr.tempusing' and pr='kill: enabled';
	select @Kill_tempusing_gb=cast(vl as numeric(9,0))
	from config.params where obj='awr.tempusing' and pr='kill: total tempdb using(Gb)>';
	select @Kill_opentran0_tempusing_gb=cast(vl as numeric(9,0))
	from config.params where obj='awr.tempusing' and pr='kill: opentran=0/total tempdb using(Gb)>';
	select @kill_rq_elapsed_sec=cast(vl as numeric(9,0))
	from config.params where obj='awr.tempusing' and pr='kill: query elapsed(sec)>';
	select @kill_userinternalusing_gb=cast(vl as numeric(9,0))
	from config.params where obj='awr.tempusing' and pr='kill: user+internal using(gb)>';
	select @kill_rowver_tranactive_sec=cast(vl as numeric(9,0))
	from config.params where obj='awr.tempusing' and pr='kill: row-verions tran active (sec)>';
	if object_id('tempdb..#tmp01') is not null
		drop table #tmp01;
	create table #tmp01(
			tt datetime2(2),
			[spid] smallint not null,
			[login_time] datetime not null,
			rq_start_time datetime null,
			user_mb numeric(9,0) null,
			internal_mb numeric(9,0) null,
			sleep_status bit not null,
			rq_elapsed_sec numeric(9,0) null,
			rowver_tran_sec numeric(9,0) null,
			open_tran_flag bit not null,
			kill_flag bit not null
	)
	;with cte01 as (
	select 
		@tt as tt, 
		ss.[spid],ss.[login_time],ss.[rq_start_time],
		ss.[user_mb],ss.[internal_mb], 
		case when ss.[status]='sleeping' then 1 else 0 end as sleep_status,
		ss.[elapsed_sec] as rq_elapsed_sec,
		ver.[tran_active_sec] as [rowver_tran_sec],
		case when ss.[open_tran_cnt]>0 then 1 else 0 end as [open_tran_flag],
		0 as kill_flag
	from info.vtempusing_ss as ss
	left join info.vtempusing_ver as ver
		on ss.spid=ver.spid and ss.[login_time]=ver.[login_time] and ver.[tran_active_sec]>300
	where ss.elapsed_sec>150)
	,cte02 as(
	select 
		@tt as tt, 
		ver.[spid],ver.[login_time],null as [rq_start_time],
		null as [user_mb], null as [internal_mb],
		case when [status]='sleeping' then 1 else 0 end as sleep_status,
		null as rq_elapsed_sec,
		ver.[tran_active_sec] as [rowver_tran_sec],
		case when [open_tran_cnt]>0 then 1 else 0 end as [open_tran_flag],
		0 as kill_flag
	from info.vtempusing_ver as ver
	left join cte01 
		on ver.spid=cte01.spid and ver.[login_time]=cte01.[login_time]
	where [tran_active_sec]>300 and cte01.spid is null
	)
	insert into #tmp01(tt,[spid], [login_time], [rq_start_time],[user_mb],[internal_mb],sleep_status, rq_elapsed_sec, [rowver_tran_sec], open_tran_flag,kill_flag)
	select tt,[spid], [login_time], [rq_start_time],[user_mb],[internal_mb],sleep_status, rq_elapsed_sec, [rowver_tran_sec], open_tran_flag,kill_flag from cte01
	union all
	select tt,[spid], [login_time], [rq_start_time],[user_mb],[internal_mb],sleep_status, rq_elapsed_sec, [rowver_tran_sec], open_tran_flag,kill_flag from cte02;
	declare @tempusing_total_gb numeric(9,0);
	select top 1 @tempusing_total_gb=cast((vl/1024) as numeric(9,0)) from info.vtempusing where pr='total_mb';
	if @kill_enabled=1
	begin
		declare kl cursor for
			select spid 
			from #tmp01
			where 
				(
					(open_tran_flag=0 and @tempusing_total_gb>@Kill_opentran0_tempusing_gb) or @tempusing_total_gb>@Kill_tempusing_gb
				)
				and (
					(rq_elapsed_sec>@kill_rq_elapsed_sec and [user_mb]+[internal_mb]>@kill_userinternalusing_gb*1024)
					or ([rowver_tran_sec]>@kill_rowver_tranactive_sec)
				);
		open kl;
		fetch next from kl into @spid;
		while @@fetch_status=0
		begin
			set @cmd='kill '+cast(@spid as varchar(7));
			exec(@cmd);
			update #tmp01
			set kill_flag=1
			where spid=@spid and kill_flag=0;
			fetch next from kl into @spid;
		end
		close kl;
		deallocate kl;
	end
	insert into awr.tempusing (tt,[spid], [login_time], [rq_start_time],[user_mb],[internal_mb],sleep_status, rq_elapsed_sec, [rowver_tran_sec], open_tran_flag,kill_flag)
	select tt,[spid], [login_time], [rq_start_time],[user_mb],[internal_mb],sleep_status, rq_elapsed_sec, [rowver_tran_sec], open_tran_flag,kill_flag
	from #tmp01;
	if object_id('tempdb..#tmp01') is not null
		drop table #tmp01;	
END

BEGIN
	SET NOCOUNT ON;
	declare @PS_command nvarchar(4000), @CMD nvarchar(4000),@rc bit, @LocalDir nvarchar(1600), @NetDir nvarchar(1600), @LocalDays int, @NetDays int;
	declare @Kind varchar(4),@DBName nvarchar(400), @InstanceName nvarchar(128),@PSFile nvarchar(300),@ErrMsg nvarchar(900), @LocalPolicy tinyint, @NetPolicy tinyint, @PS_Filter_Policy nvarchar(4000);
	declare C cursor for
	select bc.LocalDir, bc.NetDir, bc.LocalDays, bc.NetDays, bc.DBName, bc.Kind, bc.[LocalPolicy], bc.[NetPolicy]
	from sputnik.backups.BackConf bc
	inner join sys.databases sdb on bc.DBName=sdb.[name] and sdb.state_desc='ONLINE'
	where
		(@DBFilter is null or bc.DBName=@DBFilter)
		and (@type is null or bc.Kind=@type)
		and (bc.Kind in ('Full','Diff','Log')) --   .     , .   .
	UNION
	select bc.LocalDir, bc.NetDir, bc.LocalDays, bc.NetDays, bc.DBName, bc.Kind, bc.[LocalPolicy], bc.[NetPolicy]
	from sputnik.backups.BackConfWeekly bc
	inner join sys.databases sdb on bc.DBName=sdb.[name] and sdb.state_desc='ONLINE'
	where
		(@DBFilter is null or bc.DBName=@DBFilter)
		and (@type is null or bc.Kind=@type)
		and (bc.Kind in ('Full','Diff','Log')) --   .     , .   .

	--   .
	--     .    ,     ALL
	--    SQL Server     .
	SELECT @InstanceName=COALESCE('_'+CAST(SERVERPROPERTY('InstanceName') as nvarchar(128)),'');
	set @PSFile='usp_CleaningBack'+@InstanceName;
	if @type is not null
		set @PSFile=@PSFile+'_'+@type;
	else
		set @PSFile=@PSFile+'_ALL';
	set @PSFile=@PSFile+'_'+REPLACE(REPLACE(SUBSTRING(CONVERT(NVARCHAR(23),GETDATE(),126),1,23),'T','_'),':','.');
	set @PSFile=@PSFile+'.ps1';

	--1.    PowerShell
	set @CMD = N'cmd /k chcp 1251 && echo | echo #PowerShell-Script for cleaning old backups. Created by Job on local SQL Server. Date: '+convert(varchar(20),getdate(),120)+ '> %temp%\'+@PSFile;
	EXEC @rc=xp_cmdshell @CMD , no_output
	if @rc=1
	begin
		SET @ErrMsg=N'    (PowerShell ) %temp%\'+@PSFile;
		RAISERROR(@ErrMsg,11,1) WITH LOG
	end
	open C
	fetch next from C
	into @LocalDir, @NetDir, @LocalDays, @NetDays, @DBName, @Kind, @LocalPolicy, @NetPolicy
	while @@FETCH_STATUS=0
	begin
		--  [  ],  PowerShell    (   ):
		IF CHARINDEX('[', @LocalDir)>0
			SET @LocalDir=REPLACE(@LocalDir,'[','``[');
		IF CHARINDEX(']', @LocalDir)>0
			SET @LocalDir=REPLACE(@LocalDir,']','``]');
		IF CHARINDEX(']', @NetDir)>0
			SET @NetDir=REPLACE(@NetDir,']','``]');
		IF CHARINDEX(']', @NetDir)>0
			SET @NetDir=REPLACE(@NetDir,']','``]');

		--   -    Powershell!
		if @LocalPolicy=1 --    ! 
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip '+cast(@LocalDays as varchar(5));
		else if @LocalPolicy=0 --   (  )! +   3 ,    !
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip 3 ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-'+cast(@LocalDays as varchar(5))+N'))}';
		else if @LocalPolicy=2 --   (  x-       x- )!
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip '+cast(@LocalDays as varchar(5))+' ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-'+cast(@LocalDays as varchar(5))+N'))}';
		set @PS_command=N'get-childitem -path '''+@LocalDir+N''' ^^^| where {$_.extension -eq ''.'+@rez+N''' -and $_.Name -like '''+@DBName+'_'+@Kind+'_*''} '+@PS_Filter_Policy+N' ^^^| remove-item';
		set @CMD = N'cmd /k chcp 1251 && echo | echo '+@PS_command+N' >> %temp%\'+@PSFile;
		-- :
		--PRINT @CMD;
		EXEC @rc=xp_cmdshell @CMD , no_output
		if @rc=1
		begin
			SET @ErrMsg=N'    (PowerShell ) %temp%\'+@PSFile;
			RAISERROR(@ErrMsg,11,1) WITH LOG
		end
		--   -    Powershell!
		if @NetPolicy=1 --    ! 
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip '+cast(@NetDays as varchar(5));
		else if @NetPolicy=0 --   (  )! +   3 ,    !
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip 3 ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-'+cast(@NetDays as varchar(5))+N'))}';
		else if @NetPolicy=2 --   (  x-       x- )!
			set @PS_Filter_Policy= N' ^^^| Sort-Object -property lastwritetime -descending ^^^| Select-Object -skip '+cast(@NetDays as varchar(5))+' ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-'+cast(@NetDays as varchar(5))+N'))}';
		set @PS_command=N'get-childitem -path '''+@NetDir+N''' ^^^| where {$_.extension -eq ''.'+@rez+N''' -and $_.Name -like '''+@DBName+'_'+@Kind+'_*''} '+@PS_Filter_Policy+N' ^^^| remove-item';
		set @CMD = N'cmd /k chcp 1251 && echo | echo '+@PS_command+N' >> %temp%\'+@PSFile;
		-- :
		--PRINT @CMD;
		EXEC @rc=xp_cmdshell @CMD , no_output

		if @rc=1
		begin
			SET @ErrMsg=N'    (PowerShell ) %temp%\'+@PSFile;
			RAISERROR(@ErrMsg,11,1) WITH LOG
		end
		fetch next from C
		into @LocalDir, @NetDir, @LocalDays, @NetDays, @DBName, @Kind, @LocalPolicy, @NetPolicy
	end
	close C;
	deallocate C;

	--2.1   PowerShell      usp_CopyBack ( ).
	set @PS_command=N'Get-ChildItem -Path $env:TEMP\usp_CopyBack*.ps1 ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-3))} ^^^| remove-item'
	set @CMD = N'cmd /k chcp 1251 && echo | echo '+@PS_command+N' >> %temp%\'+@PSFile;
	EXEC @rc=xp_cmdshell @CMD , no_output
	if @rc=1
	begin
		SET @ErrMsg=N'    (PowerShell ) %temp%\'+@PSFile;
		RAISERROR(@ErrMsg,11,1) WITH LOG
	end

	--2.2   PowerShell      usp_CleaningBack (  ).
	set @PS_command=N'Get-ChildItem -Path $env:TEMP\usp_CleaningBack*.ps1 ^^^| where {$_.lastwritetime -lt ((get-date).AddDays(-3))} ^^^| remove-item'
	set @CMD = N'cmd /k chcp 1251 && echo | echo '+@PS_command+N' >> %temp%\'+@PSFile;
	EXEC @rc=xp_cmdshell @CMD , no_output
	if @rc=1
	begin
		SET @ErrMsg=N'    (PowerShell ) %temp%\'+@PSFile;
		RAISERROR(@ErrMsg,11,1) WITH LOG
	end

	--3.   PowerShell  
	set @CMD = 'powershell %temp%\'+@PSFile;
	EXEC @rc=xp_cmdshell @CMD , no_output
	if @rc=1
	begin
		SET @ErrMsg=N'   PowerShell-   %temp%\'+@PSFile;
		RAISERROR(@ErrMsg,11,1) WITH LOG
	end
	else
		PRINT N'     !';
end

BEGIN
	SET NOCOUNT ON;
	declare @Find_command nvarchar(3600), @Filter_FileName nvarchar(500), @PS_command nvarchar(2400), @CMD nvarchar(2800),@rc bit, @LocalDir nvarchar(1600), @NetDir nvarchar(1600), @rez_filter nvarchar(75), @date_filter nvarchar(200)='';
	declare @PSFile nvarchar(300), @Kind varchar(4), @DBName nvarchar(400), @ErrMsg nvarchar(900), @InstanceName nvarchar(128), @LogFile nvarchar(300);
	declare @Log_command nvarchar(3600), @S char(2);
	set @S=CHAR(36)+CHAR(40);	-- symbols $ and (
	--     !
	if exists(
			select bc.DBName
			from info.vGetAllBackConf bc
			inner join sys.databases sdb on bc.DBName=sdb.[name] and sdb.state_desc='ONLINE'
			where
				(@DBFilter is null or bc.DBName=@DBFilter)
				and (@type is null or (@type='Full' and bc.Kind IN ('Full','Diff')) or bc.Kind=@type)	--  Full,     Full  Diff.
				and (bc.NetDir is not null and bc.NetDir<>'' and bc.LocalDir<>bc.NetDir) --    !
		)
	BEGIN
		--      sputnik    !
		declare C cursor for
		select distinct bc.LocalDir, bc.NetDir, bc.DBName, bc.Kind
		from sputnik.info.vGetAllBackConf bc
		inner join sys.databases sdb on bc.DBName=sdb.[name] and sdb.state_desc='ONLINE'
		where
			(@DBFilter is null or bc.DBName=@DBFilter)
			and (@type is null or (@type='Full' and bc.Kind IN ('Full','Diff')) or bc.Kind=@type)	--  Full,     Full  Diff.
			and (bc.NetDir is not null and bc.NetDir<>'' and bc.LocalDir<>bc.NetDir) --    !
		--                 
		if @FilterBackupID is not null
		begin
			declare @BackupFile nvarchar(500);
			DECLARE @ChainBack TABLE (BackupFile NVARCHAR(800), BackupType VARCHAR(4), ID INT, BackupDate DATETIME2(2));
			INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
			EXEC sputnik.info.usp_GetChainLogs @DBName=@DBFilter,@FilterBackupID=@FilterBackupID, @top=1, @FromCopy=0, @GetBackupFile=1;

			select top 1 @BackupFile=BackupFile from @ChainBack;
			if @BackupFile IS NOT NULL AND @BackupFile<>'' 
			begin
				set @Filter_FileName=' -and ($_.Name) -like "'+@BackupFile+'*"';
			end
			else
				set @Filter_FileName='';
		end
		else
			set @Filter_FileName='';
		--   
		if @Force=0
			set @rez_filter='$_.extension -eq ".ONLY"';
		else
			set @rez_filter='($_.extension -eq ".ONLY" -or $_.extension -eq ".BAK")';
		--   
		if @DateStart is not null or @DateEnd is not null
		begin
			if @DateStart is not null
				set @date_filter='-and $_.lastwritetime -ge "'+convert(nvarchar(50),@DateStart,120)+'" ';
			if @DateEnd is not null
				set @date_filter=@date_filter+'-and $_.lastwritetime -le "'+convert(nvarchar(50),@DateEnd,120)+'" ';
		end
		else if @XLastHours is not null
		begin
			set @date_filter='-and $_.lastwritetime -ge "'+convert(nvarchar(50),DATEADD(HOUR,-@XLastHours,GETDATE()),120)+'" '+
							 '-and $_.lastwritetime -le "'+convert(nvarchar(50),GETDATE(),120)+'" ';
		end 
		--   ,     .
		--      .
		--  ,      .
		--+      SQL Server     .
		SELECT @InstanceName=COALESCE('_'+CAST(SERVERPROPERTY('InstanceName') as nvarchar(128)),'');
		set @PSFile='usp_CopyBack'+@InstanceName;
		if @type is not null
			set @PSFile=@PSFile+'_'+@type;
		else
			set @PSFile=@PSFile+'_ALL';
		set @PSFile=@PSFile+'_'+REPLACE(REPLACE(SUBSTRING(CONVERT(NVARCHAR(23),GETDATE(),126),1,23),'T','_'),':','.');
		set @LogFile=@PSFile+'.log';
		set @PSFile='%temp%\'+@PSFile+'.ps1';
		--1.    PowerShell
		set @CMD = N'#PowerShell-Script for COPY backups. Created by Job on local SQL Server. Date: '+convert(varchar(20),getdate(),120);
		exec dbo.[usp_WriteToFile] @CMD, @PSFile, 1;
		set @CMD = N'$ErrorActionPreference = ^^^"stop^^^"';
		exec dbo.[usp_WriteToFile] @CMD, @PSFile;
		set @CMD = N'#Logs for COPY backups. Created by Job on local SQL Server. Date: '+convert(varchar(20),getdate(),120);
		exec dbo.[usp_WriteToFile] @CMD, @LogFile, 1;
		open C
		fetch next from C
		into @LocalDir, @NetDir, @DBName, @Kind
		while @@FETCH_STATUS=0
		begin
			--  [  ],  PowerShell    (   ):
			IF CHARINDEX('[', @LocalDir)>0
				SET @LocalDir=REPLACE(@LocalDir,'[','``[');
			IF CHARINDEX(']', @LocalDir)>0
				SET @LocalDir=REPLACE(@LocalDir,']','``]');
			IF CHARINDEX(']', @NetDir)>0
				SET @NetDir=REPLACE(@NetDir,']','``]');
			IF CHARINDEX(']', @NetDir)>0
				SET @NetDir=REPLACE(@NetDir,']','``]');
			-- TRY:
			set @PS_command=N'Try {';
			exec dbo.[usp_WriteToFile] @PS_command, @PSFile;
			--   
			set @Find_command=N'get-childitem -path "'+@LocalDir+N'" ^^^| where {'+@rez_filter+' -and $_.Name -like "*'+@DBName+'*'+@Kind+N'*" '+@date_filter+@Filter_FileName+'} ^^^|';
			--   :
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") INFO   (: '+@DBName+N',  : '+@Kind+') " ^^^>^^^> %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=N'echo "INFO  : '+@S+'('+REPLACE(@Find_command,'^^^','')+N' measure).count) ; _: '+@S+'[math]::round((('+REPLACE(@Find_command,'^^^','')+N' measure length -sum).sum / 1gb),2)) ;   :" ^^^>^^^>  %temp%\'+@LogFile; 
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=@Find_command+N' select Name -expand Name ^^^>^^^>  %temp%\'+@LogFile; 
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			--
			set @PS_command=@Find_command+' Copy-Item -Destination "'+@NetDir+'" -Force';
			exec dbo.[usp_WriteToFile] @PS_command, @PSFile;
			--     !
			--  -         !
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") INFO   " ^^^>^^^> %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") INFO   (LocalDir, : '+@DBName+N',  : '+@Kind+') .ONLY  .BAK " ^^^>^^^>  %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=@Find_command+N' select Name -expand Name ^^^>^^^>  %temp%\'+@LogFile; 
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			declare @Check_command nvarchar(500);
			set @Check_command=' WHERE {(Test-Path ("'+@NetDir+'"+$_.Name)) -eq $true} ^^^|';
			set @PS_command=@Find_command+@Check_command+' Rename-Item -newname { $_.name -replace "\.ONLY",".BAK" }';
			exec dbo.[usp_WriteToFile] @PS_command, @PSFile;
			--      !
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") INFO   (NetDir, : '+@DBName+N',  : '+@Kind+') .ONLY  .BAK " ^^^>^^^>  %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Find_command=REPLACE(@Find_command, '-path "'+@LocalDir+N'"' , '-path "'+@NetDir+N'"'); 
			set @Log_command=@Find_command+N' select Name -expand Name ^^^>^^^>  %temp%\'+@LogFile; 
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @PS_command=@Find_command+' Rename-Item -newname { $_.name -replace "\.ONLY",".BAK" }';
			exec dbo.[usp_WriteToFile] @PS_command, @PSFile;
			--  +    Try
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") INFO   " ^^^>^^^> %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=N'}'; 
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @PS_command=N'Catch {';
			exec dbo.[usp_WriteToFile] @PS_command, @PSFile;
			-- + BREAK +    Catch
			set @Log_command=N'echo "'+@S+'Get-Date -Format "dd-MM-yyyy HH:mm:ss.ms") ERROR '+@S+'$error[0].exception) " ^^^>^^^> %temp%\'+@LogFile;
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=N'BREAK';
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			set @Log_command=N'}';
			exec dbo.[usp_WriteToFile] @Log_command, @PSFile;
			fetch next from C
			into @LocalDir, @NetDir, @DBName, @Kind
		end
		close C;
		deallocate C;
		--2.   PowerShell  
		set @CMD = 'powershell '+@PSFile;
		EXEC @rc=xp_cmdshell @CMD , no_output
		if @rc=1
		BEGIN
			set @ErrMsg=N'   PowerShell-   %temp%\'+@PSFile;
			RAISERROR(@ErrMsg,11,1) WITH LOG
		END;
		else
			PRINT N'     !';
	END;
end

/* ============================================= -- Author: (sqland1c) -- Create date: 09.11.2014 (1.0) -- Description: () ( !). sputnik! , , .ONLY .BAK PowerShell , , .
-- Update: 16.11.2014 (1.01) @type Full, Full Diff ! 21.11.2014 (1.05) - PowerShell , ! [usp_CleaningBack]. - @ErrMsg. 31.12.2014 (1.06) ! 23.06.2015 (1.07) . 21.10.2015 (1.10) - ! @FilterBackupID - ( ). 01.07.2016 (1.11) @XLastHours - ( X ). , @DateStart @DateEnd. - ( ). 22.12.2017 (1.120) . . 19.04.2018 (1.121) - online ! 21.02.2019 (1.400) : , [usp_WriteToFile]. 12.11.2021 (1.402) combination of characters $ and ( used in sqlcmd for variables and breaks project publishing
-- ============================================= */ create PROCEDURE backups.[usp_CopyBack] @type varchar(4) = null -- Log, Diff Full. ,@DBFilter nvarchar(100) = null -- . ,@Force bit = 0 -- ( = .BAK). ,@DateStart datetime = null ,@DateEnd datetime = null -- . ( !) ,@FilterBackupID int = null -- . ID ,@XLastHours smallint = null -- . ( X ). , @DateStart @DateEnd AS

BEGIN
	SET NOCOUNT ON;
	IF @ToDate IS NULL
		SET @ToDate=SYSDATETIME();
	DECLARE @Dir NVARCHAR(500), @File NVARCHAR(300), @FullPath NVARCHAR(800), @BackupID INT, @BackupFinishDate DATETIME2(2), @BackupStartDate DATETIME2(2), @NetDir NVARCHAR(500);
	DECLARE @WeekDay tinyint, @MonthDay tinyint;

	--      (    sputnik   ).
	SELECT
		  @File=[Backup_File]+'.BAK', @BackupID=ID, @BackupFinishDate=backup_finish_date, @BackupStartDate=backup_start_date   
	FROM [sputnik].[backups].[BackupHistory]
	WHERE
		ID = (
				SELECT ID
				FROM [sputnik].[backups].[BackupHistory]
				WHERE
					[DB_Name]=@DBNameSource
					AND [Backup_Type]='Full' 
					AND [backup_finish_date] = (SELECT 
													MAX(backup_finish_date)
												FROM [sputnik].[backups].[BackupHistory]
												WHERE
													[DB_Name]=@DBNameSource
													AND [Backup_Type]='Full'
													AND [backup_finish_date] <= @ToDate
												)
			);

	--         
	SELECT @Dir=LocalDir, @NetDir=NetDir
	FROM info.uf_GetBackConf (@DBNameSource,'Full', @BackupFinishDate);

	SET @FullPath=@Dir+@File;

	--      (    sputnik  )
	SELECT BACKUP_File, Backup_Finish_Date
	INTO #T_Logs
	FROM [sputnik].[backups].[BackupHistory]
	WHERE
		DB_NAME=@DBNameSource 
		AND database_backup_lsn=(
			SELECT MIN(database_backup_lsn)
			FROM [sputnik].[backups].[BackupHistory]
			WHERE DB_NAME=@DBNameSource
			AND database_backup_lsn>
				(
					SELECT [database_backup_LSN]
					FROM [sputnik].[backups].[BackupHistory]
					WHERE ID=@BackupID
				)
			)
		AND [Backup_Type]='Log'
		AND [Backup_Finish_Date]<=@ToDate
	ORDER BY last_LSN;

	--.     ,     .
	IF NOT EXISTS(SELECT [Backup_File] FROM #T_Logs)
	BEGIN
		--    (  )
		IF @DBNameTarget IS NULL
			SET @DBNameTarget=@DBNameSource+'_COPY_'+CONVERT(VARCHAR(8), @BackupFinishDate, 112)+'_'+REPLACE(CONVERT(VARCHAR(20), @BackupFinishDate, 108), ':', '');

		EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
			@DBNameTarget=@DBNameTarget, 
			@FromDisk=@FullPath;
	END
	ELSE
	BEGIN
		--       .
		IF @DBNameTarget IS NULL
		BEGIN
			--    (  )
			SELECT @BackupFinishDate=MAX([Backup_Finish_Date])
			FROM #T_Logs;
			SET @DBNameTarget=@DBNameSource+'_COPY_'+CONVERT(VARCHAR(8), @BackupFinishDate, 112)+'_'+REPLACE(CONVERT(VARCHAR(20), @BackupFinishDate, 108), ':', '');
		END
		PRINT ('********************************
				  : '+@FullPath);
		EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
			@DBNameTarget=@DBNameTarget, 
			@FromDisk=@FullPath,
			@NoRecovery=1;

		DECLARE RE CURSOR FOR
			SELECT [Backup_File]+'.BAK' AS [BackupFile],
					[Backup_Finish_Date]
			FROM #T_Logs;
		OPEN RE;
		FETCH NEXT FROM RE 
		INTO @File, @BackupFinishDate;
		WHILE @@FETCH_STATUS=0
		BEGIN
			--         
			SELECT @Dir=LocalDir, @NetDir=NetDir
			FROM info.uf_GetBackConf (@DBNameSource,'Log', @BackupFinishDate);

			SET @FullPath=@Dir+@File;

			PRINT ('********************************
					  : '+@FullPath);

			EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
				@DBNameTarget=@DBNameTarget, 
				@FromLog=@FullPath,
				@NoRecovery=1
			FETCH NEXT FROM RE 
			INTO @File, @BackupFinishDate;
		END
		CLOSE RE;
		DEALLOCATE RE;
		--     RECOVERY
		PRINT ('********************************
				     ONLINE');
		EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
			@DBNameTarget=@DBNameTarget, 
			@ForceRecovery=1
	END;
END

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 23.04.2014 (1.0)
-- Description: usp_GC - GetCopy.             .
				  -    usp_RestoreDB_simple (       ).
				    sputnik,       .
				 ,           sputnik.
				:
					@DBNameSource -  ,     (   );
					@DBNameTarget -  ,     (  ).   ,   
					    : @DBNameTarget +      .
					@ServerTarget ???
					@ToDate -  .    ,      .   ,  
					 .
-- Update:
				25.04.2014 (1.1)
				            info.uf_GetBackConf.

-- ============================================= */
CREATE PROCEDURE [backups].[usp_GC]  
	@DBNameSource nvarchar(300),
	@DBNameTarget nvarchar(300)=NULL,
	@ToDate datetime2(2)=NULL
AS

BEGIN
	SET NOCOUNT ON;
	DECLARE @LocalServer NVARCHAR(500)=CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(500));
	DECLARE @lseInitDate datetime2(2)=sysdatetime();
	DECLARE @Dir NVARCHAR(500), @File NVARCHAR(300), @FullPath NVARCHAR(800),@DiffPath NVARCHAR(800), @BackupFullID INT, @BackupDiffID int, @BackupFullFinishDate DATETIME2(2), @BackupDiffFinishDate DATETIME2(2), @NetDir NVARCHAR(500);
	DECLARE @WeekDay tinyint, @MonthDay tinyint, @NewFB bit=0, @NewConf bit=0, @NewDB bit=0;
	DECLARE @OldBackupHH smallint;
	DECLARE @pToDate NVARCHAR(75);
	DECLARE @StrErr NVARCHAR(900);
	IF @ToDate IS NULL
		SET @pToDate='NULL';
	ELSE
		SET @pToDate=''''''+CONVERT(NVARCHAR(75),@ToDate,121)+'''''';
	--       . ( )  (    sputnik   ).
	DECLARE @TT TABLE ([DB_name] NVARCHAR(400), Backup_Type VARCHAR(4), BackupFile NVARCHAR(500), ID INT, BackupDate DATETIME2(2), LocalDir NVARCHAR(500), NetDir NVARCHAR(500), CheckLocalDir BIT, CheckNetDir BIT, CheckLocalFile BIT, CheckLocalFileOnly BIT, CheckNetFile BIT);
	IF @ServerSource IS NULL OR @ServerSource=@LocalServer
		INSERT INTO @TT
			EXEC sputnik.info.usp_GetLastBackups @DBName=@DBNameSource, @ToDate=@ToDate;
	ELSE
		INSERT INTO @TT
			EXEC ('	SELECT *
					FROM OPENQUERY(['+@ServerSource+'], '' EXEC sputnik.info.usp_GetLastBackups @DBName=N'''''+@DBNameSource+''''', @ToDate='+@pToDate+';'')
				 ');
	SELECT @BackupFullID=ID, @BackupFullFinishDate=BackupDate,  
		@FullPath=case when @FromCopy=0 OR NetDir is null OR NetDir='' then LocalDir+BackupFile else NetDir+BackupFile end
	FROM @TT
	WHERE Backup_Type='Full'
		AND (((@FromCopy=0 OR NetDir is null OR NetDir='') AND CheckLocalFile=1) OR ((@FromCopy=1 AND CheckNetFile=1)));

	--   ,     (   )!!
	IF @FullPath IS NULL AND @RunNewBackIfNeed=1 AND @ToDate IS NULL
		begin try	
			IF @ServerSource IS NULL OR @ServerSource=@LocalServer
				EXEC sputnik.backups.usp_RunBack @DBName_in=@DBNameSource, @TypeBack='Full', @OnlyFull=1, @ForceCopy=1;
			ELSE
				EXEC ('
					  EXEC(''
							EXEC sputnik.backups.usp_RunBack @DBName_in=N'''''+@DBNameSource+''''', @TypeBack=''''Full'''', @OnlyFull=1, @ForceCopy=1;
					  '') AT ['+@ServerSource+']
				');
			SET @NewFB=1;	
		end try
		begin catch
			SET @StrErr=N'        [usp_GC2]!  : '+ERROR_MESSAGE();
			RAISERROR(@StrErr,11,1) WITH LOG
		end catch
	--    ,     DIFF,   ,     (   ). 
	ELSE IF @FullPath IS NOT NULL
	BEGIN
		-- .   ,     12 !        !
		IF @ToDate IS NULL
			SET @OldBackupHH = DATEDIFF(MINUTE,@BackupFullFinishDate,SYSDATETIME());
		ELSE
			SET @OldBackupHH = DATEDIFF(MINUTE,@BackupFullFinishDate,@ToDate);
		IF @OldBackupHH>12*60
		BEGIN
			SELECT @BackupDiffID=ID, @BackupDiffFinishDate=BackupDate,  
			@DiffPath=case when @FromCopy=0 OR NetDir is null OR NetDir='' then LocalDir+BackupFile else NetDir+BackupFile end
			FROM @TT
			WHERE Backup_Type='Diff' AND BackupDate > @BackupFullFinishDate
				AND (((@FromCopy=0 OR NetDir is null OR NetDir='') AND CheckLocalFile=1) OR ((@FromCopy=1 AND CheckNetFile=1)))
				AND (@FreshBack=0 OR DATEDIFF(MINUTE,BackupDate,SYSDATETIME())<8*60)
			IF @DiffPath IS NULL AND @RunNewDiffBackIfNeed=1 AND @ToDate IS NULL
				begin try	
					--   Diff ,    !
					DECLARE @TTD TABLE (DB NVARCHAR(300));
					IF @ServerSource IS NULL OR @ServerSource=@LocalServer
						INSERT INTO @TTD
						SELECT [DBName] AS DB FROM sputnik.backups.BackConf WHERE Kind='Diff' AND DBName=@DBNameSource;
					ELSE
						INSERT INTO @TTD
						EXEC('SELECT [DBName] AS DB
							  FROM OPENQUERY(['+@ServerSource+'], ''SELECT [DBName] FROM sputnik.backups.BackConf WHERE Kind=''''Diff'''' AND DBName=N'''''+@DBNameSource+''''';'')
							  ');
					IF NOT EXISTS(SELECT DB FROM @TTD)
					BEGIN
						IF @ServerSource IS NULL OR @ServerSource=@LocalServer
							INSERT INTO sputnik.backups.BackConf ([DBName], LocalDir, NetDir, LocalDays, NetDays, Kind, [LocalPolicy],[NetPolicy])
							SELECT [DBName], LocalDir, NetDir, 1, 1, 'Diff' AS Kind, 1, 1
							FROM sputnik.backups.BackConf
							WHERE  Kind='Full' AND DBName=@DBNameSource
						ELSE
							EXEC ('
								EXEC(''
									INSERT INTO sputnik.backups.BackConf ([DBName], LocalDir, NetDir, LocalDays, NetDays, Kind, [LocalPolicy],[NetPolicy])
									SELECT [DBName], LocalDir, NetDir, 1, 1, ''''Diff'''' AS Kind, 1, 1
									FROM sputnik.backups.BackConf
									WHERE  Kind=''''Full'''' AND DBName=N'''''+@DBNameSource+''''';
								'') AT ['+@ServerSource+']
							');
						SET @NewConf=1;
					END
					-- .        :
					--         .,     !
					IF @ServerSource IS NULL OR @ServerSource=@LocalServer
					BEGIN
						EXEC sputnik.backups.usp_RunBack @DBName_in=@DBNameSource, @TypeBack='Diff', @ForceCopy=1;
						EXEC sputnik.backups.[usp_CleaningBack] @DBFilter=@DBNameSource, @type='Diff';
					END
					ELSE
						EXEC ('
							EXEC(''
								EXEC sputnik.backups.usp_RunBack @DBName_in=N'''''+@DBNameSource+''''', @TypeBack=''''Diff'''', @ForceCopy=1;
								EXEC sputnik.backups.[usp_CleaningBack] @DBFilter=N'''''+@DBNameSource+''''', @type=''''Diff'''';
							'') AT ['+@ServerSource+']
						');
					SET @NewDB=1;	
				end try
				begin catch
					SET @StrErr=N'    .    [usp_GC2]!  : '+ERROR_MESSAGE();
					RAISERROR(@StrErr,11,1) WITH LOG
				end catch
		END
	END
	--,    .   ,       !
	IF @NewDB=1 OR @NewFB=1
	BEGIN
		DELETE FROM @TT;
		IF @ServerSource IS NULL OR @ServerSource=@LocalServer
			INSERT INTO @TT
				EXEC sputnik.info.usp_GetLastBackups @DBName=@DBNameSource, @ToDate=@ToDate;
		ELSE
			INSERT INTO @TT
				EXEC ('	SELECT *
						FROM OPENQUERY(['+@ServerSource+'], ''EXEC sputnik.info.usp_GetLastBackups @DBName=N'''''+@DBNameSource+''''', @ToDate='+@pToDate+';'')
					');
		IF @NewFB=1
		BEGIN
			SELECT @File=BackupFile, @BackupFullID=ID, @BackupFullFinishDate=BackupDate,  
				@Dir=case when  @FromCopy=0 OR NetDir is null OR NetDir='' then LocalDir else NetDir end
			FROM @TT
			WHERE Backup_Type='Full'
				AND (((@FromCopy=0 OR NetDir is null OR NetDir='') AND CheckLocalFile=1) OR ((@FromCopy=1 AND CheckNetFile=1)));
			SET @FullPath=@Dir+@File;
		END
		IF @NewDB=1
		BEGIN 
			SELECT @File=BackupFile, @BackupDiffID=ID, @BackupDiffFinishDate=BackupDate,  
			@Dir=case when @FromCopy=0 OR NetDir is null OR NetDir='' then LocalDir else NetDir end
			FROM @TT
			WHERE Backup_Type='Diff' AND BackupDate > @BackupFullFinishDate
			AND (((@FromCopy=0 OR NetDir is null OR NetDir='') AND CheckLocalFile=1) OR ((@FromCopy=1 AND CheckNetFile=1)));
			IF @@ROWCOUNT>0
				SET @DiffPath=@Dir+@File;
			ELSE
				SET @DiffPath=NULL;
		END
	END			

	IF @FullPath IS NULL
		return 0;

	--  / ,      !
	IF @RM=1
	BEGIN
		DECLARE @LogOff TABLE(DBName nvarchar(300));
		IF @ServerSource IS NULL OR @ServerSource=@LocalServer
		BEGIN
			begin try
				UPDATE [sputnik].[backups].[BackConf]
				SET [Kind]='Log'
				OUTPUT inserted.[DBName] into @LogOff
				WHERE [DBName]=@DBNameSource AND [Kind]='XLog';
				EXEC sputnik.backups.usp_RunBack @DBName_in=@DBNameSource, @TypeBack='Log', @ForceCopy=1;
			end try
			begin catch
				SET @StrErr=N'         [usp_GC2]!  : '+ERROR_MESSAGE();
				RAISERROR(@StrErr,11,1) WITH LOG
			end catch
		END
		ELSE
			begin try
				INSERT INTO @LogOff
				EXEC ('	SELECT DBName
						FROM OPENQUERY(['+@ServerSource+'], ''SELECT DBName 
															  FROM [sputnik].[backups].[BackConf] 
															  WHERE [DBName]=N'''''+@DBNameSource+''''' AND [Kind]=''''XLog'''';'')
					');
				IF EXISTS(SELECT DBname FROM @LogOff)
				BEGIN
					SET XACT_ABORT ON;
					EXEC ('	UPDATE OPENQUERY(['+@ServerSource+'], ''SELECT [Kind]
																	FROM [sputnik].[backups].[BackConf] 
																	WHERE [DBName]=N'''''+@DBNameSource+''''' AND [Kind]=''''XLog'''';'')
							SET [Kind]=''Log''
		 			');
					SET XACT_ABORT OFF;
				END	
				EXEC ('
					EXEC(''
						EXEC sputnik.backups.usp_RunBack @DBName_in=N'''''+@DBNameSource+''''', @TypeBack=''''Log'''', @ForceCopy=1;
					'') AT ['+@ServerSource+']
				');
			end try
			begin catch
				SET @StrErr=N'         [usp_GC2]!  : '+ERROR_MESSAGE();
				RAISERROR(@StrErr,11,1) WITH LOG
			end catch
	END

	--    
	DECLARE @ChainBack TABLE (BackupFile NVARCHAR(800), BackupType VARCHAR(4), ID INT, BackupDate DATETIME2(2));	
	INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
	VALUES (@FullPath, 'Full', @BackupFullID, @BackupFullFinishDate);
	IF @DiffPath IS NOT NULL
		INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
		VALUES (@DiffPath, 'Diff', @BackupDiffID, @BackupDiffFinishDate);
	IF @ServerSource IS NULL OR @ServerSource=@LocalServer
		INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
			EXEC sputnik.info.usp_GetChainLogs @DBName=@DBNameSource, @BackupFullID=@BackupFullID, @FilterBackupID=@BackupDiffID, @ToDate=@ToDate, @fromcopy=@fromcopy;
	ELSE
	BEGIN
		DECLARE @strP VARCHAR(300);
		IF @BackupDiffID IS NOT NULL
			SET @strP='@BackupFullID='+CAST(@BackupFullID AS VARCHAR(40))+', @FilterBackupID='+CAST(@BackupDiffID AS NVARCHAR(40))+', @fromcopy='+CAST(@fromcopy AS VARCHAR(1));
		ELSE
			SET @strP='@BackupFullID='+CAST(@BackupFullID AS VARCHAR(40))+', @FilterBackupID=NULL, @fromcopy='+CAST(@fromcopy AS VARCHAR(1));
		INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
			EXEC ('	SELECT *
					FROM OPENQUERY(['+@ServerSource+'] ,''EXEC sputnik.info.usp_GetChainLogs @DBName=N'''''+@DBNameSource+''''',  @ToDate='+@pToDate+', '+@strP+';'')
				 ');
	END
	--   ,   /     
	IF @RM=1
	BEGIN
		IF EXISTS(SELECT DBName FROM @LogOff)
			IF @ServerSource IS NULL OR @ServerSource=@LocalServer
				UPDATE [sputnik].[backups].[BackConf]
				SET [Kind]='XLog'
				WHERE [DBName]=@DBNameSource AND [Kind]='Log';	
			ELSE
			BEGIN 
				SET XACT_ABORT ON;
				EXEC ('	UPDATE OPENQUERY(['+@ServerSource+'], ''SELECT [Kind]
																FROM [sputnik].[backups].[BackConf] 
																WHERE [DBName]=N'''''+@DBNameSource+''''' AND [Kind]=''''Log'''';'')
						SET [Kind]=''XLog''
		 			');
				SET XACT_ABORT OFF;
			END
	END

	--    (  )
	IF @DBNameTarget IS NULL
	begin
		declare @BackupFinishDate Datetime2(2);

		SELECT @BackupFinishDate=MAX([BackupDate])
		FROM @ChainBack;

		SET @DBNameTarget=@DBNameSource+'_COPY_'+CONVERT(VARCHAR(8), @BackupFinishDate, 112)+'_'+REPLACE(CONVERT(VARCHAR(20), @BackupFinishDate, 108), ':', '');
	END
	--    .
	DECLARE @BF nvarchar(800), @BT varchar(4);
	DECLARE RE CURSOR FOR
		SELECT BackupFile, BackupType
		FROM @ChainBack
		--ORDER BY ID;
	OPEN RE;
	FETCH NEXT FROM RE INTO @BF, @BT;
	WHILE @@FETCH_STATUS=0
	BEGIN
		begin try
		IF @BT='Full' 
			BEGIN
				if @pp=0
					PRINT ('********************************
						 ['+@DBNameTarget+']   : '+@BF);
				EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
					@DBNameTarget=@DBNameTarget, 
					@FromDisk=@BF,
					@MoveFilesTo=@MoveFilesTo,
					@MoveLogFilesTo=@MoveLogFilesTo,
					@NoRecovery=1,
					@NoStats=@pp,
					@StandBy_File=@StandBy_File
					--,@dbowner=@dbowner
				;
			END
			ELSE IF @BT='Diff' 
			BEGIN
				if @pp=0
					PRINT ('********************************
						 ['+@DBNameTarget+']  . : '+@BF);
				EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
					@DBNameTarget=@DBNameTarget, 
					@FromDisk=@BF,
					@MoveFilesTo=@MoveFilesTo,
					@MoveLogFilesTo=@MoveLogFilesTo,
					@NoRecovery=1,
					@DiffBack=1,
					@NoFileList=1,
					@NoStats=@pp,
					@StandBy_File=@StandBy_File
					--,@dbowner=@dbowner
				;
			END
			ELSE IF @BT='Log' 
			BEGIN
				if @pp=0
					PRINT ('********************************
						 ['+@DBNameTarget+']    (): '+@BF);
				EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
					@DBNameTarget=@DBNameTarget, 
					@FromLog=@BF,
					@MoveFilesTo=@MoveFilesTo,
					@MoveLogFilesTo=@MoveLogFilesTo,
					@NoRecovery=1,
					@NoFileList=1,
					@NoStats=@pp,
					@StandBy_File=@StandBy_File
					--,@dbowner=@dbowner
				;
			END
		end try 
		begin catch
			SET @StrErr=N'   [usp_GC2]     ['+@DBNameTarget+']  '+@BT+'- ('+@BF+') !  : '+ERROR_MESSAGE();
			RAISERROR(@StrErr,11,1) WITH LOG
		end catch
		FETCH NEXT FROM RE INTO @BF, @BT;
	END
	CLOSE RE;
	DEALLOCATE RE;
	--     RECOVERY
	IF @NoRecovery=0
	begin
		if @pp=0
			PRINT ('********************************
				     ONLINE +  db_owner');
		EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
			@DBNameTarget=@DBNameTarget, 
			@ForceRecovery=1,
			@dbowner=@dbowner;
	end;
	--     Log Shipping Easy,
	--  ID      sputnik.lse.TargetConfig
	IF @lse=1
	BEGIN
		UPDATE sputnik.lse.TargetConfig
		SET [InitBackupHS_id]=(SELECT MAX(ID) FROM @ChainBack),
			[InitDate]=@lseInitDate
		WHERE [DBNameTarget]=@DBNameTarget;
	END

	--      .    , 
	--                  !
	begin try
		DELETE @TTD;
		IF @ServerSource IS NULL OR @ServerSource=@LocalServer
			INSERT INTO @TTD
			SELECT DBName AS DB 
			FROM sputnik.backups.BackConf 
			WHERE DBName=@DBNameSource AND Kind='Diff' AND LocalDays=1 AND NetDays=1
				AND [LocalPolicy]=1 AND [NetPolicy]=1
				AND EXISTS (SELECT DBName AS DB FROM sputnik.backups.BackConf as BakF WHERE BakF.Kind='Full' AND BakF.DBName=@DBNameSource);
		ELSE
			INSERT INTO @TTD
			EXEC('SELECT [DBName] AS DB
				  FROM OPENQUERY(['+@ServerSource+'], ''SELECT [DBName] FROM sputnik.backups.BackConf WHERE Kind=''''Diff''''  AND LocalDays=1 AND NetDays=1 AND DBName=N'''''+@DBNameSource+''''' AND [LocalPolicy]=1 AND [NetPolicy]=1 AND EXISTS (SELECT DBName AS DB FROM sputnik.backups.BackConf as BakF WHERE BakF.Kind=''''Full'''' AND BakF.DBName=N'''''+@DBNameSource+''''');'')
				');
		IF @NewConf=1 OR EXISTS(SELECT DB FROM @TTD )
		BEGIN
			IF @ServerSource IS NULL OR @ServerSource=@LocalServer
			BEGIN	
				EXEC sputnik.backups.[usp_CleaningBack] @DBFilter=@DBNameSource, @type='Diff';
				DELETE 
				FROM sputnik.backups.BackConf
				WHERE DBName=@DBNameSource AND Kind='Diff';
				--        . 
				DELETE [sputnik].[backups].[BackupHistory]
				WHERE [ID] = @BackupDiffID;
			END
			ELSE
			BEGIN
				DECLARE @BackupDiffIDstr nvarchar(50);
				set @BackupDiffIDstr=CAST(@BackupDiffID AS NVARCHAR(50));
				EXEC ('
						EXEC(''
								EXEC sputnik.backups.[usp_CleaningBack] @DBFilter=N'''''+@DBNameSource+''''', @type=''''Diff'''';
								DELETE 
								FROM sputnik.backups.BackConf
								WHERE DBName=N'''''+@DBNameSource+''''' AND Kind=''''Diff'''';
								--        . 
								DELETE [sputnik].[backups].[BackupHistory]
								WHERE [ID] = '+@BackupDiffIDstr+';
						'') AT ['+@ServerSource+']
					');
			END
		END
	end try
	begin catch
		SET @StrErr=N'   [usp_GC2]        . !  : '+ERROR_MESSAGE();
		RAISERROR(@StrErr,11,1) WITH LOG
	end catch
	return 1;
END

	/* =============================================
	-- Author:		  (sqland1c)
	-- Create date: 04.08.2014 (2.0)
	-- Description: usp_GC2 - GetCopy (!).             .
					           !!  ,      . !
					     (  .)         !

					  -    usp_RestoreDB_simple (   , .     ).
					    sputnik,       .
					 ,           sputnik (   
					        sputnik).
					:
						@DBNameSource -  ,     (   );
						@DBNameTarget -  ,     (  ).   ,   
						    : @DBNameTarget +      .
						@ServerSource -  ,   ,    ,     !
						   Linked Server     !     , 
						              !
						@ToDate -  .    ,      .   ,  
						 .
						@FromCopy - ,        (NetDir),      (LocalDir).
						@RunNewBackIfNeed -       .      
						 .
						@FreshBack -    "" . .  .     8  ,   
						 .  (    @RunNewBackIfNeed).
						@lse -  , ,        LSE (Log Shipping Easy).   ,
						      !
	-- Update:
						05.08.2014 (2.1)
						     usp_RestoreDB_simple.       
						     , ..    @NoFileList=1,  
						 ,             sys.master_files,    .
					
						05.08.2014 (2.12)    @RM     / .
						   ,     ( )   !   
						       !

						05.08.2014 (2.13)       .  -   ,
						    .  .

						06.08.2014 (2.15)   /      ,   
						 @RM.  SELECT  UPDATE      OPENQUERY.      
						  XACT_ABORT=ON (   UPDATE  OPENQUERY).

						07.08.2014 (2.17)       (  )   !
						    @pp -  ,         pp.
						         !

						13.11.2014 (2.18)     [usp_RunBack]   @ForceCopy -    
						     (   ).

						18.03.2015 (2.20)    @StandBy_File -    STANDBY (read-only)   .  
						  lse.

						20.11.2015 (2.21)    @MoveLogFilesTo,  Log-     .
						      [usp_RestoreDB_simple].

						12.01.2016 (2.22)         usp_GetLastBackups

						28.02.2017 (2.230)   @dbowner -       (  sa).

						10.11.2017 (2.233)       :
						     / . : c 2  12  (),  2  8  (.)!
						       . (    ):
						   1  (  1-  ).
						    .     .,    .  !

						10.11.2017 (2.234)    @RunNewDiffBackIfNeed -    ..
						         @RunNewBackIfNeed
	-- ============================================= */
	CREATE PROCEDURE [backups].[usp_GC2]  
		@DBNameSource nvarchar(300),
		@DBNameTarget nvarchar(300)=NULL,
		@ServerSource nvarchar(300)=NULL,
		@ToDate datetime2(2)=NULL,
		@FromCopy bit=0,
		@RunNewBackIfNeed bit=0,
		@FreshBack bit=0,
		@NoRecovery bit=0,
		@MoveFilesTo nvarchar(500)=NULL,
		@MoveLogFilesTo nvarchar(500)=NULL,
		@lse bit=0,
		@RM bit=0,
		@pp bit=0,
		@StandBy_File nvarchar(500)=null,
		@dbowner nvarchar(50)='sa',
		@RunNewDiffBackIfNeed bit=0
	AS

BEGIN -- SQL Server declare @Ver nvarchar(128), @VerMain numeric(6,3); set @Ver=CAST(SERVERPROPERTY('ProductVersion') as nvarchar(128)); set @VerMain=cast(LEFT(@Ver, CHARINDEX('.',@Ver, CHARINDEX('.', @Ver)+1)-1) as numeric(6,3)); IF @ChangeDBOwner=0 BEGIN IF @ForceRecovery=0 BEGIN IF @NoRecovery=1 EXECUTE AS LOGIN = 'sa'; declare @LASTBACKUP nvarchar(600),@FULLBACKUP nvarchar(600), @tsql nvarchar(4000), @tsql2 nvarchar(4000)='', @type nvarchar(10), @typeF int, @ErrMsg nvarchar(800); declare @All smallint, @i smallint, @File_Id int, @PrevFileID int, @DB_ID int, @FileName nvarchar(70), @NewFileName nvarchar (400), @tmkdir nvarchar(550); declare @Tab TABLE (FileName nvarchar(128), FileID bigint, type tinyint); declare @RecoveryState nvarchar(650), @RESTORE VARCHAR(8), @SingleUserMode nvarchar(300)='', @MultiUserMode nvarchar(300)=''; declare @StrStats NVARCHAR(20)=''; IF @NoStats=0 SET @StrStats = N',STATS=10';
IF @FromLog IS NULL AND @DiffBack=0 AND @ContinueRecovery=0 BEGIN IF DB_ID(@DBNameTarget) IS NOT NULL BEGIN BEGIN TRY IF @del_bak_hs=1 EXEC msdb.dbo.sp_delete_database_backuphistory @database_name = @DBNameTarget; -- ( ) , ! IF EXISTS(SELECT database_id FROM sys.database_mirroring WHERE mirroring_guid IS NOT NULL AND database_id=DB_ID(@DBNameTarget)) BEGIN SET @tsql='ALTER DATABASE ['+@DBNameTarget+'] SET PARTNER OFF'; EXEC (@tsql); END; -- SINGLE_USER ( Online Multi_USer). IF EXISTS(SELECT database_id FROM SYS.DATABASES WHERE name = @DBNameTarget AND state_desc='ONLINE' AND user_access_desc='MULTI_USER') BEGIN SET @tsql='ALTER DATABASE ['+@DBNameTarget+'] SET SINGLE_USER WITH ROLLBACK IMMEDIATE'; EXEC (@tsql); END SET @tsql='DROP DATABASE ['+@DBNameTarget+']'; EXEC (@tsql); END TRY BEGIN CATCH set @ErrMsg='usp_RestoreDB_simple: / ! : '+ERROR_MESSAGE(); RAISERROR(@ErrMsg,11,1) WITH LOG END CATCH END END
IF @MoveFilesTo IS NULL AND @FromLog IS NULL BEGIN -- , , . DECLARE @MaxDrive CHAR(1); IF @FromLog IS NULL AND @DiffBack=0 exec sputnik.info.usp_GetDrives @GetMaxFree=1, @MaxFreeDrive=@MaxDrive OUTPUT; ELSE SELECT TOP 1 @MaxDrive=LEFT(physical_name,1) FROM SYS.MASTER_FILES WHERE database_id = DB_ID(@DBNameTarget); SET @MoveFilesTo=@MaxDrive+':\DATA\'+@DBNameTarget+'\'; END;
if @FromDisk is null AND @FromLog IS NULL begin set @FULLBACKUP='';
if @ToDate is null set @ToDate=getdate()
IF @ServerSource IS NULL SET @ServerSource=@@SERVERNAME;
IF @DBNameSource IS NULL SET @DBNameSource=@DBNameTarget;
set @tsql=N'select @res1= ltrim(rtrim(MF.physical_device_name)), @res2=BS.type from (SELECT top 1 BS.media_set_id, BS.name, BS.type FROM ['+@ServerSource+'].[msdb].[dbo].[backupset] as BS where BS.database_name = '''+@DBNameSource+''' and (BS.type in (''I'',''D'')) and (BS.backup_finish_date <= '''+convert(nvarchar(23),@ToDate,109)+''') order by BS.backup_finish_date desc) as BS inner join ['+@ServerSource+'].[msdb].[dbo].[backupmediafamily] as MF on BS.media_set_id=MF.media_set_id';
exec sp_executesql @tsql, N'@res1 nvarchar(300) OUTPUT,@res2 nvarchar(10) OUTPUT', @res1=@LASTBACKUP OUTPUT,@res2=@type OUTPUT
IF @type='I' -- , ! BEGIN set @tsql=N'select @res1= ltrim(rtrim(MF.physical_device_name)) from (SELECT top 1 BS.media_set_id, BS.name FROM ['+@ServerSource+'].[msdb].[dbo].[backupset] as BS where BS.database_name = '''+@DBNameSource+''' and BS.type = ''D'' order by BS.backup_finish_date desc) as BS inner join ['+@ServerSource+'].[msdb].[dbo].[backupmediafamily] as MF on BS.media_set_id=MF.media_set_id';
exec sp_executesql @tsql, N'@res1 nvarchar(300) OUTPUT', @res1=@FULLBACKUP OUTPUT END end else begin IF @FromLog IS NULL set @LASTBACKUP=@FromDisk; ELSE set @LASTBACKUP=@FromLog; set @FULLBACKUP=''; end set @LASTBACKUP=replace(@LASTBACKUP,'.only','.BAK'); IF @FULLBACKUP<>'' set @FULLBACKUP=replace(@FULLBACKUP,'.only','.BAK');
-- MoveFilesTo Log --.. Restore Log MoveFilesTo . IF @FromLog IS NULL BEGIN -- , @Tab if @NoFileList=1 AND EXISTS(select database_id from sys.databases where name=@DBNameTarget and state=1) -- @NoFileList=1, sys.master_files -- , RESTORING. insert into @Tab (FileName,FileID, type) select name as FileName, file_id as FileID, type from sys.master_files where database_id=db_id(@DBNameTarget); else begin -- ! if object_id('tempdb.dbo.#FileList') is not null drop table #FileList; create table #FileList ( LogicalName nvarchar(128), PhysicalName nvarchar(260), Type char(1), FileGroupName nvarchar(128), Size numeric(20,0), MaxSize numeric(20,0), FileId bigint, CreateLSN numeric(25,0), DropLSN numeric(25,0) NULL, UniqueID uniqueidentifier, ReadOnlyLSN numeric(25,0) NULL, ReadWriteLSN numeric(25,0) NULL, BackupSizeInBytes bigint, SourceBlockSize int, FileGroupId int, LogGroupGUID uniqueidentifier NULL, DifferentialBaseLSN numeric(25,0) NULL, DifferentialBaseGUID uniqueidentifier NULL, isReadOnly bit, isPresent bit, TDEThumbrint varbinary(32), SnapshotURL nvarchar(360) )
insert into #FileList exec( 'restore filelistonly from disk=N'''+@LASTBACKUP+'''' ) insert into @Tab (FileName,FileID, type) select LogicalName as FileName, FileID, case when Type='L' then 1 when Type='D' then 0 when Type='F' then 2 when Type='S' then 3 --FileStream end as type from #FileList end
-- Restore . declare CF Cursor for select FileName,FileID,type from @Tab; open CF; fetch next from CF into @FileName, @File_Id, @typeF; while @@Fetch_Status=0 begin if @File_ID=1 set @NewFileName=@DBNameTarget++'.MDF'; else if @typeF=0 set @NewFileName=@DBNameTarget+cast(@File_Id as nvarchar(10))+'.NDF'; else if @typeF=1 set @NewFileName=@DBNameTarget+'_log_'+cast(@File_Id as nvarchar(10))+'.LDF'; else if @typeF=2 set @NewFileName=@DBNameTarget+'_FullTextSearchCatalog_'+cast(@File_Id as nvarchar(10))+'.NDF'; else if @typeF=3 begin set @NewFileName=@DBNameTarget+'_FS_'+@FileName+'\'; -- FileStream ( ). set @tmkdir = N'mkdir "' + @MoveFilesTo+@NewFileName + '"'; exec xp_cmdshell @tmkdir, no_output; end set @tsql2=@tsql2+N' MOVE N'''+@FileName+''' TO N'''+ CASE WHEN @MoveLogFilesTo IS NOT NULL AND @typeF=1 THEN @MoveLogFilesTo ELSE @MoveFilesTo END + @NewFileName+''','; fetch next from CF into @FileName, @File_Id, @typeF; end close CF; deallocate CF; END;
IF @LASTBACKUP IS NOT NULL BEGIN -- @MoveFilesTo ( ). set @tmkdir = N'mkdir "' + @MoveFilesTo + '"'; exec xp_cmdshell @tmkdir, no_output; if @MoveLogFilesTo is not null begin -- @@MoveLogFilesTo ( ). set @tmkdir = N'mkdir "' + @MoveLogFilesTo + '"'; exec xp_cmdshell @tmkdir, no_output; end -- Restore. if @NoRecovery=1 set @RecoveryState='NORECOVERY'; else set @RecoveryState='RECOVERY'; if @StandBy_File IS NOT NULL AND @FromLog IS NOT NULL BEGIN set @RecoveryState='STANDBY=N'''+@StandBy_File+''''; if exists (select database_id from sys.databases where name=@DBNameTarget and is_in_standby=1 and [state]=0) BEGIN set @SingleUserMode=' ALTER DATABASE ['+@DBNameTarget+'] SET SINGLE_USER WITH ROLLBACK IMMEDIATE; '; if @NoSetMultiUser=0 set @MultiUserMode=' ALTER DATABASE ['+@DBNameTarget+'] SET MULTI_USER; '; END END if @FULLBACKUP='' begin IF @FromLog IS NULL SET @RESTORE='DATABASE'; ELSE SET @RESTORE='LOG';
set @tsql=@SingleUserMode+ 'RESTORE ' +@RESTORE+ ' ['+@DBNameTarget+'] FROM DISK = N'''+@LASTBACKUP+''' WITH FILE = 1, '+@tsql2+' '+@RecoveryState+',NOUNLOAD, REPLACE'+@StrStats+';'+ +@MultiUserMode; end else begin set @tsql= 'RESTORE DATABASE ['+@DBNameTarget+'] FROM DISK = N'''+@FULLBACKUP+''' WITH FILE = 1, '+@tsql2+' NORECOVERY, NOUNLOAD, REPLACE'+@StrStats; --print (@tsql); exec (@tsql); set @tsql= 'RESTORE DATABASE ['+@DBNameTarget+'] FROM DISK = N'''+@LASTBACKUP+''' WITH FILE = 1, '+@tsql2+' '+@RecoveryState+', NOUNLOAD, REPLACE'+@StrStats; end; --print (@tsql); exec (@tsql); END ELSE select ' !!!' END ELSE BEGIN set @tsql='RESTORE DATABASE ['+@DBNameTarget+'] WITH RECOVERY'; EXEC (@tsql); END END ELSE set @NoRecovery=0;
IF @NoRecovery=0 BEGIN -- -- (, ) SET @tsql=N'USE [master] ALTER DATABASE ['+@DBNameTarget+'] SET RECOVERY SIMPLE WITH NO_WAIT;'; --print (@tsql); EXEC (@tsql); SET @tsql=N'USE ['+@DBNameTarget+']; IF EXISTS(select principal_id from sys.database_principals where [name]='''+@dbowner+''')'+ CASE WHEN @VerMain < 11 THEN ' exec sp_addrolemember ''db_owner'','''+@dbowner+''';' ELSE ' ALTER ROLE [db_owner] ADD MEMBER ['+@dbowner+'];' END+' ELSE ALTER AUTHORIZATION ON DATABASE::['+@DBNameTarget+'] TO ['+@dbowner+'];'; --print (@tsql); EXEC (@tsql);
-- 128 .: EXEC sputnik.db_maintenance.usp_ShrinkLogFile @db_name=@DBNameTarget, @SetSizeMb=128, @FileGrowthMb=64 END END

	/* =============================================
	-- Author:		  (sqland1c)
	-- Create date: 17.12.2013 (1.0)
	-- Description:             (    ).
					       ,       
					 LinkedServer c   @ServerSource (     RestoreDB_Link.
					 ,         ,   !
					  @dbowner    (    SQL Server)     (   1   )!
					  @MoveFilesTo        .
	-- Update:
					09.01.2014 (1.1)
						  @ToDate -       ( ).
					17.01.2014 (1.2)
						   .  :        -,  restore  !	
					21.01.2014 (1.25)
						   @MoveFilesTo,   .   xp_cmdshell.
					28.01.2014 (1.27)
						   @NoRecovery -   1,       NoRecovery (  ).
							  0.
						      STATS=10 -     10 %.
					26.02.2014 (1.28)
						  -      "",     .
					28.02.2014 (1.4)
						  :        (     ).
						        -          @FromDisk.
					04.03.2014 (1.41)
						          .
					02.04.2014 (1.43)
						  -  ONLY  BAK       (    ).
					06.04.2014 (1.5)
						     !         (   ).
						        !         -
						 ,        \DATA\  .
					19.04.2014 (1.6)
						  @FromLog -    . ,     
						  !
					20.04.2014 (1.61)
						        .
					22.04.2014 (1.62)
						      sa  ,   @NoRecovery=1.    
						 sa,    .
					23.04.2014 (1.65)
						  @ForceRecovery.         RECOVERY.  
						         RECOVERY.    ,   
						 .
					03.06.2014 (1.7)
						  @DiffBack.     ,    @FromDisk . .  
						  .  (   ).
					05.08.2014 (1.8)
						  @NoFileList.   1,            ( 
						 sys.master_files. ,       .
						          ,    !
					07.08.2014 (1.81)
						  @NoStats,          -  
						( STATS   RESTORE).
					24.09.2014 (1.82)
						              . 
						         -    ERROR_MESSAGE.
					18.03.2015 (1.85)
						   @StandBy_File -    STANDBY (read-only)   .  
						  lse.
					20.03.2015 (1.86)
						      STANDBY (read-only) -       
						   single_user,        .
					14.08.2015 (1.90)
						  FileStream-!
						        !
						      PRIMARY     @NoRecovery=1. 
						        @ContinueRecovery=1.        
						(     READONLY!),  Primary.        ( )!
					25.10.2015 (1.92)
						   Log-     MoveFilesTo (      Restore Log...)
						   @MoveLogFilesTo -    Log-   .    ,
						          @MoveFilesTo
					15.12.2015 (1.93)
						      @tsql  @tsql2          !
					10.09.2016 (1.94)
						   @NoSetMultiUser!
					05.04.2017 (1.950)
						   @ChangeDBOwner!  =1,       ,  .       ,     db_owner.
						  -    128 ( 512).
					13.04.2017 (1.953)
						     restore filelistonly.      .
						    2016  (  ).
					22.02.2018 (1.957)
						   @del_bak_hs -  1,        (  ).
						  0.
				07.08.2019 (1.961)
					     SQL Server (<2012)         .
				26.11.2020 (1.962)
					Added new column for FILELISTONLY (for compability with 2019)
-- ============================================= */
CREATE PROCEDURE [backups].[usp_RestoreDB_simple]  
	@DBNameTarget nvarchar(200),
	@DBNameSource nvarchar(200)=null,
	@ServerSource nvarchar(200)=null,
	@dbowner nvarchar(50)='sa',
	@MoveFilesTo nvarchar(500)=NULL,
	@MoveLogFilesTo nvarchar(500)=NULL,
	@ToDate datetime=null,
	@NoRecovery bit = 0,
	@FromDisk nvarchar(600)=null,
	@DiffBack bit = 0,
	@FromLog  nvarchar(600)=null,
	@ForceRecovery bit = 0,
	@NoFileList bit = 0,
	@NoStats bit = 0,
	@StandBy_File nvarchar(500)=null,
	@ContinueRecovery bit = 0,
	@NoSetMultiUser bit=0,
	@ChangeDBOwner bit = 0,
	@del_bak_hs bit = 0
AS

BEGIN SET NOCOUNT ON; -- [sa], Linked Server .
EXECUTE AS LOGIN = 'sa';
-- 1- 2- ; EXEC(' EXEC (''IF EXISTS(SELECT database_id FROM sys.database_mirroring WHERE mirroring_guid IS NOT NULL AND database_id=DB_ID('''''+@DB+''''')) ALTER DATABASE ['+@DB+'] SET PARTNER OFF; '') AT ['+@ServerMirror+'.PECOM.LOCAL] ');
EXEC(' IF EXISTS(SELECT database_id FROM sys.database_mirroring WHERE mirroring_guid IS NOT NULL AND database_id=DB_ID('''+@DB+''')) ALTER DATABASE ['+@DB+'] SET PARTNER OFF; ');
-- LOg - , ! IF NOT EXISTS(SELECT [DBName] FROM sputnik.backups.BackConf WHERE Kind='Log' AND DBName=@DB) BEGIN INSERT INTO sputnik.backups.BackConf ([DBName], LocalDir, NetDir, LocalDays, NetDays, Kind) SELECT TOP 1 [DBName], LocalDir+'LOG\', NetDir+'LOG\', 2, 2, 'Log' AS Kind FROM sputnik.backups.BackConf WHERE DBName=@DB END;
-- 2- ! EXEC [sputnik].[backups].[usp_StartBackup] @type = N'Log', @DBFilter = @DB; -- 2- : EXEC [sputnik].[db_maintenance].[usp_ShrinkLogFile] @db_name = @DB, @SetSizeMb = 2048, @FileGrowthMb = 128 -- 2- ! UPDATE [sputnik].[backups].[BackConf] SET [Kind]='XLog' WHERE [DBName]=@DB AND [Kind]='Log';
--: ! DECLARE @TT TABLE ([DB_name] NVARCHAR(400), Backup_Type VARCHAR(4), BackupFile NVARCHAR(500), ID INT, BackupDate DATETIME2(2), LocalDir NVARCHAR(500), NetDir NVARCHAR(500), CheckLocalFile BIT, CheckNetFile BIT); INSERT INTO @TT EXEC sputnik.info.usp_GetLastBackups @DBName=@DB, @Backup_type='Full';
DECLARE @FullBackupNetFile NVARCHAR(800); SELECT @FullBackupNetFile=NetDir+BackupFile FROM @TT WHERE CheckNetFile = 1 AND DATEDIFF(minute,BackupDate,SYSDATETIME()) < 10081
DECLARE @NewConf bit = 0; -- , ! IF @FullBackupNetFile IS NULL EXEC [sputnik].[backups].[usp_StartBackup] @type = N'Full', @DBFilter = @DB ELSE BEGIN /* : . .
( Full) . sputnik */ IF NOT EXISTS(SELECT [DBName] FROM sputnik.backups.BackConf WHERE Kind='Diff' AND DBName=@DB) BEGIN INSERT INTO sputnik.backups.BackConf ([DBName], LocalDir, NetDir, LocalDays, NetDays, Kind) SELECT [DBName], LocalDir, NetDir, 0, 0, 'Diff' AS Kind FROM sputnik.backups.BackConf WHERE Kind='Full' AND DBName=@DB SET @NewConf=1; END -- . 2- ! EXEC [sputnik].[backups].[usp_StartBackup] @type = N'Diff', @DBFilter = @DB END
-- + . --( , : !) DECLARE @FullDir NVARCHAR(500),@DiffDir NVARCHAR(500), @FullFile NVARCHAR(300), @DiffFile NVARCHAR(300), @CMD NVARCHAR(1100); IF @FullBackupNetFile IS NULL BEGIN SELECT @FullDir=NetDir FROM sputnik.backups.BackConf WHERE DBName = @DB AND Kind = 'Full'; SELECT @FullFile=M.[Backup_File]+'.BAK' FROM [sputnik].[backups].[BackupHistory] M INNER JOIN ( SELECT [DB_Name], [Backup_Type], MAX(backup_finish_date) AS [BackupDate] FROM [sputnik].[backups].[BackupHistory] WHERE [DB_Name]=@DB AND [Backup_Type] = 'Full' GROUP BY [DB_Name], [Backup_Type] ) G ON M.[DB_Name]=G.[DB_Name] AND M.Backup_Type=G.Backup_Type AND M.backup_finish_date=G.[BackupDate] SET @FullBackupNetFile=@FullDir+@FullFile; END ELSE BEGIN -- . , , . SELECT @DiffDir=NetDir FROM sputnik.backups.BackConf WHERE DBName = @DB AND Kind = 'Diff'; SELECT @DiffFile=M.[Backup_File]+'.BAK' FROM [sputnik].[backups].[BackupHistory] M INNER JOIN ( SELECT [DB_Name], [Backup_Type], MAX(backup_finish_date) AS [BackupDate] FROM [sputnik].[backups].[BackupHistory] WHERE [DB_Name]=@DB AND [Backup_Type] = 'Diff' GROUP BY [DB_Name], [Backup_Type] ) G ON M.[DB_Name]=G.[DB_Name] AND M.Backup_Type=G.Backup_Type AND M.backup_finish_date=G.[BackupDate] END -- EXEC(' EXEC ('' EXEC [sputnik].[backups].[usp_RestoreDB_simple] @DBNameTarget=N'''''+@DB+''''', @FromDisk=N'''''+@FullBackupNetFile+''''', @NoRecovery=1 '') AT ['+@ServerMirror+'.PECOM.LOCAL] '); IF @DiffFile IS NOT NULL -- EXEC(' EXEC ('' EXEC [sputnik].[backups].[usp_RestoreDB_simple] @DBNameTarget=N'''''+@DB+''''', @FromDisk=N'''''+@DiffDir+@DiffFile+''''', @NoRecovery=1, @DiffBack=1 '') AT ['+@ServerMirror+'.PECOM.LOCAL] ');
-- 2- ! -- 2- ! UPDATE [sputnik].[backups].[BackConf] SET [Kind]='Log' WHERE [DBName]=@DB AND [Kind]='XLog'; EXEC [sputnik].[backups].[usp_StartBackup] @type = N'Log', @DBFilter = @DB; -- 2- ! UPDATE [sputnik].[backups].[BackConf] SET [Kind]='XLog' WHERE [DBName]=@DB AND [Kind]='Log'; -- ! DECLARE @Dir NVARCHAR(500), @File NVARCHAR(300); SELECT @File=[Backup_File]+'.BAK' FROM [sputnik].[backups].[BackupHistory] WHERE ID = ( SELECT ID FROM [sputnik].[backups].[BackupHistory] WHERE [DB_Name]=@DB AND [Backup_Type]='Log' AND [backup_finish_date] = (SELECT MAX(backup_finish_date) FROM [sputnik].[backups].[BackupHistory] WHERE [DB_Name]=@DB AND [Backup_Type]='Log' ) ) SELECT @Dir=NetDir FROM sputnik.backups.BackConf WHERE DBName = @DB AND Kind = 'XLog'; EXEC(' EXEC ('' EXEC [sputnik].[backups].[usp_RestoreDB_simple] @DBNameTarget=N'''''+@DB+''''', @FromLog=N'''''+@Dir+@File+''''', @NoRecovery=1 '') AT ['+@ServerMirror+'.PECOM.LOCAL] ')
-- EndPoint ( ) 1- 2- . EXEC(' IF NOT EXISTS (SELECT endpoint_id FROM sys.endpoints WHERE name = ''EndPoint_Mirroring'') CREATE ENDPOINT [EndPoint_Mirroring] STATE=started AS TCP (listener_port=5022, listener_ip=all) FOR database_mirroring (role=partner, authentication=windows negotiate, encryption=required algorithm AES); '); EXEC(' EXEC('' IF NOT EXISTS (SELECT endpoint_id FROM sys.endpoints WHERE name = ''''EndPoint_Mirroring'''') CREATE ENDPOINT [EndPoint_Mirroring] STATE=started AS TCP (listener_port=5022, listener_ip=all) FOR database_mirroring (role=partner, authentication=windows negotiate, encryption=required algorithm AES); '') AT ['+@ServerMirror+'.PECOM.LOCAL]; ');
-- 1- , 2- (): EXEC(' EXEC ('' ALTER DATABASE ['+@DB+'] SET PARTNER = N''''TCP://'+@ServerProd+'.PECOM.LOCAL:5022''''; '') AT ['+@ServerMirror+'.PECOM.LOCAL] '); EXEC (' ALTER DATABASE ['+@DB+'] SET PARTNER = N''TCP://'+@ServerMirror+'.PECOM.LOCAL:5022''; ');
-- 2- ! UPDATE [sputnik].[backups].[BackConf] SET [Kind]='Log' WHERE [DBName]=@DB AND [Kind]='XLog';
-- . , -- !
IF @NewConf=1 OR EXISTS(SELECT DBName FROM sputnik.backups.BackConf WHERE DBName=@DB AND Kind='Diff' AND LocalDays=0 AND NetDays=0 ) BEGIN EXEC sputnik.backups.[usp_CleaningBack] @DBFilter=@DB, @type='Diff';
DELETE FROM sputnik.backups.BackConf WHERE DBName=@DB AND Kind='Diff';
-- . DELETE [sputnik].[backups].[BackupHistory] WHERE [ID] = ( SELECT M.[ID] FROM [sputnik].[backups].[BackupHistory] M INNER JOIN ( SELECT [DB_Name], [Backup_Type], MAX(backup_finish_date) AS [BackupDate] FROM [sputnik].[backups].[BackupHistory] WHERE [DB_Name]=@DB AND [Backup_Type] = 'Diff' GROUP BY [DB_Name], [Backup_Type] ) G ON M.[DB_Name]=G.[DB_Name] AND M.Backup_Type=G.Backup_Type AND M.backup_finish_date=G.[BackupDate] );
END END

BEGIN
	SET NOCOUNT ON;
	--    [sa],   Linked Server      .
	EXECUTE AS LOGIN = 'sa';
	DECLARE @StrErr NVARCHAR(900);
	--   :     ,    !
	--   @ForceRM=1,     !
	DECLARE @MirrorState VARCHAR(30);
	select @MirrorState=mirroring_state_desc
	from sys.database_mirroring
	where DB_NAME(database_id) =@DB
		and mirroring_guid is not null;
	IF @MirrorState IN ('SYNCHRONIZING','SYNCHRONIZED') AND @ForceRM=0 AND @DisableMirroring=0
	begin
		set @StrErr=N'  backups.usp_RM2 , ..   ['+@DB+']     ('+@MirrorState+').
					   ,   backups.usp_RM2   @ForceRM=1';
		PRINT(@StrErr);
		return 0;
	end

	--  ,     Full Recovery Model!
	DECLARE @RecMod varchar(25);
	SELECT @RecMod=recovery_model_desc
	FROM sys.databases
	WHERE name = @DB
	IF @RecMod<>'FULL' AND @DisableMirroring=0
	begin
		set @StrErr=N'  backups.usp_RM2 , ..   ['+@DB+']   <> "FULL" ('+@MirrorState+').
					     Recovery Model=FULL!';
		PRINT(@StrErr);
		return 0;
	end

	DECLARE @LocalServer NVARCHAR(600);
	SELECT @LocalServer=CAST(SERVERPROPERTY('MachineName') AS NVARCHAR(300))+COALESCE('\'+CAST(SERVERPROPERTY('InstanceName') AS NVARCHAR(300)),'');

	--   1-  2- ;
	EXEC('
			EXEC
			(''IF EXISTS(SELECT database_id FROM sys.database_mirroring WHERE mirroring_guid IS NOT NULL AND database_id=DB_ID('''''+@DB+'''''))
					ALTER DATABASE ['+@DB+'] SET PARTNER OFF;
			'')
			AT ['+@ServerMirror+']
		');

	EXEC('
		IF EXISTS(SELECT database_id FROM sys.database_mirroring WHERE mirroring_guid IS NOT NULL AND database_id=DB_ID('''+@DB+'''))
				ALTER DATABASE ['+@DB+'] SET PARTNER OFF;
		');
	IF @DisableMirroring=1
		return 0;

	--    LOg -   ,  !
	IF NOT EXISTS(SELECT [DBName] FROM sputnik.backups.BackConf WHERE Kind IN ('Log','XLog') AND DBName=@DB)
	BEGIN
		INSERT INTO sputnik.backups.BackConf ([DBName], LocalDir, NetDir, LocalDays, NetDays, Kind)
		SELECT TOP 1
			[DBName], LocalDir+'LOG\', NetDir+'LOG\', 2, 2, 'Log' AS Kind
		FROM sputnik.backups.BackConf
		WHERE DBName=@DB
	END;

	--                  !
	UPDATE [sputnik].[backups].[BackConf]
		SET [Kind]='XLog'
		WHERE [DBName]=@DB AND [Kind]='Log';

	--          usp_GC2
	declare @MoveFilesTo_str nvarchar(500), @MoveLogFilesTo_str nvarchar(500);
	select
		@MoveFilesTo_str=
		CASE 
			WHEN @MoveFilesTo IS NULL THEN ''
			ELSE ', @MoveFilesTo=N'''''+@MoveFilesTo+''''''
		END,
		@MoveLogFilesTo_str=
		CASE 
			WHEN @MoveLogFilesTo IS NOT NULL AND @MoveFilesTo IS NOT NULL THEN ', @MoveLogFilesTo=N'''''+@MoveLogFilesTo+''''''
			ELSE ''
		END;

	DECLARE @UseFreshDiffBack_char CHAR(1);
	SET @UseFreshDiffBack_char=CASE @UseFreshDiffBack WHEN 1 THEN '1' ELSE '0' END;
	begin try
		EXEC('
			EXEC
			(''exec sputnik.backups.usp_GC2 @ServerSource=N'''''+@LocalServer+''''', @DBNameSource=N'''''+@DB+''''', @DBNameTarget=N'''''+@DB+''''',
											@FromCopy=1, @NoRecovery=1, @RunNewBackIfNeed=1, @FreshBack='+@UseFreshDiffBack_char+', @RM=1'+@MoveFilesTo_str+@MoveLogFilesTo_str+',@RunNewDiffBackIfNeed='+@UseFreshDiffBack_char+';
			'')
			AT ['+@ServerMirror+']
		');
	end try
	begin catch
		SET @StrErr=N'    [usp_GC2]        !  : '+ERROR_MESSAGE();
		RAISERROR(@StrErr,11,1) WITH LOG
	end catch

	-- EndPoint   (  )  1-  2- .
	EXEC('
			IF NOT EXISTS (SELECT endpoint_id FROM sys.endpoints WHERE name = ''EndPoint_Mirroring'')
				CREATE ENDPOINT [EndPoint_Mirroring]
					STATE=started
					AS TCP (listener_port=5022, listener_ip=all)
					FOR database_mirroring (role=partner, authentication=windows negotiate, encryption=required algorithm AES);
		');
	EXEC('
			EXEC(''
				IF NOT EXISTS (SELECT endpoint_id FROM sys.endpoints WHERE name = ''''EndPoint_Mirroring'''')
					CREATE ENDPOINT [EndPoint_Mirroring]
						STATE=started
						AS TCP (listener_port=5022, listener_ip=all)
						FOR database_mirroring (role=partner, authentication=windows negotiate, encryption=required algorithm AES);
				'')
			AT ['+@ServerMirror+'];
		');

	--     ,    :
	begin try
		--        (),
		--      SQL Server   
		--   SQL Server (  ).
		DECLARE @MachineName nvarchar(300);
		SET @MachineName=SUBSTRING(@ServerProd,0,CASE CHARINDEX('\',@ServerProd) WHEN 0 THEN LEN(@ServerProd)+1 ELSE CHARINDEX('\',@ServerProd) END)
		EXEC('
				EXEC
				(''
					ALTER DATABASE ['+@DB+'] SET PARTNER = N''''TCP://'+@MachineName+'.PECOM.LOCAL:5022'''';
				'')
				AT ['+@ServerMirror+']
		');
		SET @MachineName=SUBSTRING(@ServerMirror,0,CASE CHARINDEX('\',@ServerMirror) WHEN 0 THEN LEN(@ServerMirror)+1 ELSE CHARINDEX('\',@ServerMirror) END)
		EXEC
			('
				ALTER DATABASE ['+@DB+'] SET PARTNER = N''TCP://'+@MachineName+'.PECOM.LOCAL:5022'';
			');
	end try
	begin catch
		SET @StrErr=N'   [usp_RM2]    !  : '+ERROR_MESSAGE();
		RAISERROR(@StrErr,11,1) WITH LOG
	end catch 

	--       2- !
	UPDATE [sputnik].[backups].[BackConf]
		SET [Kind]='Log'
		WHERE [DBName]=@DB AND [Kind]='XLog';

END

	/* =============================================
	-- Author:		  (sql1c)
	-- Create date: 05.08.2014 (2.0)
	-- Description:	usp_RM - REPAIR MIRROR - /    .
					   - backup/restore     sputnik.
	   Update:
					05.08.2014 (2.0)
					    usp_RM!     usp_GC2       !
					    !  ,      try...catch!
				
					06.10.2014 (2.01)
					   @DisableMirroring.          .

					06.05.2015 (2.05)
					    SQL Server     . 
					   @ServerProd  @ServerMirror     SQL Server
					     (, pecom.local  )!
					       SQL Server.

					20.11.2015 (2.07)
					   @MoveFilesTo  @MoveLogFilesTo  ,    
					  .   ,     ,   
					 .

					10.11.2017 (2.080)
					   @UseFreshDiffBack -    
					.     ,     
					.  (     ).
					  .
	-- ============================================= */
	CREATE PROCEDURE backups.usp_RM2
		@ServerProd NVARCHAR(200),
		@ServerMirror NVARCHAR(200),
		@DB NVARCHAR(300),
		@ForceRM bit = 0,
		@DisableMirroring bit = 0,
		@MoveFilesTo nvarchar(500)=NULL,
		@MoveLogFilesTo nvarchar(500)=NULL,
		@UseFreshDiffBack bit = 1
	AS

BEGIN
	set nocount on;
	DECLARE @DBName NVARCHAR(300), @LocalDir NVARCHAR(500), @NetDir NVARCHAR(500), @LocalDays int, @NetDays INT, @tint int, @getdate datetime, @Extension nvarchar (20), @Edition nvarchar(100), @Compression nvarchar(50), @FG nvarchar(1000)=NULL, @str_FG nvarchar(2000)='', @str_FG_fname nvarchar(1000)='';
	DECLARE @tstr NVARCHAR(1100), @tcmd NVARCHAR(400),@rc int,@backup_file nvarchar(500), @WeekDay tinyint, @DynTypeBack varchar(4), @MonthDay tinyint, @StrStats NVARCHAR(20)='';
	SET @Edition=CAST(SERVERPROPERTY('Edition') as nvarchar(100));
	SELECT @Compression=CASE WHEN CHARINDEX('Express',@Edition)>0 THEN '' ELSE ', COMPRESSION' END;	
	--SELECT @Edition, @Compression;
	SET DATEFORMAT YMD;
	set @getdate=GETDATE();
	IF @NoStats=0
		SET @StrStats=N',STATS=25';
	--   !      BackConfWeekly       .
	--  ,       BackConf.
	select @WeekDay=sputnik.info.uf_GetWeekDay(@getdate);
	select @MonthDay=DATEPART ( DAY , @getdate );
	--update: ,    Full.    Full  Diff.
	--	 : 1.  Full  Weekly. 2.  Diff  Weekly. 3. Full  Daily 4.  Diff  Daily	

	SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG 
	FROM sputnik.backups.BackConfWeekly
	WHERE Kind=@TypeBack and DBName=@DBNAME_in and MonthDay=@MonthDay;
	if @DBName is null and @TypeBack='Full' and @OnlyFull=0
		SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG  
		FROM sputnik.backups.BackConfWeekly
		WHERE Kind='Diff' and DBName=@DBNAME_in and MonthDay=@MonthDay;

	if @DBName is null
	begin
		SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG 
		FROM sputnik.backups.BackConfWeekly
		WHERE Kind=@TypeBack and DBName=@DBNAME_in and WeekDay=@WeekDay;
		if @DBName is null and @TypeBack='Full' and @OnlyFull=0
			SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG  
			FROM sputnik.backups.BackConfWeekly
			WHERE Kind='Diff' and DBName=@DBNAME_in and WeekDay=@WeekDay;
	end
	if @DBName is null
	begin
		SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG 
		FROM sputnik.backups.BackConf 
		WHERE Kind=@TypeBack and DBName=@DBNAME_in
		if @DBName is null and @TypeBack='Full' and @OnlyFull=0
			SELECT TOP 1 @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind, @FG=FG  
			FROM sputnik.backups.BackConf
			WHERE Kind='Diff' and DBName=@DBNAME_in;
	end
	--  (   !):    @OnlyFull      
	--      Full   BackConfWeekly       !
	if @DBName is null and @OnlyFull=1 and @TypeBack='Full'
	begin
		DECLARE @T TABLE (DBName NVARCHAR(300), LocalDir NVARCHAR(500), NetDir NVARCHAR(500), LocalDays INT, NetDays INT, Kind VARCHAR(4), Ord tinyint);
		INSERT INTO @T
		SELECT TOP 1 DBName, LocalDir, NetDir, LocalDays, NetDays, Kind, 1 AS Ord
		FROM sputnik.backups.BackConfWeekly
		WHERE Kind='Full' and DBName=@DBNAME_in and MonthDay BETWEEN 1 AND 31
		UNION ALL
		SELECT TOP 1 DBName, LocalDir, NetDir, LocalDays, NetDays, Kind, 2 AS Ord
		FROM sputnik.backups.BackConfWeekly
		WHERE Kind='Full' and DBName=@DBNAME_in and WeekDay BETWEEN 1 AND 7;
		SELECT @DBName=DBName, @LocalDir=LocalDir, @NetDir=NetDir, @LocalDays=LocalDays, @NetDays=NetDays, @DynTypeBack=Kind
		FROM @T
		WHERE Ord=(SELECT MIN(Ord) FROM @T);
	end		

	if @DBName is not null
	begin
		--   ,   
		SET @tcmd='md "' + @LocalDir+'"';
		if @debug=1 print 'xp_cmdshell '''+@tcmd+'';
		else	EXEC xp_cmdshell @tcmd, no_output
		IF @LocalDir<>@NetDir and @NetDir is not null and @NetDir<>''
		begin
			SET @tcmd='md "' + @NetDir+'"';
			if @debug=1 print 'xp_cmdshell '''+@tcmd+'';
			else	EXEC xp_cmdshell @tcmd, no_output
			--   @Extension  only -    NetDir     BAK
			set @Extension = '.ONLY';
		end
		else
			--   @Extension  BAK -     !
			set @Extension = '.BAK';

		IF @FG IS NOT NULL AND @FG<>'' AND @DynTypeBack IN ('Full', 'Diff')
		BEGIN
			--    !!
			--        
			--     BACKUP    
			--       ,    .
			declare @tsql_fg nvarchar(2000), @c_fg_cur nvarchar(300);
			if OBJECT_ID('tempdb.dbo.#t_fg') is not null
				drop table #t_fg;
			create table #t_fg (fg nvarchar(300));
			set @tsql_fg='USE ['+@DBName+'];
				insert into #t_fg (fg)			 
				select name as fg
				from sys.filegroups
				where charindex(QUOTENAME(name),'''+@FG+''')>0;'
			EXEC (@tsql_fg);

			declare C_fg cursor for
			select distinct fg
			from #t_fg;
			open C_fg;
			fetch next from C_fg into @c_fg_cur;
			while @@FETCH_STATUS=0
			begin
				if LEN(@str_FG)>0
					SET @str_FG=@str_FG+',';
				if LEN(@str_FG_fname)=0
					SET @str_FG_fname='FG_';
				SET @str_FG=@str_FG+' FILEGROUP = '''+@c_fg_cur+''' ';
				SET @str_FG_fname=@str_FG_fname+@c_fg_cur+'_';
				fetch next from C_fg into @c_fg_cur;
			end
			close C_fg;
			deallocate C_fg; 	  
		END

		set @backup_file=@DBName+'_'+@DynTypeBack+'_'+@str_FG_fname+REPLACE(REPLACE(SUBSTRING(CONVERT(NVARCHAR(max),@getdate,126),1,19),'T','_'),':','.');
		SET @tstr=@LocalDir+CASE WHEN RIGHT(@LocalDir,1)='\' THEN '' ELSE '\' END+@backup_file+@Extension;

		if @DynTypeBack='Full'
			if @debug=1 PRINT(N'BACKUP DATABASE ['+@DBName+'] '+@str_FG+' TO  DISK = N'''+@tstr+''' WITH FORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM, BUFFERCOUNT=64, MAXTRANSFERSIZE=2097152');
			else EXEC(N'BACKUP DATABASE ['+@DBName+'] '+@str_FG+' TO  DISK = N'''+@tstr+''' WITH FORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM, BUFFERCOUNT=64, MAXTRANSFERSIZE=2097152');
		else if @DynTypeBack='Diff'
			if @debug=1 PRINT(N'BACKUP DATABASE ['+@DBName+'] '+@str_FG+' TO  DISK = N'''+@tstr+''' WITH DIFFERENTIAL,FORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM, BUFFERCOUNT=64, MAXTRANSFERSIZE=2097152');
			else EXEC(N'BACKUP DATABASE ['+@DBName+'] '+@str_FG+' TO  DISK = N'''+@tstr+''' WITH DIFFERENTIAL,FORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM, BUFFERCOUNT=64, MAXTRANSFERSIZE=2097152');
		else if @DynTypeBack='Log'
		begin
			if @debug=1 PRINT(N'BACKUP LOG ['+@DBName+'] TO  DISK = N'''+@tstr+''' WITH NOFORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM');
			else EXEC(N'BACKUP LOG ['+@DBName+'] TO  DISK = N'''+@tstr+''' WITH NOFORMAT, INIT, SKIP, NOREWIND, NOUNLOAD'+@Compression+@StrStats+', CHECKSUM');
			/*	      !   -     !
				.    .
				--if datepart(hh,@getdate)=23		--   LOG  23  00.
				--	begin try
				--		exec db_maintenance.usp_ShrinkLogFile @DBName
				--	end try
				--	begin catch
				--		RAISERROR('    (shrinkfile)      usp_ShrinkLogs !',11,1) WITH LOG
				--	end catch
			*/
		end
		begin try
			if @debug=0 exec backups.usp_WriteBackuphistory @DBName, @FG, @DynTypeBack,@backup_file,@tstr
		end try
		begin catch
			RAISERROR('          usp_WriteBackuphistory !',11,1) WITH LOG
		end catch

		--   @ForceCopy           !
		IF @ForceCopy=1
			if @debug=0 exec backups.usp_CopyBack @DBFilter=@DBName,@type=@DynTypeBack,@DateStart=@getdate;			
	end
END

BEGIN set nocount on; IF @ForceDelete=1 BEGIN IF @ForceSetMonth=0 BEGIN IF @ForceSetWeek=0 DELETE FROM backups.BackConf WHERE DBName=@DBNAME_in AND Kind=@TypeBack ELSE DELETE FROM backups.BackConfWeekly WHERE DBName=@DBNAME_in AND Kind=@TypeBack AND ((WeekDay=@FullBackupDay AND @FullBackupDay BETWEEN 1 AND 7) OR (@FullBackupDay=0 AND WeekDay BETWEEN 1 AND 7)) END ELSE DELETE FROM backups.BackConfWeekly WHERE DBName=@DBNAME_in AND Kind=@TypeBack AND ((MonthDay=@FullBackupDay AND @FullBackupDay BETWEEN 1 AND 31) OR (@FullBackupDay=0 AND MonthDay BETWEEN 1 AND 31)) return 1; END IF @LocalDir is null OR @LocalDir='' OR @TypeBack NOT IN ('Full','Diff','Log') return 0; IF CHARINDEX('\',@LocalDir,LEN(@LocalDir)-1)=0 set @LocalDir=@LocalDir+'\'; IF CHARINDEX('\',@NetDir,LEN(@NetDir)-1)=0 set @NetDir=@NetDir+'\'; IF @DBNAME_in = '' set @DBNAME_in = NULL; IF @LocalDaysWeek IS NULL SET @LocalDaysWeek=@LocalDays; IF @NetDaysWeek IS NULL SET @NetDaysWeek=@NetDays;
IF @DBNAME_in is null set @SubDirDBName=1; IF @ForceSetWeek=0 BEGIN WITH DBs AS ( select name, @TypeBack as Kind from sys.databases where (@DBNAME_in is null or name=@DBNAME_in) and (name not in ('tempdb')) and (state=0 and is_read_only=0) and (recovery_model_desc<>'SIMPLE' or @TypeBack<>'Log') ) MERGE backups.BackConf AS target USING ( select name, Kind, case when @SubDirDBName=1 then @LocalDir+name+'\' else @LocalDir end, case when @SubDirDBName=1 and @NetDir is not null then @NetDir+name+'\' else @NetDir end, @LocalDays, @NetDays, @LocalPolicy, @NetPolicy from DBs ) AS source (DBName,Kind,LocalDir,NetDir,LocalDays,NetDays, LocalPolicy, NetPolicy) ON (target.DBName=source.DBName and target.Kind=source.Kind) WHEN NOT MATCHED THEN INSERT (DBName,LocalDir,NetDir,LocalDays,NetDays,Kind, LocalPolicy, NetPolicy) VALUES (source.DBName,source.LocalDir,source.NetDir,source.LocalDays,source.NetDays,source.Kind, source.LocalPolicy, source.NetPolicy) WHEN MATCHED AND @Replace=1 THEN UPDATE SET LocalDir=source.LocalDir, NetDir=source.NetDir, LocalDays=source.LocalDays, NetDays=source.NetDays, LocalPolicy=source.LocalPolicy, NetPolicy=source.NetPolicy; END IF (@TypeBack='Diff' AND @OnlyDiff=0) OR @ForceSetWeek=1 BEGIN WITH DBs AS ( select name, 'Full' as Kind from sys.databases where (@DBNAME_in is null or name=@DBNAME_in) and (name not in ('tempdb')) and (state=0 and is_read_only=0) ) MERGE backups.BackConfWeekly AS target USING ( select name, Kind, case when @SubDirDBName=1 then @LocalDir+name+'\' else @LocalDir end, case when @SubDirDBName=1 and @NetDir is not null then @NetDir+name+'\' else @NetDir end, @LocalDaysWeek, @NetDaysWeek, @FullBackupDay, @LocalPolicy, @NetPolicy from DBs ) AS source (DBName,Kind,LocalDir,NetDir,LocalDays,NetDays, WeekDay, LocalPolicy, NetPolicy) ON (target.DBName=source.DBName and target.Kind=source.Kind and target.WeekDay=source.WeekDay) WHEN NOT MATCHED THEN INSERT (DBName,LocalDir,NetDir,LocalDays,NetDays,Kind,WeekDay, LocalPolicy, NetPolicy) VALUES (source.DBName,source.LocalDir,source.NetDir,source.LocalDays,source.NetDays,source.Kind,source.WeekDay,source.LocalPolicy, source.NetPolicy) WHEN MATCHED AND @Replace=1 THEN UPDATE SET LocalDir=source.LocalDir, NetDir=source.NetDir, LocalDays=source.LocalDays, NetDays=source.NetDays, LocalPolicy=source.LocalPolicy, NetPolicy=source.NetPolicy;
/* Weekly Daily ! ! ! WITH DBs AS ( select name, 'Full' as Kind from sys.databases where (@DBNAME_in is null or name=@DBNAME_in) and (name not in ('tempdb','model')) and (state=0 and is_read_only=0) ) DELETE Del FROM backups.BackConf AS Del INNER JOIN DBs ON Del.DBName=DBs.name and Del.Kind=DBs.Kind */ END return 1; END

begin set nocount on; declare @dbname nvarchar(400), @DateStart datetime=null, @pp_config bit=0; DECLARE @BackupHere BIT, @DlgHandle UNIQUEIDENTIFIER, @MsgRequest XML, @count_q_pp int; declare @sql NVARCHAR(MAX), @dblist_fmt NVARCHAR(MAX); IF @DBList > N'' AND @DBList IS NOT NULL BEGIN --11.04.17 @DBList ( ); set @dblist_fmt = 'N'''+REPLACE(@DBList, @DBList_delimeter,''' , N''')+''''; set @dblist_fmt = REPLACE(@dblist_fmt,'N'''' , ',''); set @dblist_fmt = REPLACE(@dblist_fmt,' , N''''',''); END CREATE TABLE #x(DB NVARCHAR(600)); SET @sql = N'SELECT name FROM sys.databases WHERE 1=1' + CASE WHEN @dblist_fmt IS NOT NULL THEN ' AND name IN (' + @dblist_fmt + ')' ELSE '' END; INSERT #x EXEC sp_executesql @sql;
-- - ! -- - ! -- ( @OnlyCopy) IF @OnlyCopy=0 BEGIN IF @type='Log' set @DateStart=DATEADD(day,-1,GETDATE()); ELSE set @DateStart=GETDATE(); END;
IF @type<>'Full' set @ForceFULL=0;
-- ! -- , ONLINE! -- Log , <>simple declare BACKUPS scroll cursor for select Conf.dbname from ( select distinct DBName from sputnik.info.vGetAllBackConf where (@DBFilter is null or DBName=@DBFilter) and (@type is null or (Kind=@type) or (Kind in ('Full','Diff') and @type='Full')) ) Conf inner join ( select name as dbname from sys.databases where state_desc='ONLINE' and is_in_standby=0 and is_read_only=0 and name <> 'tempdb' and (@type<>'Log' OR recovery_model_desc<>'SIMPLE') ) DBFact inner join #x X ON DBFact.dbname=X.DB on Conf.dbname=DBFact.dbname
-- ! if @pp=0 begin select @pp_config=[pp] from backups.Config where [pp] is not null and DateConfig=(select max(DateConfig) from backups.Config where [pp] is not null); if @pp_config=1 -- Backups! BEGIN DIALOG CONVERSATION @DlgHandle FROM SERVICE [pp_Commander] TO SERVICE N'pp_ProcessorHard' ON CONTRACT [pp_back_Contract] WITH ENCRYPTION = OFF; end;
-- 1. ( !), open BACKUPS if @OnlyCopy=0 begin fetch next from BACKUPS into @dbname while @@FETCH_STATUS=0 begin --check: is it possible to make a backup on this server IF CAST (LEFT (CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(2)),2) AS SMALLINT) >= 11 SET @BackupHere=sys.fn_hadr_backup_is_preferred_replica(@dbname); ELSE SET @BackupHere=1; IF @BackupHere=1 BEGIN -- , -- Service Broker. if @pp=0 and @pp_config=1 begin -- XML. set @MsgRequest=cast('<backups> <db>'+@dbname+'</db> <type>'+@type+'</type> </backups>' as xml);
--31.12.2015 : ! -- , ! ;WITH XMLNAMESPACES ('http://pecom.ru/pegasExchange/types/' as ns), Q AS ( select CAST(message_body as XML) as msg_xml from pp.ProcessorQueueHard where message_type_name='pp_back_Request' ) select @count_q_pp=count(*) from Q where msg_xml.exist(N'(/backups/db/text()[.=sql:variable("@dbname")])')=1 AND msg_xml.exist(N'(/backups/type/text()[.=sql:variable("@type")])')=1; IF @count_q_pp=0 OR @count_q_pp IS NULL BEGIN -- ! SEND ON CONVERSATION @DlgHandle MESSAGE TYPE [pp_back_Request] (@MsgRequest); END end else -- backups.usp_RunBack! exec backups.usp_RunBack @DBName_IN=@dbname, @TypeBack=@type, @NoStats=@NoStats, @OnlyFull=@ForceFULL; END
fetch next from BACKUPS into @dbname end end CLOSE BACKUPS; DEALLOCATE BACKUPS;
if @pp=1 or @pp_config=0 begin -- 2. , -- usp_CopyBack
--check: is it possible to make a backup on this server IF CAST (LEFT (CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(2)),2) AS SMALLINT) >= 11 SET @BackupHere=sys.fn_hadr_backup_is_preferred_replica(@dbname); ELSE SET @BackupHere=1; IF @BackupHere=1 BEGIN -- ! exec backups.usp_CopyBack @DBFilter=@DBFilter,@type=@type, @DateStart=@DateStart; END; end; end

/* ============================================= -- Author: (sqland1c) -- Create date: 10.12.2013 (1.0) -- Description: , BackConf BackConfWeekly ! ! -- Update: 26.12.2013 (1.1) BackConf BackConfWeekly. ! ( ) ONLINE 16.01.2014 (1.2) : , . scroll, ( ). 18.01.2014 (1.22)
@OnlyCopy - 1, . . 0. 17.03.2014 (1.25) DISTINCT. 16.04.2014 (1.3) Always ON! AlwaysOn . SQL Server 2012 . 13.10.2014 (1.33) @NoStats - 1, %. usp_RunBack - ( - ). @type - Full( !) 11.11.2014 (1.40) - PowerShell usp_CopyBack. ! @OnlyCopy! 21.11.2014 (1.41) - , ! 18.01.2015 (1.50) ( Service Broker). pp backups.config 18.01.2015 (1.51) - [ProcessorQueueHard]. . - - 2. 13.07.2015 (1.52) AlwaysOn - Log . 31.12.2015 (1.55) pp! , ! 01.04.2016 (1.56) IIF CASE SQL Server < 2012! 25.08.2016 (1.57) model. 25.10.2016 (1.580) @ForceFULL - ( DIFF). , @type 'Full'. 02.11.2016 (1.585) Log-: , recovery_model=SIMPLE.
05.06.2017 (1.590) @DBList @DBList_delimeter: , @DBList_delimeter ( ). . @DBFilter - .
29.10.2018 (1.591) DIFF ( FULL): vGetAllBackConf , .
29.10.2020 (1.592) AlwaysOn - Full Diff .
23.02.2021 (1.593) Fixed check: is it possible to make a backup on this server (AlwaysOn) -- ============================================= */ CREATE proc [backups].[usp_StartBackup] @type varchar(4) = 'Full' -- Log Full Diff. ,@DBFilter nvarchar(400) = null ,@OnlyCopy bit = 0 ,@NoStats bit = 1 ,@pp bit = 0 --, ( Service Broker). ,@ForceFULL bit = 0 -- , DIFF. ,@DBList NVARCHAR(MAX) = NULL ,@DBList_delimeter NVARCHAR(10)=',' as

create proc backups.usp_WriteBackuphistory 
	@db_name nvarchar(300),
	@fg nvarchar(1000)=null,
	@backup_type varchar(4),
	@backup_file nvarchar(260),
	@backup_file_fullname nvarchar(260)
as

SET NOCOUNT ON;
DECLARE @SN nvarchar(300), @TN nvarchar(300), @CF nvarchar(300), @IT varchar(30), @I varchar(10), @sql_cmd nvarchar(1000), @top_cmd nvarchar(100)='';
DECLARE @top_cmd_plus nvarchar(100)='';
set @top_cmd_plus=' TOP (20000) ';
if @top is not null
	set @top_cmd=' TOP ('+cast(@top as varchar(40))+') ';
DECLARE Cl CURSOR FOR
SELECT SchemaName,TableName,Column_filter,interval_type,interval
FROM sputnik.config.cleanup_hs;
OPEN Cl;
FETCH NEXT FROM Cl INTO @SN, @TN, @CF, @IT, @I;
WHILE @@FETCH_STATUS=0
BEGIN
	SET @sql_cmd='IF OBJECT_ID(''['+@SN+'].['+@TN+']'') IS NOT NULL
DELETE '+CASE WHEN @TN IN ('sql_handle_collect','pfc_data','pfc_data_dyn') THEN @top_cmd_plus ELSE @top_cmd END+' FROM ['+@SN+'].['+@TN+'] WHERE ['+@CF+'] < DATEADD('+@IT+',-'+@I+',SYSDATETIME())';
	IF @only_print_command=1
		PRINT @sql_cmd;
	ELSE
		EXEC(@sql_cmd);
	FETCH NEXT FROM Cl INTO @SN, @TN, @CF, @IT, @I;
END
CLOSE Cl;
DEALLOCATE Cl;

BEGIN
	declare @Return NVARCHAR(100);
	select @Return=PolicyScan 
	from db_maintenance.TabList_US
	where Tab=@Tab AND Stat=@Stat;
	IF @Return is null
		select @Return=PolicyScan 
		from db_maintenance.TabList_US
		where Tab=@Tab AND Stat IS NULL;
	return @Return;
END

/* =============================================
-- Author:		Ivanov Andrei
-- Create date: 02.06.2016
-- Description:	This function gets the PolicyScan for Update Statistics for the specified table and statistics.
			
-- ============================================= */
CREATE FUNCTION db_maintenance.uf_CheckTabList_US
(
	@Tab NVARCHAR(1000), --Table name in []
	@Stat NVARCHAR(1000) --Stat name in []
)
RETURNS NVARCHAR(100)
AS

BEGIN
	declare @WeekDay tinyint, @getdate datetime2(2), @PeriodHours tinyint, @LastRunDate datetime2(2);
	set nocount on;
	set @getdate=GETDATE();
	select @WeekDay=sputnik.info.uf_GetWeekDay(@getdate);

	select top 1 
		@PeriodHours=[PeriodHours], 
		@LastRunDate=[LastRunDate]
	from 
		[sputnik].[db_maintenance].[FreeProcCache]
	where 
		(WeekDay is null or WeekDay=@WeekDay)

	if (@LastRunDate is null) or (datediff(hour,@LastRunDate,@getdate)>=@PeriodHours) or (@Force=1)
	begin
		DBCC FREEPROCCACHE;
		update [sputnik].[db_maintenance].[FreeProcCache]
		set [LastRunDate]=@getdate;
	end
END

begin set nocount on; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; DECLARE @tt_start datetime2(2), @StrErr NVARCHAR(MAX); declare @tt_start_usp datetime2(2), @time_elapsed_sec int, @i_cnt bigint=0; DECLARE @TSQL NVARCHAR(MAX),@obj_filter_str nvarchar(2000), @StopList_str NVARCHAR(MAX); DECLARE @AllCores_cnt smallint; set @tt_start_usp=CAST(SYSDATETIME() AS datetime2(2)); -- , . select @StopList_str=StopList_str from sputnik.db_maintenance.StopLists where UniqueName=@UniqueName_SL; select @StopList_str=COALESCE(@StopList_str,'');
-- - CPU SQL Server: select @AllCores_cnt=count(*) from sys.dm_os_schedulers where [status]='VISIBLE ONLINE';
IF OBJECT_ID('tempdb.dbo.#T_ST') IS NOT NULL DROP TABLE #T_ST; CREATE TABLE #T_ST (db NVARCHAR(2000), shm NVARCHAR(2000), obj NVARCHAR(2000), stat NVARCHAR(2000), obj_id int, stat_id int, last_upd datetime2(2), mod_count bigint, perc_change decimal(18,2), row_count bigint,DataUsed_Mb decimal(9,1));
/* */ declare @DB_current nvarchar(2000); if OBJECT_ID('tempdb..#src_ag_db') IS NOT NULL DROP TABLE #src_ag_db; CREATE TABLE #src_ag_db (DB nvarchar(2000), [db_id] int, [Role] nvarchar(800), [PartnerReplica] nvarchar(800), [PrimaryReplica] nvarchar(800), sync_state nvarchar(800), health nvarchar(800), DB_State nvarchar(800)); IF CAST (LEFT (CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(2)),2) AS SMALLINT) >= 11 -- AlwaysON AG : INSERT INTO #src_ag_db (DB, [db_id], [Role], [PartnerReplica], [PrimaryReplica], sync_state, health, DB_State) SELECT DB_NAME(ag_db.database_id) as DB, ag_db.database_id as [db_id], ISNULL(arstates.role_desc, '') AS [Role], ISNULL(AR.replica_server_name, '') as [PartnerReplica], ISNULL(agstates.primary_replica, '') AS [PrimaryReplica], ag_db.synchronization_state_desc as sync_state, ag_db.synchronization_health_desc as health, ag_db.database_state_desc as DB_State FROM sys.dm_hadr_database_replica_states ag_db LEFT JOIN sys.dm_hadr_availability_group_states as agstates on ag_db.group_id=agstates.group_id LEFT JOIN sys.dm_hadr_availability_replica_states AS arstates ON ag_db.replica_id = arstates.replica_id and ag_db.group_id=arstates.group_id LEFT JOIN sys.availability_replicas as AR ON ag_db.replica_id=AR.replica_id and ag_db.group_id=AR.group_id WHERE ag_db.is_local=1 AND ISNULL(arstates.role_desc, '') = 'SECONDARY' DECLARE DB CURSOR FOR select name as DB from sys.databases sdb left join #src_ag_db adb ON sdb.database_id=adb.[db_id] where (name = @DBName or @DBName is null or @DBName='') and state_desc='ONLINE' and database_id>4 and is_read_only=0 and (name NOT LIKE '201%(%)%' and name NOT LIKE 'S201%(%)%') and adb.db is null; OPEN DB; FETCH NEXT FROM DB INTO @DB_current; WHILE @@FETCH_STATUS=0 BEGIN SET @obj_filter_str=COALESCE(@obj_filter,''); SET @TSQL=N'USE ['+@DB_current+N']; INSERT INTO #T_ST SELECT TOP ('+CONVERT(NVARCHAR(10),@RowLimit)+N') CAST(DB_NAME() AS NVARCHAR(2000)) as [db], SCHEMA_NAME(objs.schema_id) as shm, objs.name as obj, stat.name as stat, objs.object_id as obj_id, stat.stats_id as stat_id, CAST(sp.last_updated as datetime2(2)) as last_upd, sp.modification_counter as mod_count, CAST(100 * CAST([sp].[modification_counter] AS DECIMAL(18,2)) / CAST(case when [sp].[rows]=0 then 1 else [sp].[rows] end AS DECIMAL(18,2)) AS DECIMAL(18,2)) AS [perc_change], prts.rows as row_count, prts.DataUsed_Mb FROM sys.stats stat INNER JOIN sys.objects objs ON stat.object_id=objs.object_id INNER JOIN ( SELECT p.object_id, SUM(p.row_count) AS rows, CAST(SUM(p.[used_page_count])/128.0 as numeric(9,1)) AS DataUsed_Mb FROM sys.dm_db_partition_stats p WHERE index_id IN (0,1) GROUP BY p.object_id ) prts ON objs.object_id=prts.object_id CROSS APPLY sys.dm_db_stats_properties(stat.object_id, stat.stats_id) AS sp WHERE ( (CAST(100 * CAST([sp].[modification_counter] AS DECIMAL(18,2)) / CAST(case when [sp].[rows]=0 then 1 else [sp].[rows] end AS DECIMAL(18,2)) AS DECIMAL(18,2)) >= '+CONVERT(VARCHAR(18),@filter_perc_min)+' AND ('+COALESCE(CONVERT(VARCHAR(20),@filter_perc_max),'NULL')+' IS NULL OR CAST(100 * CAST([sp].[modification_counter] AS DECIMAL(18,2)) / CAST(case when [sp].[rows]=0 then 1 else [sp].[rows] end AS DECIMAL(18,2)) AS DECIMAL(18,2)) <= '+COALESCE(CONVERT(VARCHAR(20),@filter_perc_max),'NULL')+')) OR [sp].[modification_counter]>'+COALESCE(CONVERT(VARCHAR(30),@ModCntr_max),'1000000')+' OR [sp].[modification_counter] is null ) AND ([sp].[modification_counter]>'+COALESCE(CONVERT(VARCHAR(30),@ModCntr_min),'10')+' OR [sp].[modification_counter] is null) AND (last_updated<DATEADD(HOUR,-'+CONVERT(VARCHAR(5),@filter_old_hours)+',getdate()) or last_updated is NULL) AND ('+COALESCE(CONVERT(VARCHAR(20),@filter_rows_min),'NULL')+' IS NULL OR prts.rows>='+COALESCE(CONVERT(VARCHAR(20),@filter_rows_min),'NULL')+') AND ('+COALESCE(CONVERT(VARCHAR(20),@filter_rows_max),'NULL')+' IS NULL OR prts.rows<='+COALESCE(CONVERT(VARCHAR(20),@filter_rows_max),'NULL')+') AND ('+COALESCE(CONVERT(VARCHAR(20),@filter_DataUsedMb_min),'NULL')+' IS NULL OR prts.DataUsed_Mb>='+COALESCE(CONVERT(VARCHAR(20),@filter_DataUsedMb_min),'NULL')+') AND ('+COALESCE(CONVERT(VARCHAR(20),@filter_DataUsedMb_max),'NULL')+' IS NULL OR prts.DataUsed_Mb<='+COALESCE(CONVERT(VARCHAR(20),@filter_DataUsedMb_max),'NULL')+') AND ('''+@obj_filter_str+'''='''' OR objs.name LIKE '''+@obj_filter_str+''') AND CHARINDEX(objs.name+'';'','''+@StopList_str+''')=0 ORDER BY mod_count DESC, [perc_change] DESC ;';
--PRINT(@TSQL); EXEC(@TSQL); FETCH NEXT FROM DB INTO @DB_current; END CLOSE DB; DEALLOCATE DB;
-- mod_count perc_change! -- - + % ! CREATE CLUSTERED INDEX tmp_cix_mod01 ON #T_ST ( mod_count DESC, perc_change DESC ) ;
IF @only_show=1 BEGIN IF @analyze_mode=0 SELECT TOP (@RowLimit) * FROM #T_ST ORDER BY mod_count DESC, perc_change DESC; IF @analyze_mode=1 SELECT DISTINCT [db], [shm], COUNT_BIG(*) over (partition by [db], [shm]) as cnt, SUM(mod_count) over (partition by [db], [shm]) as sum_mod_cnt, SUM(row_count) over (partition by [db], [shm]) as sum_row_count, CAST(100 * CAST(SUM(mod_count) over (partition by [db], [shm]) AS DECIMAL(19,0)) / CAST( case when SUM(row_count) over (partition by [db], [shm])=0 then 1 else SUM(row_count) over (partition by [db], [shm]) end AS DECIMAL(19,0)) AS DECIMAL(19,0)) AS [perc_change], SUM(DataUsed_Mb) over (partition by [db], [shm]) as sum_DataUsed_Mb, MIN(last_upd) over (partition by [db], [shm]) as old_upd FROM #T_ST IF @analyze_mode=2 BEGIN ;WITH cte_src01 as( SELECT COUNT_BIG(DISTINCT [db]) as db_cnt FROM #T_ST ) SELECT TOP 1 (SELECT db_cnt FROM cte_src01) as db_cnt, COUNT_BIG(*) over () as cnt, SUM(mod_count) over () as sum_mod_cnt, SUM(row_count) over () as sum_row_count, CAST(100 * CAST(SUM(mod_count) over () AS DECIMAL(19,0)) / CAST(case when SUM(row_count) over ()=0 then 1 else SUM(row_count) over () end AS DECIMAL(19,0)) AS DECIMAL(19,0)) AS [perc_change], SUM(DataUsed_Mb) over () as sum_DataUsed_Mb, MIN(last_upd) over () as old_upd FROM #T_ST END END ELSE BEGIN DECLARE @commant_text_log NVARCHAR(MAX),@HandleCmd NVARCHAR(2000), @Cmd_handle_log NVARCHAR(MAX), @UpdCmd NVARCHAR(MAX), @shm_id int, @obj_id int, @stat_id int, @db_id int, @flag_fail bit; DECLARE CST CURSOR FOR SELECT TOP(@RowLimit) N'SET DEADLOCK_PRIORITY '+CAST(@DeadLck_PR as nvarchar(5))+N'; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET LOCK_TIMEOUT '+CAST(@Lck_Timeout as nvarchar(25))+N'; USE ['+DB+N']; ' as Cmd_Handle, -- : tsql_handle_log=N'--dlck_pr='+CAST(@DeadLck_PR as nvarchar(2))+N';tr_iso_lvl=1;lck_tmt='+CAST(@Lck_Timeout as nvarchar(12))+N'; ', N'UPDATE STATISTICS '+QUOTENAME(shm)+N'.'+QUOTENAME(obj)+N' '+QUOTENAME(stat)+N' '+ COALESCE( -- : COALESCE(db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)),@policy_scan), CASE WHEN @AllCores_cnt>11 OR DataUsed_Mb<750 THEN CASE ---- : --WHEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) IS NOT NULL THEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) WHEN DataUsed_Mb<=300 THEN N'WITH FULLSCAN;' --WHEN row_count<1000000 THEN 'WITH FULLSCAN;' WHEN DataUsed_Mb<=500 THEN N'WITH SAMPLE 90 PERCENT;' --WHEN row_count<3000000 THEN 'WITH SAMPLE 90 PERCENT;' WHEN DataUsed_Mb<=1024 THEN N'WITH SAMPLE 75 PERCENT;' --WHEN row_count<5000000 THEN 'WITH SAMPLE 85 PERCENT;' WHEN DataUsed_Mb<1500 THEN N'WITH SAMPLE 50 PERCENT;' --WHEN row_count<10000000 THEN 'WITH SAMPLE 50 PERCENT;' WHEN DataUsed_Mb<2048 THEN N'WITH SAMPLE 35 PERCENT;' --WHEN row_count<15000000 THEN 'WITH SAMPLE 40 PERCENT;' WHEN DataUsed_Mb<3000 THEN N'WITH SAMPLE 24 PERCENT;' --WHEN row_count<20000000 THEN 'WITH SAMPLE 25 PERCENT;' WHEN DataUsed_Mb<5000 THEN N'WITH SAMPLE 12 PERCENT;' --WHEN row_count<30000000 THEN 'WITH SAMPLE 15 PERCENT;' WHEN DataUsed_Mb<7000 THEN N'WITH SAMPLE 9 PERCENT;' --WHEN row_count<50000000 THEN 'WITH SAMPLE 10 PERCENT;' WHEN DataUsed_Mb<10100 THEN N'WITH SAMPLE 6 PERCENT;' --WHEN row_count<75000000 THEN 'WITH SAMPLE 7 PERCENT;' WHEN DataUsed_Mb<15100 THEN N'WITH SAMPLE 3 PERCENT;' --WHEN row_count<100000000 THEN 'WITH SAMPLE 5 PERCENT;' WHEN DataUsed_Mb<20100 THEN N'WITH SAMPLE 2 PERCENT;' WHEN DataUsed_Mb<30100 THEN N'WITH SAMPLE 1 PERCENT;' --WHEN row_count<200000000 THEN 'WITH SAMPLE 3 PERCENT;' --WHEN row_count<300000000 THEN 'WITH SAMPLE 2 PERCENT;' --WHEN row_count<500000000 THEN 'WITH SAMPLE 1 PERCENT;' ELSE N'WITH SAMPLE 50000 ROWS' END WHEN (@AllCores_cnt>5 AND @AllCores_cnt<=11) OR DataUsed_Mb<1500 THEN CASE ---- : --WHEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) IS NOT NULL THEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) --WHEN row_count<500000 THEN 'WITH FULLSCAN;' --WHEN row_count<1000000 THEN 'WITH SAMPLE 80 PERCENT;' --WHEN row_count<2000000 THEN 'WITH SAMPLE 45 PERCENT;' --WHEN row_count<3000000 THEN 'WITH SAMPLE 30 PERCENT;' --WHEN row_count<5000000 THEN 'WITH SAMPLE 20 PERCENT;' --WHEN row_count<7000000 THEN 'WITH SAMPLE 13 PERCENT;' --WHEN row_count<10000000 THEN 'WITH SAMPLE 9 PERCENT;' --WHEN row_count<15000000 THEN 'WITH SAMPLE 6 PERCENT;' --WHEN row_count<20000000 THEN 'WITH SAMPLE 3 PERCENT;' --WHEN row_count<40000000 THEN 'WITH SAMPLE 2 PERCENT;' --WHEN row_count<75000000 THEN 'WITH SAMPLE 1 PERCENT;' --ELSE 'WITH SAMPLE 700000 ROWS' WHEN DataUsed_Mb<=151 THEN N'WITH FULLSCAN;' WHEN DataUsed_Mb<=300 THEN N'WITH SAMPLE 90 PERCENT;' WHEN DataUsed_Mb<=751 THEN N'WITH SAMPLE 75 PERCENT;' WHEN DataUsed_Mb<1201 THEN N'WITH SAMPLE 50 PERCENT;' WHEN DataUsed_Mb<1801 THEN N'WITH SAMPLE 35 PERCENT;' WHEN DataUsed_Mb<2048 THEN N'WITH SAMPLE 24 PERCENT;' WHEN DataUsed_Mb<3501 THEN N'WITH SAMPLE 12 PERCENT;' WHEN DataUsed_Mb<5501 THEN N'WITH SAMPLE 9 PERCENT;' WHEN DataUsed_Mb<7510 THEN N'WITH SAMPLE 6 PERCENT;' WHEN DataUsed_Mb<10100 THEN N'WITH SAMPLE 3 PERCENT;' WHEN DataUsed_Mb<15100 THEN N'WITH SAMPLE 2 PERCENT;' WHEN DataUsed_Mb<20100 THEN N'WITH SAMPLE 1 PERCENT;' ELSE N'WITH SAMPLE 25000 ROWS' END ELSE CASE ---- : --WHEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) IS NOT NULL THEN db_maintenance.uf_CheckTabList_US(QUOTENAME(obj),QUOTENAME(stat)) --WHEN row_count<200000 THEN 'WITH FULLSCAN;' --WHEN row_count<300000 THEN 'WITH SAMPLE 80 PERCENT;' --WHEN row_count<500000 THEN 'WITH SAMPLE 50 PERCENT;' --WHEN row_count<1000000 THEN 'WITH SAMPLE 30 PERCENT;' --WHEN row_count<2000000 THEN 'WITH SAMPLE 15 PERCENT;' --WHEN row_count<3000000 THEN 'WITH SAMPLE 10 PERCENT;' --WHEN row_count<4000000 THEN 'WITH SAMPLE 8 PERCENT;' --WHEN row_count<5000000 THEN 'WITH SAMPLE 6 PERCENT;' --WHEN row_count<7000000 THEN 'WITH SAMPLE 3 PERCENT;' --WHEN row_count<10000000 THEN 'WITH SAMPLE 2 PERCENT;' --ELSE 'WITH SAMPLE 300000 ROWS' WHEN DataUsed_Mb<=101 THEN N'WITH FULLSCAN;' WHEN DataUsed_Mb<=200 THEN N'WITH SAMPLE 90 PERCENT;' WHEN DataUsed_Mb<=551 THEN N'WITH SAMPLE 75 PERCENT;' WHEN DataUsed_Mb<1001 THEN N'WITH SAMPLE 50 PERCENT;' WHEN DataUsed_Mb<1501 THEN N'WITH SAMPLE 35 PERCENT;' WHEN DataUsed_Mb<1751 THEN N'WITH SAMPLE 24 PERCENT;' WHEN DataUsed_Mb<2501 THEN N'WITH SAMPLE 12 PERCENT;' WHEN DataUsed_Mb<3501 THEN N'WITH SAMPLE 9 PERCENT;' WHEN DataUsed_Mb<5510 THEN N'WITH SAMPLE 6 PERCENT;' WHEN DataUsed_Mb<7800 THEN N'WITH SAMPLE 3 PERCENT;' WHEN DataUsed_Mb<10100 THEN N'WITH SAMPLE 2 PERCENT;' WHEN DataUsed_Mb<25100 THEN N'WITH SAMPLE 1 PERCENT;' ELSE N'WITH SAMPLE 12000 ROWS' END END ) as Cmd, obj_id, stat_id, DB_ID(DB) FROM #T_ST ORDER BY mod_count DESC, perc_change DESC;
OPEN CST; FETCH NEXT FROM CST INTO @HandleCmd, @Cmd_handle_log, @UpdCmd, @obj_id, @stat_id, @db_id; WHILE @@FETCH_STATUS=0 BEGIN -- TimeOut, - HS ! IF @timeout_sec is not null AND @timeout_sec>0 BEGIN set @time_elapsed_sec=DATEDIFF(second,@tt_start_usp, CAST(SYSDATETIME() AS datetime2(2))); IF @time_elapsed_sec>@timeout_sec BEGIN set @commant_text_log=N' TimeOut usp_RecomputeStats. @TimeOut_sec='+cast(@TimeOut_sec as nvarchar(30))+N'; @time_elapsed_sec='+cast(@time_elapsed_sec as nvarchar(30)); -- : EXEC sputnik.db_maintenance.usp_WriteHS @DB_ID=@db_id, @Command_Type=101, --101-TimeOut for Update Statistics (usp_RecomputeStats) @Command_Text_1000=@commant_text_log, @tt_start=@tt_start_usp, @Status=0; --0-Success BREAK; -- . END END
set @flag_fail=0; set @StrErr=NULL; BEGIN TRY --PRINT(@HandleCmd); set @tt_start=CAST(SYSDATETIME() AS datetime2(2)); EXEC(@HandleCmd+@UpdCmd);
END TRY BEGIN CATCH set @flag_fail=1; set @StrErr=/*' [usp_RecomputeStats]! : '+*/COALESCE(ERROR_MESSAGE(),'null *Error message is not defined'); END CATCH set @commant_text_log=@Cmd_handle_log+@UpdCmd; -- : EXEC sputnik.db_maintenance.usp_WriteHS @DB_ID=@db_id, @Object_ID=@obj_id, @Index_Stat_ID=@stat_id, @Index_Stat_Type=1, --1-Statistic @Command_Type=3, --3-Update Statistics @Command_Text_1000=@commant_text_log, @tt_start=@tt_start, @Status=@flag_fail, --0-Success, 1-Fail(Error) @Error_Text_1000=@StrErr;
set @i_cnt+=1; -- ! WAITFOR DELAY @delayperiod;
FETCH NEXT FROM CST INTO @HandleCmd, @Cmd_handle_log, @UpdCmd, @obj_id, @stat_id, @db_id; END CLOSE CST; DEALLOCATE CST; END; IF @only_show=0 BEGIN set @commant_text_log=N' : usp_RecomputeStats. : '+CONVERT(NVARCHAR(10),@i_cnt)+N' . : @DBName='''+COALESCE(@DBName,'NULL')+N''',@RowCount='+CONVERT(nvarchar(20),@RowLimit); -- : EXEC sputnik.db_maintenance.usp_WriteHS @DB_ID=0, @Command_Type=201, --201-TaskCompleted for Update Statistics (usp_RecomputeStats) @Command_Text_1000=@commant_text_log, @tt_start=@tt_start_usp, @Status=0; --0-Success END; end

	/* =============================================
