	CREATE PROCEDURE db_maintenance.usp_RecomputeStats
		@DBName nvarchar(2000)=NULL,
		@UniqueName_SL nvarchar(200)=NULL,
		@RowLimit smallint=10,
		@delayperiod char(12)='00:00:00.200',
		@filter_rows_min int=NULL,
		@filter_rows_max int=NULL,
		@filter_DataUsedMb_min numeric(9,1)=NULL,
		@filter_DataUsedMb_max numeric(9,1)=NULL,
		@filter_perc_min decimal(18,2)=15.00,
		@filter_perc_max decimal(18,2)=NULL,
		@filter_old_hours tinyint=24,
		@policy_scan varchar(100)=NULL,
		@PauseMirroring bit=0,
		@DeadLck_PR smallint=0,
		@Lck_Timeout int=20000,
		@obj_filter nvarchar(2000) = null,
		@only_show bit = 0,
		@ModCntr_max bigint=10000000, -- -    ,  !
		@ModCntr_min int=10, --   .  -     - !
		@timeout_sec int = NULL,		--       .  NULL ( 0) - .
		@analyze_mode smallint = 0 --    .
	as

BEGIN SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW; SET LOCK_TIMEOUT 30000; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; -- : declare @db_name_check nvarchar(300); select @db_name_check = name from sys.databases where name = @db_name and state_desc='ONLINE' if @db_name_check is null return -1; declare @tsql Nvarchar(2400); -- ReindexData - , ! if object_id('sputnik.db_maintenance.ReindexData') IS NULL create table db_maintenance.ReindexData ( DBName nvarchar(300), SchemaName nvarchar(300), TableName nvarchar(300), IndexName nvarchar(300), TableID int, IndexID int, IndexType tinyint, SetFillFactor tinyint, TableCreateDate datetime2(2), TableModifyDate datetime2(2), PrepareDate datetime2(2), [PageCount] bigint, AVG_Fragm_percent tinyint, [~PageUsed_perc] tinyint, [~Row_cnt] bigint, [~RowSize_Kb] numeric(9,3), LastUpdateStats datetime2(2), LastCommand nvarchar(500), LastRunDate datetime2(2), ReindexCount int default 0, NotRunOnline bit default 0 ); else begin -- ( ): if not exists (select column_id from sys.columns where object_id=OBJECT_ID('sputnik.db_maintenance.ReindexData') and name='~PageUsed_perc') alter table sputnik.db_maintenance.ReindexData add [~PageUsed_perc] tinyint NULL; if not exists (select column_id from sys.columns where object_id=OBJECT_ID('sputnik.db_maintenance.ReindexData') and name='~Row_cnt') alter table sputnik.db_maintenance.ReindexData add [~Row_cnt] bigint NULL; if not exists (select column_id from sys.columns where object_id=OBJECT_ID('sputnik.db_maintenance.ReindexData') and name='~RowSize_Kb') alter table sputnik.db_maintenance.ReindexData add [~RowSize_Kb] numeric(9,3) NULL;
end -- -- #T_Source . if object_id('tempdb..#T_Source') is not null drop table #T_Source; create table #T_Source ( DBName nvarchar(300), SchemaName nvarchar(300), TableName nvarchar(300), IndexName nvarchar(300), TableID int, IndexID int, IndexType tinyint, SetFillFactor tinyint, TableCreateDate datetime2(2), TableModifyDate datetime2(2), PrepareDate datetime2(2), [PageCount] bigint, AVG_Fragm_percent tinyint, [~PageUsed_perc] tinyint, [~Row_cnt] bigint, [~RowSize_Kb] numeric(9,3), LastUpdateStats datetime2(2), LastCommand nvarchar(500), LastRunDate datetime2(2), ReindexCount int default 0, NotRunOnline bit default 0 ); set @tsql ='use '+QUOTENAME(@db_name_check)+'; SET NOCOUNT ON; SET DEADLOCK_PRIORITY LOW; SET LOCK_TIMEOUT 30000; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; insert into #T_Source (DBName, SchemaName, TableName, IndexName, TableID, IndexID, IndexType,SetFillFactor, TableCreateDate, TableModifyDate, PrepareDate, PageCount, AVG_Fragm_percent,[~PageUsed_perc],[~Row_cnt],[~RowSize_Kb], LastUpdateStats, LastCommand, LastRunDate,NotRunOnline) select '''+QUOTENAME(@db_name_check)+''' as DBName,QUOTENAME(S.name) as SchemaName, T.TableName, I.IndexName, T.object_id as TableID, I.index_id as IndexID, I.type as IndexType, I.Fill_Factor as SetFillFactor, T.create_date as TableCreateDate, T.modify_date as TableModifyDate, getdate() as PrepareDate, null as PageCount, null as AVG_Fragm_percent, NULL as [~PageUsed_perc], NULL as [~Row_cnt], NULL as [~RowSize_Kb], null as LastUpdateStats, null as LastCommand, null as LastRunDate, case when LOB.index_id is null then 0 when LOB.index_id is not null then 1 end as NotRunOnline from ( select object_id, QUOTENAME(name) as IndexName, index_id, type, fill_factor from sys.indexes where index_id>0 -- . and is_disabled=0 -- ) I inner join ( select QUOTENAME(name) as TableName, object_id, schema_id, create_date, modify_date from sys.tables ) T on I.object_id=T.object_id inner join sys.schemas S on T.schema_id=S.schema_id left join ( select object_id, index_id from ( select container_id as id from sys.allocation_units where type=2 --LOB_DATA ) lob inner join sys.partitions p on lob.id=p.partition_id ) LOB on LOB.object_id=I.object_id and LOB.index_id=I.index_id '; declare @db_id int, @flag_fail bit, @StrErr varchar(2048), @tt_start datetime2(2), @command_text_log varchar(8000); set @flag_fail=0; set @StrErr=NULL; BEGIN TRY set @tt_start=CAST(SYSDATETIME() AS datetime2(2)); exec (@tsql); -- MERGE (#T_Source) ReindexData MERGE INTO sputnik.db_maintenance.ReindexData AS target USING #T_Source AS source --(DBName, SchemaName, TableName, IndexName, IndexType,SetFillFactor, TableCreateDate, TableModifyDate, PrepareDate, PageCount, AVG_Fragm_percent, LastUpdateStats, LastCommand, LastRunDate,NotRunOnline) ON target.DBName=source.DBName AND target.SchemaName=source.SchemaName AND target.TableName=source.TableName AND target.IndexName=source.IndexName WHEN NOT MATCHED THEN INSERT (DBName, SchemaName, TableName, IndexName, TableID, IndexID, IndexType,SetFillFactor, TableCreateDate, TableModifyDate, PrepareDate, PageCount, AVG_Fragm_percent, [~PageUsed_perc],[~Row_cnt],[~RowSize_Kb], LastUpdateStats, LastCommand, LastRunDate,NotRunOnline) VALUES (source.DBName, source.SchemaName, source.TableName, source.IndexName, source.TableID, source.IndexID, source.IndexType, source.SetFillFactor, source.TableCreateDate, source.TableModifyDate, source.PrepareDate, source.PageCount, source.AVG_Fragm_percent,source.[~PageUsed_perc],source.[~Row_cnt],source.[~RowSize_Kb], source.LastUpdateStats, source.LastCommand, source.LastRunDate, source.NotRunOnline) WHEN NOT MATCHED BY source AND -- - ! (target.DBName=QUOTENAME(@db_name_check)) THEN DELETE WHEN MATCHED AND -- , , ! ( target.TableID<>source.TableID OR target.IndexID<>source.IndexID OR target.IndexType<>source.IndexType OR target.SetFillFactor<>source.SetFillFactor OR target.TableCreateDate<>source.TableCreateDate OR target.TableModifyDate<>source.TableModifyDate OR target.NotRunOnline<>source.NotRunOnline ) THEN UPDATE SET target.TableID=source.TableID, target.IndexID=source.IndexID, target.IndexType=source.IndexType, target.SetFillFactor=source.SetFillFactor, target.TableCreateDate=source.TableCreateDate, target.TableModifyDate=source.TableModifyDate, target.PrepareDate=source.PrepareDate, target.LastUpdateStats=source.LastUpdateStats, target.NotRunOnline=source.NotRunOnline ; END TRY BEGIN CATCH set @flag_fail=1; set @StrErr=/*' [usp_reindex_preparedata]! : '+*/COALESCE(ERROR_MESSAGE(),'null *Error message is not defined'); END CATCH set @db_id=DB_ID(@db_name_check); -- set @command_text_log='exec [db_maintenance].[usp_reindex_preparedata] @db_name='''+@db_name_check+''';'; EXEC sputnik.db_maintenance.usp_WriteHS @DB_ID=@db_id, @Index_Stat_Type=0, --0-Index @Command_Type=4, --4-Prepare data for ReIndex (usp_reindex_preparedata) @Command_Text_1000=@command_text_log, @tt_start=@tt_start, @Status=@flag_fail, --0-Success, 1-Fail(Error) @Error_Text_1000=@StrErr;
END

BEGIN
	SET NOCOUNT ON;
	SET DEADLOCK_PRIORITY LOW;
	SET LOCK_TIMEOUT 30000;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	DECLARE @tt_start datetime2(2), @StrErr NVARCHAR(MAX),@flag_fail bit, @db_id_check int, @obj_id int, @ind_id int, @command_type tinyint, @tsql_handle_log varchar(2000), @commant_text_log Nvarchar(MAX),@AllCores_cnt smallint, @MaxDop_set smallint;
	declare @tt_start_usp datetime2(2), @time_elapsed_sec int;
	declare @tsql_handle nvarchar (2400), @tsql nvarchar (2400), @tsqlcheck nvarchar (800), @StopList_str NVARCHAR(MAX);
	declare @MirrorState nvarchar(75);
	set @tt_start_usp=CAST(SYSDATETIME() AS datetime2(2));
	--   SQL Server. MaxDop     Enterprise:
	DECLARE @Ed VARCHAR(3)=LEFT(CAST(SERVERPROPERTY('Edition') AS VARCHAR(128)),3);
	IF @Ed='Ent'
	BEGIN
		--  -  CPU  SQL Server:
		select @AllCores_cnt=count(*) from sys.dm_os_schedulers where [status]='VISIBLE ONLINE';
		--  MAXDOP = 64 :
		IF @AllCores_cnt>64
			set @AllCores_cnt=64;
		IF @MaxDop is not null
		BEGIN
			set @MaxDop_set=@MaxDop;
			IF @AllCores_cnt<@MaxDop_set
				set @MaxDop_set=@AllCores_cnt;
		END
	END

	IF @TableFilter is not null
		set @filter_old_hours=0;
	ELSE
		set @TableFilter='';

	--   ,          .
	select @StopList_str=StopList_str from sputnik.db_maintenance.StopLists where UniqueName=@UniqueName_SL;
	select @StopList_str=COALESCE(@StopList_str,'');

	--    !
	set @tsql_handle= N'
SET DEADLOCK_PRIORITY '+CAST(@DeadLck_PR as varchar(2))+';
SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SET LOCK_TIMEOUT '+CAST(@Lck_Timeout as varchar(12))+';
	';
	--   :
	set @tsql_handle_log='--dlck_pr='+CAST(@DeadLck_PR as varchar(2))+';tr_iso_lvl=1;lck_tmt='+CAST(@Lck_Timeout as varchar(12))+';
	';
	--         ,        .
	declare @SchemaName nvarchar(2000), @TableName nvarchar(2000), @IndexName nvarchar(2000), @PageCount int, @AVG_Fragm_percent tinyint,@NotRunOnline bit;
	declare @command nvarchar(MAX), @check_set_online char(3), @PageU_prc tinyint, @i_cnt bigint=0,@i_cnt_skip bigint=0;
	--declare @T_i table (SchemaName nvarchar(300),TableName nvarchar(300),IndexName nvarchar(300), obj_id int, ind_id int, [PageCount] bigint,AVG_Fragm_percent tinyint,PageU_prc tinyint,NotRunOnline bit);
	IF OBJECT_ID('tempdb.dbo.#T_RI') IS NOT NULL
		DROP TABLE #T_RI;
	CREATE TABLE #T_RI (DB nvarchar(2000),SchemaName nvarchar(2000),TableName nvarchar(2000),IndexName nvarchar(2000), obj_id int, ind_id int, [PageCount] bigint,AVG_Fragm_percent tinyint,PageU_prc tinyint,NotRunOnline bit, qt numeric(19,6));


	/*	    */
	declare @DB_current nvarchar(2000);
	if OBJECT_ID('tempdb..#src_ag_db') IS NOT NULL
		DROP TABLE #src_ag_db;
	CREATE TABLE #src_ag_db (DB nvarchar(2000), [db_id] int, [Role] nvarchar(2000), [PartnerReplica] nvarchar(2000), [PrimaryReplica] nvarchar(2000), sync_state nvarchar(2000), health nvarchar(2000), DB_State nvarchar(2000));
	IF CAST (LEFT (CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(2)),2) AS SMALLINT) >= 11
		--    AlwaysON AG   :
		INSERT INTO #src_ag_db (DB, [db_id], [Role], [PartnerReplica], [PrimaryReplica], sync_state, health, DB_State)
			SELECT
				DB_NAME(ag_db.database_id) as DB,
				ag_db.database_id as [db_id],
				ISNULL(arstates.role_desc, '') AS [Role],
				ISNULL(AR.replica_server_name, '') as [PartnerReplica],
				ISNULL(agstates.primary_replica, '') AS [PrimaryReplica],
				ag_db.synchronization_state_desc as sync_state,
				ag_db.synchronization_health_desc as health,
				ag_db.database_state_desc as DB_State 
			FROM sys.dm_hadr_database_replica_states ag_db
			LEFT JOIN sys.dm_hadr_availability_group_states as agstates
				on ag_db.group_id=agstates.group_id	
			LEFT JOIN sys.dm_hadr_availability_replica_states AS arstates
				ON ag_db.replica_id = arstates.replica_id
					and ag_db.group_id=arstates.group_id
			LEFT JOIN sys.availability_replicas as AR
				ON ag_db.replica_id=AR.replica_id
					and ag_db.group_id=AR.group_id
			WHERE ag_db.is_local=1 
				AND ISNULL(arstates.role_desc, '') = 'SECONDARY'
	DECLARE DB CURSOR FOR
	select name as DB
	from sys.databases sdb
	left join #src_ag_db adb ON sdb.database_id=adb.[db_id]
	where 
		(name = @db_name or @db_name is null or @db_name='') 
		and state_desc='ONLINE'
		and database_id>4
		and is_read_only=0
		and (name NOT LIKE '201%(%)%' and name NOT LIKE 'S201%(%)%')
		and adb.db is null;
	OPEN DB;
	FETCH NEXT FROM DB INTO @DB_current;
	WHILE @@FETCH_STATUS=0
	BEGIN
		;with cte_src_1 as
		(
			select top (@rowlimit) 
				SchemaName,TableName,IndexName,[PageCount],AVG_Fragm_percent,NotRunOnline
				,CAST( 
						([AVG_Fragm_percent])
						+ (100-[~PageUsed_perc])
						+ (cast([PageCount] as numeric(19,6)) / case when MAX ([PageCount]) over ()=0 then 1 else MAX ([PageCount]) over () end * 10)
						+ (datediff(day,LastRunDate,[LastUpdateStats])*[AVG_Fragm_percent]*0.01) 
						- (cast([ReindexCount] as numeric(19,6)) / case when MAX (ReindexCount) over ()=0 then 1 else MAX (ReindexCount) over () end * 10)
						as numeric(19,6)) as qt,
				[TableID] as obj_id, [IndexID] as ind_id,
				[~PageUsed_perc] as PageU_prc
			from 
				[db_maintenance].[ReindexData]
			where 
				DBName=QUOTENAME(@DB_current)
				and (@TableFilter='' or TableName=QUOTENAME(@TableFilter))
				and CHARINDEX(TableName+';',@StopList_str)=0
				and ([LastUpdateStats] is not null or @fragm_tresh<0)
				and ([PageCount] is not null and (@filter_pages_min is null or [PageCount] >= @filter_pages_min))
				and ([PageCount] is not null and (@filter_pages_max is null or [PageCount] <= @filter_pages_max))
				and (
						(
							(AVG_Fragm_percent is not null and (@filter_fragm_min is null or AVG_Fragm_percent >= @filter_fragm_min))
							OR ([~PageUsed_perc]<@PageUsed_tresh AND AVG_Fragm_percent>0)
						)
						AND (AVG_Fragm_percent is not null and (@filter_fragm_max is null or AVG_Fragm_percent <= @filter_fragm_max))				
					)
				and (LastRunDate is null or (@filter_old_hours is null or DATEDIFF(HOUR,[LastRunDate],getdate()) >= @filter_old_hours))
				and (LastRunDate is null or (LastUpdateStats > LastRunDate) or @fragm_tresh<0)
			order by qt desc
		)
		insert into #T_RI (DB, SchemaName,TableName,IndexName, obj_id, ind_id, [PageCount],AVG_Fragm_percent,PageU_prc,NotRunOnline,qt)
		select
				@DB_current,SchemaName,TableName,IndexName, obj_id, ind_id, [PageCount],AVG_Fragm_percent,PageU_prc,NotRunOnline,qt
		from 
			cte_src_1;

		FETCH NEXT FROM DB INTO @DB_current;
	END
	CLOSE DB;
	DEALLOCATE DB;

	--   qt!
	--    !
	CREATE CLUSTERED INDEX tmp_cix_qc01 ON #T_RI
	(
		[qt] DESC
	) ;

	if @only_show=1
		select SchemaName,TableName,IndexName,[PageCount],AVG_Fragm_percent,NotRunOnline 
		from
		(select  top (@rowlimit) SchemaName,TableName,IndexName,[PageCount],AVG_Fragm_percent,NotRunOnline from #T_RI order by [qt] desc) t
		order by NEWID();
	else
	BEGIN

		declare C cursor for
		select DB, SchemaName,TableName,IndexName, obj_id, ind_id ,[PageCount],AVG_Fragm_percent,PageU_prc,NotRunOnline
		from
		(select  top (@rowlimit) DB, SchemaName,TableName,IndexName, obj_id, ind_id,  [PageCount],AVG_Fragm_percent, PageU_prc, NotRunOnline from #T_RI order by [qt] desc) t
		order by NEWID();
		open C
		fetch next from C into @DB_current, @SchemaName,  @TableName, @IndexName, @obj_id, @ind_id, @PageCount, @AVG_Fragm_percent,@PageU_prc,@NotRunOnline;
		while @@fetch_status=0
		begin
			-- TimeOut,    -    HS  !
			IF @timeout_sec is not null AND @timeout_sec>0
			BEGIN
				set @time_elapsed_sec=DATEDIFF(second,@tt_start_usp, CAST(SYSDATETIME() AS datetime2(2)));
				IF @time_elapsed_sec>@timeout_sec
				BEGIN
					set @commant_text_log=' TimeOut  usp_reindex_run. @TimeOut_sec='+cast(@TimeOut_sec as varchar(30))+'; @time_elapsed_sec='+cast(@time_elapsed_sec as varchar(30));
					--    :
					EXEC sputnik.db_maintenance.usp_WriteHS 
						@DB_ID=@db_id_check,
						@Command_Type=100, --100-TimeOut for Reindex (usp_reindex_run)
						@Command_Text_1000=@commant_text_log,
						@tt_start=@tt_start_usp,
						@Status=0; --0-Success
					BREAK; --   .
				END
			END

			set @db_id_check=DB_ID(@DB_current);
			select 
				@MirrorState=mirroring_state_desc
			from 
				sys.database_mirroring
			where
				mirroring_guid is not null
				and database_id=@db_id_check

			if @PauseMirroring=1 and @MirrorState in ('SYNCHRONIZED','SYNCHRONIZING')
				exec(N'alter database ['+@DB_current+'] set partner suspend');

			--  Enterprise:  @MaxDop (  ) -     !
			IF @MaxDop IS NULL AND @Ed='Ent'
			BEGIN
				select @MaxDop_set=
				CASE 
					WHEN @PageCount<640 THEN 2		--5
					WHEN @PageCount<6400 THEN 4 	--50
					WHEN @PageCount<64000 THEN 8	--500
					WHEN @PageCount<640000 THEN 16	--5
					WHEN @PageCount<6400000 THEN 32	--50
					ELSE 64
				END;
				--  CPU ,   :
				select @MaxDop_set=
				CASE 
					WHEN @AllCores_cnt<=4 AND @MaxDop_set>1 THEN 1
					WHEN @AllCores_cnt<=9 AND @MaxDop_set>2 THEN 2
					WHEN @AllCores_cnt<=14 AND @MaxDop_set>3 THEN 3
					WHEN @AllCores_cnt<=16 AND @MaxDop_set>4 THEN 4
					WHEN @AllCores_cnt<=20  AND @MaxDop_set>5 THEN 5
					ELSE @MaxDop_set
				END;
				IF @AllCores_cnt<=@MaxDop_set
					set @MaxDop_set=@AllCores_cnt-1;
			END;

			set @check_set_online=@set_online;
			IF (@check_set_online='ON' and @NotRunOnline=1 and @policy_offline=1)
			BEGIN
				--  
				set @command_type=8;
				set @commant_text_log='  , .. @NotRunOnline=1  @policy_offline=1';
				set @i_cnt_skip += 1;
			END
			ELSE BEGIN

				if @check_set_online='ON' and @NotRunOnline=1 and @policy_offline=0
					set @check_set_online='OFF';	

				--rebuild      @fragm_tresh       @PageUsed_tresh
				--    reorginize!
				if (@AVG_Fragm_percent <= @fragm_tresh AND (@PageU_prc>=@PageUsed_tresh)) OR (@NotRunOnline=1 AND @check_set_online='ON' AND @policy_offline=2)
				begin
					set @command=N'alter index '+@IndexName+N' on '+@SchemaName+N'.'+@TableName+N' reorganize ';
					set @command_type=2;
				end
				else if @AVG_Fragm_percent > @fragm_tresh OR (@PageU_prc<@PageUsed_tresh and @AVG_Fragm_percent>0)
				begin
					if @check_set_online='ON'
						set @command_type=1;
					else
						set @command_type=0;
					set @command=N'alter index '+@IndexName+N' on '+@SchemaName+N'.'+@TableName+N' rebuild with ( sort_in_tempdb = '+@set_sortintempdb+', online = '+@check_set_online+N' , data_compression = '+@set_compression+', fillfactor='+cast(@set_fillfactor as varchar(5))+CASE WHEN @Ed='Ent' THEN ', MAXDOP = '+cast(@MaxDop_set as varchar(5)) ELSE '' END+')';

				end
				set @tsql=@tsql_handle+N'
	use '+QUOTENAME(@DB_current)+';
	'			+@command;

				set @flag_fail=0;
				set @StrErr=NULL;
				BEGIN TRY
					--PRINT(@UpdCmd);
					set @tt_start=CAST(SYSDATETIME() AS datetime2(2));
					exec(@tsql);

					update [db_maintenance].[ReindexData] 
					set 
						[LastRunDate]=getdate(),
						[LastCommand]=@command,
						[ReindexCount]+=1
					where 
						DBName=QUOTENAME(@DB_current)
						AND SchemaName=@SchemaName
						AND TableName=@TableName
						AND IndexName=@IndexName;
				END TRY
				BEGIN CATCH
					set @flag_fail=1;
					set @StrErr=/*'      [usp_reindex_run]!  : '+*/COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
				END CATCH
				--      .          !
				--exec(N''use '+@DB_current+'; update statistics ''+ @SchemaName+N''.''+@TableName+N'' ''+@IndexName+N'' with fullscan'' );

				set @commant_text_log=@tsql_handle_log+@command;

				set @i_cnt+=1;

				waitfor delay @delayperiod;
			END;

		--    :
			EXEC sputnik.db_maintenance.usp_WriteHS 
				@DB_ID=@db_id_check,
				@Object_ID=@obj_id,
				@Index_Stat_ID=@ind_id,
				@Index_Stat_Type=0, --0-Index
				@Command_Type=@command_type, --0-Rebuild Index Offline, 1-Rebuild Index Online, 2-Reorginize Index,8-Skip Offline Index
				@Command_Text_1000=@commant_text_log,
				@tt_start=@tt_start,
				@Status=@flag_fail, --0-Success, 1-Fail(Error)
				@Error_Text_1000=@StrErr;
			fetch next from C into @DB_current, @SchemaName,  @TableName, @IndexName, @obj_id, @ind_id, @PageCount, @AVG_Fragm_percent,@PageU_prc,@NotRunOnline;

		end
		close C;
		deallocate C;

		select 
			@MirrorState=mirroring_state_desc
		from 
			sys.database_mirroring
		where
			mirroring_guid is not null
			and database_id=DB_ID(@DB_current)
		if @PauseMirroring=1 and @MirrorState = 'SUSPENDED'
			exec(N'alter database ['+@DB_current+'] set partner resume');

		set @commant_text_log=' : usp_reindex_run.  : '+CONVERT(VARCHAR(10),@i_cnt)+
		CASE WHEN @i_cnt_skip>0 THEN ' ; : '+CONVERT(VARCHAR(10),@i_cnt_skip)
			ELSE ''
		END
		+' . : @db_name='''+COALESCE(@db_name,'NULL')+''',@RowCount='+CONVERT(VARCHAR(10),@RowLimit);
		--    :
		EXEC sputnik.db_maintenance.usp_WriteHS 
			@DB_ID=0,
			@Command_Type=200, --200-TaskCompleted for Reindex (usp_reindex_run)
			@Command_Text_1000=@commant_text_log,
			@tt_start=@tt_start_usp,
			@Status=0; --0-Success

	END
END

	/* =============================================
	-- Author:		  (sqland1c)
	-- Create date: 24.01.2013 (1.0)
	-- Description:             
					  db_maintenance.ReindexDataFor_.

					 @db_name      .
				
					 @indexeslimit -       1 .   50.
					 @delayperiod -       00:00:00,     .   2 .
						 2  ,     SQL Server.
				
					 @filter_pages -   ,    .      ,    
						  .   128.    null,      .
					 @filter_fragm -     %.      ,       
						.   10.    null,      .
					 @filter_old_hours -     .  ,   ,       .
						  6.    null,      .
					 @fragm_tresh -     %.       (reorginize),     - 
						 (rebuild).   30.
					 @set_fillfactor -    FillFactor   .   97%.
					 @set_compression -       .    Enterprise!   NONE.
					 @set_online -   online   .    Enterprise!   OFF.						
	-- Update:		
					31.01.2014 (1.1)
					 @filter_pages,      12. 
					  @set_sortintempdb,  ON,        sort_in_tempdb	
					22.02.2014 (1.2)
					  -     LastUpdateStats  !
					25.02.2014 (1.3)
					      (IF). 
					26.02.2014 (1.4)
					     /    ReindexCount.
					    @filter_oldest -> @filter_old_hours -  ,   .   6 .
					27.02.2014 (1.5)
					    ONLINE   (   REBUILD).
						   LOB ,  ONLINE  .
					05.03.2014 (1.51)
					       READ UNCOMMITTED     .
					11.03.2014 (1.6)
					   @PauseMirroring (   PauseMirroring   ReindexConf).   
					   (  1).   0.
					15.10.2014 (1.62)
					   @TableFilter -       !     
					 (   @filter_old_hours).
					18.12.2014 (1.63)
					    =30  (  ).       .
					02.03.2015 (1.64)
					  usp_freeproccache (  )   usp_reindex_start.
					24.06.2015 (2.00) 
					   .       .
					08.07.2015 (2.01) 
					       ReindexData -   (  -    - ReindexCount).
					09.07.2015 (2.02) 
					  @only_show -       ( )
					07.11.2015 (2.05)
					     ReindexData -  .        qt.
					    , , -    ,       . 
					         !
					01.12.2015 (2.06)
					    qt.
					18.10.2016 (2.07)
					    qt -  [ReindexCount] -  ,   .
					31.03.2017 (3.000)
					 ,   ( + ),    (@PageUsed_tresh,@MaxDop,@timeout_sec,@policy_offline)
					12.04.2017 (3.002)
					   @MaxDop_set -  ""     -  CPU ( 20). 
					       -   "order by qt desc".    - "".
					26.04.2017 (3.005)
					    (Rebuild)  .      @fragm_tresh    (, -1).
					29.12.2017 (3.006)
					   .
					14.11.2018 (3.010)
					   2008 (iif   case).
	-- ============================================= */
	CREATE PROCEDURE [db_maintenance].[usp_reindex_run]
		@db_name nvarchar(2000)=NULL,
		@UniqueName_SL nvarchar(200)=NULL,
		@rowlimit smallint = 50,
		@delayperiod char(12) = '00:00:00.100',
		@filter_pages_min int = 12,
		@filter_pages_max int = null,
		@filter_fragm_min tinyint = 10,
		@filter_fragm_max tinyint = null,
		@filter_old_hours tinyint = 24,
		@fragm_tresh smallint = 30,
		@set_fillfactor tinyint = 100,
		@set_compression char(4)='NONE',
		@set_online char(3)='OFF',
		@set_sortintempdb char(3)='OFF',
		@PauseMirroring bit=0,
		@TableFilter nvarchar(2000)=null,
		@DeadLck_PR smallint=0,
		@Lck_Timeout int=20000,
		@only_show bit = 0,
		@PageUsed_tresh tinyint = 80,	--%  ,        REBUILD,   reorganize.
		@MaxDop smallint = NULL,		---  CPU       (   Enterprise!).
		@timeout_sec int = NULL,		--       .  NULL ( 0) - .
		@policy_offline tinyint = 2		--  ,  Online Rebuild : 0-Rebuild Offline,1-,2-Reorganize.
	AS

begin
	set nocount on;
	declare @getdate datetime, @gettime time,  @WeekDay tinyint;
	declare @DBName nvarchar(2000), @RowLimit smallint, @delayperiod char(12), @filter_pages_min int , @filter_pages_max int , @UniqueName_SL nvarchar(200),
	@filter_fragm_min tinyint, @filter_fragm_max tinyint, @filter_old_hours tinyint, 
	@fragm_tresh tinyint, @set_fillfactor tinyint, @set_compression char(4), @set_online char(3), @set_sortintempdb char(3), @PauseMirroring bit,
	@DeadLck_PR smallint, @Lck_Timeout int, @filter_rows_min int, @filter_rows_max int, @filter_perc_min decimal(18,2), @filter_perc_max decimal(18,2),
	@policy_scan varchar(100), @timeout_sec int;
	declare @mv_Name nvarchar(200);
	declare @filter_DataUsedMb_min numeric(9,1), @filter_DataUsedMb_max numeric(9,1);
	set @getdate=GETDATE();
	set @gettime=CAST(@getdate as TIME);
	--   !
	select @WeekDay=sputnik.info.uf_GetWeekDay(@getdate);

	--       !
	--insert into @mv(Name)
	select top 1 @mv_Name=UniqueName
	from sputnik.db_maintenance.mw as mw
	where 
			(@getdate >= mw.DateOpen OR mw.DateOpen IS NULL)
		  and (@getdate <= mw.DateClose OR mw.DateClose IS NULL)
		  and (
			(
				(@gettime >= case when mw.TimeOpen<mw.TimeClose then mw.TimeOpen else '00:00:00' end OR mw.TimeOpen IS NULL)
				 and (@gettime <= mw.TimeClose OR mw.TimeClose IS NULL)
			) 
			OR	
			(	(@gettime >= mw.TimeOpen OR mw.TimeOpen IS NULL)
				and (@gettime <= case when mw.TimeOpen<mw.TimeClose then mw.TimeClose else '23:59:59.999' end OR mw.TimeClose IS NULL)
			)
		  )
		  and (CHARINDEX(CAST(@WeekDay as CHAR(1)),mw.WeekDays/*,CAST(@WeekDay as CHAR(1))*/)>0 OR mw.WeekDays IS NULL)
		  /*and UniqueName IN (select UniqueName_MW from sputnik.db_maintenance.ReindexConf)*/
	order by case when TimeClose<TimeOpen then DATEDIFF(minute, TimeOpen, '23:59:59')+1+DATEDIFF(minute, '00:00:00', TimeClose) else DATEDIFF (minute, TimeOpen,TimeClose) end;
	--select @mv_Name as MW_Name;

	if @StartRecomputeStats=0
	BEGIN
		--      !
		--    ,    ONLINE,     !
		declare INDEXs cursor
		for
			select Conf.*
			from
			(
				select [DBName]
						--,[UniqueName_MW]
						,[UniqueName_SL]
						,[RowLimit]
						,[delayperiod]
						,[filter_pages_min]
						,[filter_pages_max]
						,[filter_fragm_min]
						,[filter_fragm_max]
						,[filter_old_hours]
						,[fragm_tresh]
						,[set_fillfactor]
						,[set_compression]
						,[set_online]
						,[set_sortintempdb]
						,[PauseMirroring]
						,[DeadLck_PR]
						,[Lck_Timeout]
						,[timeout_sec]
				from sputnik.db_maintenance.ReindexConf
				where
					(@DBFilter is null or DBName=@DBFilter)
					and (UniqueName_MW = @mv_Name ) 
			) Conf
			inner join
			(
				select name as dbname
				from sys.databases
				where 
				state_desc='ONLINE' and is_in_standby=0 and is_read_only=0
				and name not in ('master','msdb','model','tempdb')
				union all 
				select NULL as dbname
			) DBFact
			on Conf.dbname=DBFact.dbname OR (DBFact.dbname IS NULL and Conf.DBName IS NULL)
	END
	else 
	BEGIN
		--       !
		--    ,    ONLINE,     !
		declare ReStats cursor
		for
			select Conf.*
			from
			(
				select  [DBName]
					  --,[UniqueName_MW]
					  ,[UniqueName_SL]
					  ,[RowLimit]
					  ,[delayperiod]
					  ,[filter_rows_min]
					  ,[filter_rows_max]
					  ,filter_DataUsedMb_min , filter_DataUsedMb_max 
					  ,[filter_perc_min]
					  ,[filter_perc_max]
					  ,[filter_old_hours]
					  ,[policy_scan]
					  ,[PauseMirroring]
					  ,[DeadLck_PR]
					  ,[Lck_Timeout]
					  ,[timeout_sec]
				from sputnik.db_maintenance.RecomputeStatsConf
				where
					(@DBFilter is null or DBName=@DBFilter)
					and (UniqueName_MW = @mv_Name ) 
			) Conf
			inner join
			(
				select name as dbname
				from sys.databases
				where 
				state_desc='ONLINE' and is_in_standby=0 and is_read_only=0
				and name not in ('master','msdb','model','tempdb')
				union all 
				select NULL as dbname
			) DBFact
			on Conf.dbname=DBFact.dbname OR (DBFact.dbname IS NULL and Conf.DBName IS NULL)
	END

	if @StartRecomputeStats=0 --        .
	BEGIN
		open INDEXs
		fetch next from INDEXs
			into @DBName, @UniqueName_SL, @RowLimit, @delayperiod, @filter_pages_min, @filter_pages_max, @filter_fragm_min, @filter_fragm_max,
				@filter_old_hours, @fragm_tresh, @set_fillfactor, @set_compression, @set_online, @set_sortintempdb, @PauseMirroring, @DeadLck_PR,
				@Lck_Timeout,@timeout_sec;
		while @@FETCH_STATUS=0
		begin
			if @StartUpdateStats=0
				-- !
				EXEC db_maintenance.usp_reindex_run 
					@db_name=@DBName, 
					@UniqueName_SL=@UniqueName_SL,
					@RowLimit=@RowLimit, 
					@delayperiod=@delayperiod, 
					@filter_pages_min=@filter_pages_min,
					@filter_pages_max=@filter_pages_max, 
					@filter_fragm_min=@filter_fragm_min,
					@filter_fragm_max=@filter_fragm_max,
					@filter_old_hours=@filter_old_hours, 
					@fragm_tresh=@fragm_tresh, 
					@set_fillfactor=@set_fillfactor, 
					@set_compression=@set_compression, 
					@set_online=@set_online, 
					@set_sortintempdb=@set_sortintempdb,
					@PauseMirroring=@PauseMirroring,
					@TableFilter=@TableFilter,
					@DeadLck_PR=@DeadLck_PR,
					@Lck_Timeout=@Lck_Timeout,
					@only_show=@only_show,
					@timeout_sec=@timeout_sec;
			else
			BEGIN
				--  ()    !
				-- @oldupdhours=3 - ,     ,   3 .
				--@rowlimit_max=1000 -  1    1000    .

				-- 
				--select @DBName as DBName, @rowlimit as rowlimit, @delayperiod as DelayPeriod,
				--	   3 as OldUpdhours, @TableFilter as TableFilter;
				exec db_maintenance.usp_reindex_updatestats 
					@db_name=@DBName, 
					@rowlimit=@rowlimit,
					@delayperiod=@delayperiod,
					@oldupdhours=3,
					@TableFilter=@TableFilter,
					@rowlimit_max=3000;
			END
			fetch next from INDEXs
			into @DBName, @UniqueName_SL, @RowLimit, @delayperiod, @filter_pages_min, @filter_pages_max, @filter_fragm_min, @filter_fragm_max,
				@filter_old_hours, @fragm_tresh, @set_fillfactor, @set_compression, @set_online, @set_sortintempdb, @PauseMirroring, @DeadLck_PR,
				@Lck_Timeout,@timeout_sec;
		end;
		CLOSE INDEXs;
		DEALLOCATE INDEXs;
	END
	else if @StartRecomputeStats=1
	BEGIN
	--         
	--     -    !
		open ReStats
		fetch next from ReStats
			into @DBName, @UniqueName_SL, @RowLimit, @delayperiod, @filter_rows_min, @filter_rows_max, @filter_DataUsedMb_min, @filter_DataUsedMb_max,
			 @filter_perc_min, @filter_perc_max, @filter_old_hours, @policy_scan, @PauseMirroring, @DeadLck_PR, @Lck_Timeout,@timeout_sec;
		while @@FETCH_STATUS=0
		begin
			exec db_maintenance.usp_RecomputeStats
				@DBName=@DBName, 
				@UniqueName_SL=@UniqueName_SL,
				@RowLimit=@RowLimit,
				@delayperiod=@delayperiod,
				@filter_rows_min=@filter_rows_min,
				@filter_rows_max=@filter_rows_max,
				@filter_DataUsedMb_min=@filter_DataUsedMb_min,
				@filter_DataUsedMb_max=@filter_DataUsedMb_max,
				@filter_perc_min=@filter_perc_min,
				@filter_perc_max=@filter_perc_max,
				@filter_old_hours=@filter_old_hours,
				@policy_scan=@policy_scan,
				@PauseMirroring=@PauseMirroring,
				@DeadLck_PR=@DeadLck_PR,
				@Lck_Timeout=@Lck_Timeout,
				@only_show=@only_show,
				@timeout_sec=@timeout_sec;

			fetch next from ReStats
			into @DBName, @UniqueName_SL, @RowLimit, @delayperiod, @filter_rows_min, @filter_rows_max, @filter_DataUsedMb_min, @filter_DataUsedMb_max,
			 @filter_perc_min, @filter_perc_max, @filter_old_hours, @policy_scan, @PauseMirroring, @DeadLck_PR, @Lck_Timeout,@timeout_sec;
		end
		CLOSE ReStats;
		DEALLOCATE ReStats;
	END
end

BEGIN
	declare @tt_start_proc datetime2(2);
	set @tt_start_proc=CAST(SYSDATETIME() AS datetime2(2));
	SET NOCOUNT ON;
	SET DEADLOCK_PRIORITY LOW;
	SET LOCK_TIMEOUT 30000;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
	declare @tsql nvarchar (2400),@tsqlcheck nvarchar (600);
	----    :
	--declare @DB_current nvarchar(300);
	--select @DB_current = name from sys.databases where name = @db_name and state_desc='ONLINE'
	--if @DB_current is null
	--	return -1;

	if @TableFilter is not null
		set @oldupdhours=0;
	else
		set @TableFilter='';

	/*	    */
	declare @DB_current nvarchar(300);
	if OBJECT_ID('tempdb..#src_ag_db') IS NOT NULL
		DROP TABLE #src_ag_db;
	CREATE TABLE #src_ag_db (DB nvarchar(800), [db_id] int, [Role] nvarchar(800), [PartnerReplica] nvarchar(800), [PrimaryReplica] nvarchar(800), sync_state nvarchar(800), health nvarchar(800), DB_State nvarchar(800));
	IF CAST (LEFT (CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(2)),2) AS SMALLINT) >= 11
		--    AlwaysON AG   :
		INSERT INTO #src_ag_db (DB, [db_id], [Role], [PartnerReplica], [PrimaryReplica], sync_state, health, DB_State)
			SELECT
				DB_NAME(ag_db.database_id) as DB,
				ag_db.database_id as [db_id],
				ISNULL(arstates.role_desc, '') AS [Role],
				ISNULL(AR.replica_server_name, '') as [PartnerReplica],
				ISNULL(agstates.primary_replica, '') AS [PrimaryReplica],
				ag_db.synchronization_state_desc as sync_state,
				ag_db.synchronization_health_desc as health,
				ag_db.database_state_desc as DB_State 
			FROM sys.dm_hadr_database_replica_states ag_db
			LEFT JOIN sys.dm_hadr_availability_group_states as agstates
				on ag_db.group_id=agstates.group_id	
			LEFT JOIN sys.dm_hadr_availability_replica_states AS arstates
				ON ag_db.replica_id = arstates.replica_id
					and ag_db.group_id=arstates.group_id
			LEFT JOIN sys.availability_replicas as AR
				ON ag_db.replica_id=AR.replica_id
					and ag_db.group_id=AR.group_id
			WHERE ag_db.is_local=1 
				AND ISNULL(arstates.role_desc, '') = 'SECONDARY'
	DECLARE DB CURSOR FOR
	select name as DB
	from sys.databases sdb
	left join #src_ag_db adb ON sdb.database_id=adb.[db_id]
	where 
		(name = @db_name or @db_name is null or @db_name='') 
		and state_desc='ONLINE'
		and database_id>4
		and is_read_only=0
		and (name NOT LIKE '201%(%)%' and name NOT LIKE 'S201%(%)%')
		and adb.db is null;
	OPEN DB;
	FETCH NEXT FROM DB INTO @DB_current;
	WHILE @@FETCH_STATUS=0
	BEGIN

		--           usp_reindex_preparedata	 			
		--     ,      MERGE!
		exec sputnik.db_maintenance.usp_reindex_preparedata @DB_current;

		declare @db_id int, @flag_fail bit, @StrErr varchar(2048), @tt_start datetime2(2), @command_text_log varchar(8000);
		--      : avg_fragmentation_in_percent  page_count.
		declare @IndexID int, @TableID int, @AvgFrag tinyint, @PageCount bigint, @i_limit int=1,@Avg_Page_Used tinyint,@Row_cnt bigint,@RowSize_Kb numeric(9,3);
		set @db_id=DB_ID(@DB_current);

		declare C cursor for
		select TOP (@rowlimit_max) IndexID,TableID
		from   [db_maintenance].[ReindexData]
		where
			DBName=QUOTENAME(@DB_current)
			and (@TableFilter = '' or TableName= QUOTENAME(@TableFilter))
			and ([LastUpdateStats] is null or [LastRunDate]>[LastUpdateStats]
				or DATEDIFF(HOUR,[LastUpdateStats],getdate())>=@oldupdhours)
		order by [LastUpdateStats] ASC;
		open C
		fetch next from C into @IndexID, @TableID
		while @@fetch_status=0
		begin
			set @flag_fail=0;
			set @StrErr=NULL;
			BEGIN TRY

				set @tt_start=CAST(SYSDATETIME() AS datetime2(2));

				select 			
					@AvgFrag=cast (ROUND(max(avg_fragmentation_in_percent),0) as tinyint),
					@Avg_Page_Used=cast (ROUND(min(avg_page_space_used_in_percent),0) as tinyint),
					@PageCount=sum(page_count),
					@Row_cnt=sum(record_count),
					@RowSize_Kb=cast (max(avg_record_size_in_bytes)/1024.00 as numeric(9,3))
				from sys.dm_db_index_physical_stats (@db_id, @TableID, @IndexID,NULL,'SAMPLED');

				--select @AvgFrag,@Avg_Page_Used,@PageCount,@Row_cnt,@RowSize_Kb
				--select @DB_current,@TableID,@IndexID;
				--select [AVG_Fragm_percent],[PageCount],[~PageUsed_perc],[~Row_cnt],[~RowSize_Kb],[LastUpdateStats]
				--from [db_maintenance].[ReindexData] 
				--where DBName=@DB_current
				--	and TableID=@TableID
				--	and IndexID=@IndexID; 

				update [db_maintenance].[ReindexData] 
				set [AVG_Fragm_percent]=@AvgFrag,
					[PageCount]=@PageCount,
					[~PageUsed_perc]=@Avg_Page_Used,
					[~Row_cnt]=@Row_cnt,
					[~RowSize_Kb]=@RowSize_Kb,
					[LastUpdateStats]=getdate()
				where 
					DBName=QUOTENAME(@DB_current)
					and TableID=@TableID
					and IndexID=@IndexID;
			END TRY
			BEGIN CATCH
				--     ( !):
				set @flag_fail=1;
				set @StrErr=/*'        [usp_reindex_updatestats]!  : '+*/COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
				set @command_text_log='--dlck_pr=-5;tr_iso_lvl=1;lck_tmt=30000;
		select @AvgFrag=cast(MAX(avg_fragmentation_in_percent) as tinyint), @PageCount=sum(page_count)
		from sys.dm_db_index_physical_stats ('+CAST(@db_id as varchar(100))+','+CAST(@TableID as varchar(100))+','+CAST(@IndexID as varchar(100))+',default,default);
		--update table [db_maintenance].[ReindexData]';
				EXEC sputnik.db_maintenance.usp_WriteHS 
					@DB_ID=@db_id,
					@Object_ID=@TableID,
					@Index_Stat_ID=@IndexID,
					@Index_Stat_Type=0, --0-Index
					@Command_Type=5, --5-Update data for ReIndex (usp_reindex_updatestats)
					@Command_Text_1000=@command_text_log,
					@tt_start=@tt_start,
					@Status=@flag_fail, --0-Success, 1-Fail(Error)
					@Error_Text_1000=@StrErr;
			END CATCH

			fetch next from C into @IndexID, @TableID;

			if @i_limit>=@rowlimit
			begin
				waitfor delay @delayperiod;
				set @i_limit=1;
			end
			else
				set @i_limit+=1;		
		end
		close C
		deallocate C;

		--          :
		set @command_text_log='exec [db_maintenance].[usp_reindex_updatestats] @db_name='''+@DB_current+''', @rowlimit='+CAST(@rowlimit as varchar(100))+',@delayperiod='''+@delayperiod+''',@oldupdhours='+CAST(@oldupdhours as varchar(100))+',@TableFilter='+CASE WHEN @TableFilter='' THEN 'NULL' ELSE ''''+@TableFilter+'''' END+',@rowlimit_max='+CAST(@rowlimit_max as varchar(100))+';';
		EXEC sputnik.db_maintenance.usp_WriteHS 
			@DB_ID=@db_id,
			@Index_Stat_Type=0, --0-Index
			@Command_Type=5, --5-Update data for ReIndex (usp_reindex_updatestats)
			@Command_Text_1000=@command_text_log,
			@tt_start=@tt_start_proc,
			@Status=0, --0-Success, 1-Fail(Error)
			@Error_Text_1000=NULL;

		FETCH NEXT FROM DB INTO @DB_current;
	END
	CLOSE DB;
	DEALLOCATE DB;
END

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 15.01.2014 (1.0)
-- Description:            
				    dm_db_index_physical_stats 
				 @db_name      .
				 @rowlimit -       1 .   500.
				 @countexec -   ()    .   50 .
				 @delayperiod -       00:00:00,     .   06 .
				 3  ,     SQL Server.
				 @oldupdhours -   ,        !   6 .
-- Update:		15.01.2014 (1.1)
				     .    ,   .
				24.01.2014 (1.15)
				    .
				24.01.2014 (1.20)
				     default  DETAILED.
				30.01.2014 (1.4)
				             !			
				25.02.2014 (1.5)
				     -    .
				26.02.2014 (1.55)
				  @oldupdate  @oldupdhours -       ,    .    6 .
				27.02.2014 (1.56)
				     @oldupdhours   6  3 .
				05.03.2014 (1.57)
				       READ UNCOMMITTED     .
				    @tsql   2400.
				06.03.2014 (1.58)
				      @delayperiod  6  2 .
				15.10.2014 (1.6)
				   @TableFilter -         .
				    @oldupdhours  !
				24.06.2015 (2.0)
				       .     
				  usp_reindex_preparedata.       ReindexData.
				18.08.2016 (2.01)    @rowlimit_max -  1   
				 - .
				14.12.2016 (2.020)
				        HS.
-- ============================================= */
CREATE PROCEDURE db_maintenance.usp_reindex_updatestats
	@db_name nvarchar(300) = NULL,
	@rowlimit int = 50,
	@delayperiod char(12) = '00:00:00:500',
	@oldupdhours tinyint = 3,
	@TableFilter nvarchar(300)=null,
	@rowlimit_max bigint = 1000000
AS

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 10.12.2013 (1.0)
-- Description:    !     @db_name 
				   shrinkfile    
				().
				  @SetSizeMb -       (  1 )
				  @FileGrowthMb -    ,   ,     .
				  @Disk-        (  ,   )!
-- Update:		17.12.2013 (1.1) -   .   try...catch   ,   exec.
									      ,        SQL Server.
									         !
				17.12.2013 (1.2) -    .        dm_io_virtual_file_stats
								       .
				25.04.2014 (1.3) -   @SetMaxSizeGB -    Limit    .    .
									       1  ( 3).	
				28.04.2014 (1.31) -      !
				30.04.2014 (1.35) -       .        
									   -    TempDB.
				09.06.2014 (1.4)  -     .  @AutoShrink (  0),    
									      !      ,   @SetSizeMb  !
				19.02.2015 (1.41) -   .  @AutoShrink   . 
									    .
				01.03.2016 (1.42) -    @truncateonly -  ,       TRUNCATEONLY - 
									      ,      .
									 target_size   -  @SetSizeMb  @AutoShrink !
-- ============================================= */
CREATE PROCEDURE [db_maintenance].[usp_ShrinkDB]
	@db_name nvarchar(50),
	@SetSizeMb int = 1024,
	@FileGrowthMb int = null,
	@Disk char(1) = null,
	@SetMaxSizeGB int = NULL,
	@AutoShrink BIT = 0,
	@truncateonly BIT = 0
AS

/* ============================================= -- Author: (sqland1c) -- Create date: 29.11.2013 (1.0) -- Description: ! @db_name shrinkfile ( , ). @SetSizeMb - @FileGrowthMb - . @truncate - Log . ! -- Update: 17.03.2014 (1.1) . @SetSizeMb 23 . 25.04.2014 (1.12)
@FileGrowthMb=64 ( 4), @SetSizeMb - 10. 15.04.2014 (1.14) : 512 @FileGrowthMb ( ) 3072 @SetSizeMb ( ). 18.03.2016 (1.20) ! ! CHECKPOINT !
, . . -- ============================================= */ CREATE PROCEDURE db_maintenance.usp_ShrinkLogFile @db_name nvarchar(50) = null, @SetSizeMb int = null, @FileGrowthMb int = null, @truncate bit=null AS

begin
	set nocount on;
	DECLARE @StrErr VARCHAR(2048);
	if @tt_end is null
		set @tt_end=CAST(SYSDATETIME() as datetime2(2));
	if LEN(@Command_Text_1000)>1000
		set @Command_Text_1000=LEFT(@Command_Text_1000,1000);
	if @Error_Text_1000 is not null AND LEN(@Error_Text_1000)>1000
		set @Error_Text_1000=LEFT(@Error_Text_1000,1000);

	BEGIN TRY
		insert into sputnik.db_maintenance.HS ([DB_ID],[Object_ID],[Index_Stat_ID],[Index_Stat_Type],[Command_Type],[Command_Text_1000],[tt_start],[tt_end],[Status],[Error_Text_1000])
		select COALESCE(@DB_ID,0), @Object_ID, @Index_Stat_ID, @Index_Stat_Type, @Command_Type, @Command_Text_1000, @tt_start, @tt_end, @Status, @Error_Text_1000 ;
	END TRY
	BEGIN CATCH
		SET @StrErr=N'An error occurred while writing to the sputnik.db_maintenance.HS table via the [usp_WriteHS] procedure! Error text: '+COALESCE(ERROR_MESSAGE(),'null *Error message is not defined');
		--print @StrErr;
		--RAISERROR(@StrErr,11,1) WITH LOG;
		EXEC xp_logevent 55000, @StrErr, ERROR; 
	END CATCH

end

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 19.10.2016 (1.0)
-- Description: The procedure is used for logging all database maintenance operations (writing to the HS table).
-- Update:		
				21.10.2016 (1.005) Fixed bug number, now 55000 (50000 cannot be used for custom bugs).
				27.12.2017 (1.010) Instead of NULL for DB_ID, 0 will be written to the history table.
-- ============================================= */
CREATE PROCEDURE db_maintenance.usp_WriteHS
	@DB_ID int,
	@Object_ID int=null,
	@Index_Stat_ID int=null,
	@Index_Stat_Type bit=null,
	@Command_Type tinyint,
	@Command_Text_1000 varchar(8000),
	@tt_start datetime2(2),
	@tt_end datetime2(2)=null,
	@Status bit,
	@Error_Text_1000 varchar(8000)=null
as

BEGIN; SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET QUOTED_IDENTIFIER ON; SET ANSI_PADDING ON; SET CONCAT_NULL_YIELDS_NULL ON; SET ANSI_WARNINGS ON; SET NUMERIC_ROUNDABORT OFF; SET ARITHABORT ON;
IF @filter IS NULL OR @filter_type IS NULL OR @not_filter IS NULL OR @not_filter_type IS NULL OR @show_own_spid IS NULL OR @show_system_spids IS NULL OR @show_sleeping_spids IS NULL OR @get_full_inner_text IS NULL OR @get_plans IS NULL OR @get_outer_command IS NULL OR @get_transaction_info IS NULL OR @get_task_info IS NULL OR @get_locks IS NULL OR @get_avg_time IS NULL OR @get_additional_info IS NULL OR @find_block_leaders IS NULL OR @delta_interval IS NULL OR @format_output IS NULL OR @output_column_list IS NULL OR @sort_order IS NULL OR @return_schema IS NULL OR @destination_table IS NULL OR @help IS NULL BEGIN; RAISERROR('Input parameters cannot be NULL', 16, 1); RETURN; END;
IF @filter_type NOT IN ('session', 'program', 'database', 'login', 'host') BEGIN; RAISERROR('Valid filter types are: session, program, database, login, host', 16, 1); RETURN; END;
IF @filter_type = 'session' AND @filter LIKE '%[^0123456789]%' BEGIN; RAISERROR('Session filters must be valid integers', 16, 1); RETURN; END;
IF @not_filter_type NOT IN ('session', 'program', 'database', 'login', 'host') BEGIN; RAISERROR('Valid filter types are: session, program, database, login, host', 16, 1); RETURN; END;
IF @not_filter_type = 'session' AND @not_filter LIKE '%[^0123456789]%' BEGIN; RAISERROR('Session filters must be valid integers', 16, 1); RETURN; END;
IF @show_sleeping_spids NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @show_sleeping_spids are: 0, 1, or 2', 16, 1); RETURN; END;
IF @get_plans NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @get_plans are: 0, 1, or 2', 16, 1); RETURN; END;
IF @get_task_info NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @get_task_info are: 0, 1, or 2', 16, 1); RETURN; END;
IF @format_output NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @format_output are: 0, 1, or 2', 16, 1); RETURN; END;
IF @help = 1 BEGIN; DECLARE @header VARCHAR(MAX), @params VARCHAR(MAX), @outputs VARCHAR(MAX);
SELECT @header = REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('/' + REPLICATE('*', 93), t.text) + 94, CHARINDEX(REPLICATE('*', 93) + '/', t.text) - (CHARINDEX('/' + REPLICATE('*', 93), t.text) + 94) ) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ), @params = CHAR(13) + REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('--~', t.text) + 5, CHARINDEX('--~', t.text, CHARINDEX('--~', t.text) + 5) - (CHARINDEX('--~', t.text) + 5) ) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ), @outputs = CHAR(13) + REPLACE ( REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32, CHARINDEX('*/', t.text, CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32) - (CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32) ) ), CHAR(9), CHAR(255) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ) + CHAR(13) FROM sys.dm_exec_requests AS r CROSS APPLY sys.dm_exec_sql_text(r.sql_handle) AS t WHERE r.session_id = @@SPID;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@header) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ) SELECT RTRIM(LTRIM( SUBSTRING ( @header, number + 1, CHARINDEX(CHAR(13), @header, number + 1) - number - 1 ) )) AS [------header---------------------------------------------------------------------------------------------------------------] FROM numbers WHERE SUBSTRING(@header, number, 1) = CHAR(13);
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@params) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT RTRIM(LTRIM( SUBSTRING ( @params, number + 1, CHARINDEX(CHAR(13), @params, number + 1) - number - 1 ) )) AS token, number, CASE WHEN SUBSTRING(@params, number + 1, 1) = CHAR(13) THEN number ELSE COALESCE(NULLIF(CHARINDEX(',' + CHAR(13) + CHAR(13), @params, number), 0), LEN(@params)) END AS param_group, ROW_NUMBER() OVER ( PARTITION BY CHARINDEX(',' + CHAR(13) + CHAR(13), @params, number), SUBSTRING(@params, number+1, 1) ORDER BY number ) AS group_order FROM numbers WHERE SUBSTRING(@params, number, 1) = CHAR(13) ), parsed_tokens AS ( SELECT MIN ( CASE WHEN token LIKE '@%' THEN token ELSE NULL END ) AS parameter, MIN ( CASE WHEN token LIKE '--%' THEN RIGHT(token, LEN(token) - 2) ELSE NULL END ) AS description, param_group, group_order FROM tokens WHERE NOT ( token = '' AND group_order > 1 ) GROUP BY param_group, group_order ) SELECT CASE WHEN description IS NULL AND parameter IS NULL THEN '-------------------------------------------------------------------------' WHEN param_group = MAX(param_group) OVER() THEN parameter ELSE COALESCE(LEFT(parameter, LEN(parameter) - 1), '') END AS [------parameter----------------------------------------------------------], CASE WHEN description IS NULL AND parameter IS NULL THEN '----------------------------------------------------------------------------------------------------------------------' ELSE COALESCE(description, '') END AS [------description-----------------------------------------------------------------------------------------------------] FROM parsed_tokens ORDER BY param_group, group_order;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@outputs) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT RTRIM(LTRIM( SUBSTRING ( @outputs, number + 1, CASE WHEN COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) < COALESCE(NULLIF(CHARINDEX(CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2, @outputs, number + 1), 0), LEN(@outputs)) THEN COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) - number - 1 ELSE COALESCE(NULLIF(CHARINDEX(CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2, @outputs, number + 1), 0), LEN(@outputs)) - number - 1 END ) )) AS token, number, COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) AS output_group, ROW_NUMBER() OVER ( PARTITION BY COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) ORDER BY number ) AS output_group_order FROM numbers WHERE SUBSTRING(@outputs, number, 10) = CHAR(13) + 'Formatted' OR SUBSTRING(@outputs, number, 2) = CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2 ), output_tokens AS ( SELECT *, CASE output_group_order WHEN 2 THEN MAX(CASE output_group_order WHEN 1 THEN token ELSE NULL END) OVER (PARTITION BY output_group) ELSE '' END COLLATE Latin1_General_Bin2 AS column_info FROM tokens ) SELECT CASE output_group_order WHEN 1 THEN '-----------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+1, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)) ELSE SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)-1) END ELSE '' END AS formatted_column_name, CASE output_group_order WHEN 1 THEN '-----------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN SUBSTRING(column_info, CHARINDEX(']', column_info)+2, LEN(column_info)) ELSE SUBSTRING(column_info, CHARINDEX(']', column_info)+2, CHARINDEX('Non-Formatted:', column_info, CHARINDEX(']', column_info)+2) - CHARINDEX(']', column_info)-3) END ELSE '' END AS formatted_column_type, CASE output_group_order WHEN 1 THEN '---------------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN '' ELSE CASE WHEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, 1) = '<' THEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, CHARINDEX('>', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))) ELSE SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))) END END ELSE '' END AS unformatted_column_name, CASE output_group_order WHEN 1 THEN '---------------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN '' ELSE CASE WHEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, 1) = '<' THEN '' ELSE SUBSTRING(column_info, CHARINDEX(']', column_info, CHARINDEX('Non-Formatted:', column_info))+2, CHARINDEX('Non-Formatted:', column_info, CHARINDEX(']', column_info)+2) - CHARINDEX(']', column_info)-3) END END ELSE '' END AS unformatted_column_type, CASE output_group_order WHEN 1 THEN '----------------------------------------------------------------------------------------------------------------------' ELSE REPLACE(token, CHAR(255) COLLATE Latin1_General_Bin2, '') END AS [------description-----------------------------------------------------------------------------------------------------] FROM output_tokens WHERE NOT ( output_group_order = 1 AND output_group = LEN(@outputs) ) ORDER BY output_group, CASE output_group_order WHEN 1 THEN 99 ELSE output_group_order END;
RETURN; END;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@output_column_list)) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT '|[' + SUBSTRING ( @output_column_list, number + 1, CHARINDEX(']', @output_column_list, number) - number - 1 ) + '|]' AS token, number FROM numbers WHERE SUBSTRING(@output_column_list, number, 1) = '[' ), ordered_columns AS ( SELECT x.column_name, ROW_NUMBER() OVER ( PARTITION BY x.column_name ORDER BY tokens.number, x.default_order ) AS r, ROW_NUMBER() OVER ( ORDER BY tokens.number, x.default_order ) AS s FROM tokens JOIN ( SELECT '[session_id]' AS column_name, 1 AS default_order UNION ALL SELECT '[dd hh:mm:ss.mss]', 2 WHERE @format_output IN (1, 2) UNION ALL SELECT '[dd hh:mm:ss.mss (avg)]', 3 WHERE @format_output IN (1, 2) AND @get_avg_time = 1 UNION ALL SELECT '[avg_elapsed_time]', 4 WHERE @format_output = 0 AND @get_avg_time = 1 UNION ALL SELECT '[physical_io]', 5 WHERE @get_task_info = 2 UNION ALL SELECT '[reads]', 6 UNION ALL SELECT '[physical_reads]', 7 UNION ALL SELECT '[writes]', 8 UNION ALL SELECT '[tempdb_allocations]', 9 UNION ALL SELECT '[tempdb_current]', 10 UNION ALL SELECT '[CPU]', 11 UNION ALL SELECT '[context_switches]', 12 WHERE @get_task_info = 2 UNION ALL SELECT '[used_memory]', 13 UNION ALL SELECT '[physical_io_delta]', 14 WHERE @delta_interval > 0 AND @get_task_info = 2 UNION ALL SELECT '[reads_delta]', 15 WHERE @delta_interval > 0 UNION ALL SELECT '[physical_reads_delta]', 16 WHERE @delta_interval > 0 UNION ALL SELECT '[writes_delta]', 17 WHERE @delta_interval > 0 UNION ALL SELECT '[tempdb_allocations_delta]', 18 WHERE @delta_interval > 0 UNION ALL SELECT '[tempdb_current_delta]', 19 WHERE @delta_interval > 0 UNION ALL SELECT '[CPU_delta]', 20 WHERE @delta_interval > 0 UNION ALL SELECT '[context_switches_delta]', 21 WHERE @delta_interval > 0 AND @get_task_info = 2 UNION ALL SELECT '[used_memory_delta]', 22 WHERE @delta_interval > 0 UNION ALL SELECT '[tasks]', 23 WHERE @get_task_info = 2 UNION ALL SELECT '[status]', 24 UNION ALL SELECT '[wait_info]', 25 WHERE @get_task_info > 0 OR @find_block_leaders = 1 UNION ALL SELECT '[locks]', 26 WHERE @get_locks = 1 UNION ALL SELECT '[tran_start_time]', 27 WHERE @get_transaction_info = 1 UNION ALL SELECT '[tran_log_writes]', 28 WHERE @get_transaction_info = 1 UNION ALL SELECT '[open_tran_count]', 29 UNION ALL SELECT '[sql_command]', 30 WHERE @get_outer_command = 1 UNION ALL SELECT '[sql_text]', 31 UNION ALL SELECT '[query_plan]', 32 WHERE @get_plans >= 1 UNION ALL SELECT '[blocking_session_id]', 33 WHERE @get_task_info > 0 OR @find_block_leaders = 1 UNION ALL SELECT '[blocked_session_count]', 34 WHERE @find_block_leaders = 1 UNION ALL SELECT '[percent_complete]', 35 UNION ALL SELECT '[host_name]', 36 UNION ALL SELECT '[login_name]', 37 UNION ALL SELECT '[database_name]', 38 UNION ALL SELECT '[program_name]', 39 UNION ALL SELECT '[additional_info]', 40 WHERE @get_additional_info = 1 UNION ALL SELECT '[start_time]', 41 UNION ALL SELECT '[login_time]', 42 UNION ALL SELECT '[request_id]', 43 UNION ALL SELECT '[collection_time]', 44 ) AS x ON x.column_name LIKE token ESCAPE '|' ) SELECT @output_column_list = STUFF ( ( SELECT ',' + column_name as [text()] FROM ordered_columns WHERE r = 1 ORDER BY s FOR XML PATH('') ), 1, 1, '' );
IF COALESCE(RTRIM(@output_column_list), '') = '' BEGIN; RAISERROR('No valid column matches found in @output_column_list or no columns remain due to selected options.', 16, 1); RETURN; END;
IF @destination_table <> '' BEGIN; SET @destination_table = --database COALESCE(QUOTENAME(PARSENAME(@destination_table, 3)) + '.', '') + --schema COALESCE(QUOTENAME(PARSENAME(@destination_table, 2)) + '.', '') + --table COALESCE(QUOTENAME(PARSENAME(@destination_table, 1)), '');
IF COALESCE(RTRIM(@destination_table), '') = '' BEGIN; RAISERROR('Destination table not properly formatted.', 16, 1); RETURN; END; END;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@sort_order)) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT '|[' + SUBSTRING ( @sort_order, number + 1, CHARINDEX(']', @sort_order, number) - number - 1 ) + '|]' AS token, SUBSTRING ( @sort_order, CHARINDEX(']', @sort_order, number) + 1, COALESCE(NULLIF(CHARINDEX('[', @sort_order, CHARINDEX(']', @sort_order, number)), 0), LEN(@sort_order)) - CHARINDEX(']', @sort_order, number) ) AS next_chunk, number FROM numbers WHERE SUBSTRING(@sort_order, number, 1) = '[' ), ordered_columns AS ( SELECT x.column_name + CASE WHEN tokens.next_chunk LIKE '%asc%' THEN ' ASC' WHEN tokens.next_chunk LIKE '%desc%' THEN ' DESC' ELSE '' END AS column_name, ROW_NUMBER() OVER ( PARTITION BY x.column_name ORDER BY tokens.number ) AS r, tokens.number FROM tokens JOIN ( SELECT '[session_id]' AS column_name UNION ALL SELECT '[physical_io]' UNION ALL SELECT '[reads]' UNION ALL SELECT '[physical_reads]' UNION ALL SELECT '[writes]' UNION ALL SELECT '[tempdb_allocations]' UNION ALL SELECT '[tempdb_current]' UNION ALL SELECT '[CPU]' UNION ALL SELECT '[context_switches]' UNION ALL SELECT '[used_memory]' UNION ALL SELECT '[physical_io_delta]' UNION ALL SELECT '[reads_delta]' UNION ALL SELECT '[physical_reads_delta]' UNION ALL SELECT '[writes_delta]' UNION ALL SELECT '[tempdb_allocations_delta]' UNION ALL SELECT '[tempdb_current_delta]' UNION ALL SELECT '[CPU_delta]' UNION ALL SELECT '[context_switches_delta]' UNION ALL SELECT '[used_memory_delta]' UNION ALL SELECT '[tasks]' UNION ALL SELECT '[tran_start_time]' UNION ALL SELECT '[open_tran_count]' UNION ALL SELECT '[blocking_session_id]' UNION ALL SELECT '[blocked_session_count]' UNION ALL SELECT '[percent_complete]' UNION ALL SELECT '[host_name]' UNION ALL SELECT '[login_name]' UNION ALL SELECT '[database_name]' UNION ALL SELECT '[start_time]' UNION ALL SELECT '[login_time]' UNION ALL SELECT '[program_name]' ) AS x ON x.column_name LIKE token ESCAPE '|' ) SELECT @sort_order = COALESCE(z.sort_order, '') FROM ( SELECT STUFF ( ( SELECT ',' + column_name as [text()] FROM ordered_columns WHERE r = 1 ORDER BY number FOR XML PATH('') ), 1, 1, '' ) AS sort_order ) AS z;
CREATE TABLE #sessions ( recursion SMALLINT NOT NULL, session_id SMALLINT NOT NULL, request_id INT NOT NULL, session_number INT NOT NULL, elapsed_time INT NOT NULL, avg_elapsed_time INT NULL, physical_io BIGINT NULL, reads BIGINT NULL, physical_reads BIGINT NULL, writes BIGINT NULL, tempdb_allocations BIGINT NULL, tempdb_current BIGINT NULL, CPU INT NULL, thread_CPU_snapshot BIGINT NULL, context_switches BIGINT NULL, used_memory BIGINT NOT NULL, tasks SMALLINT NULL, status VARCHAR(30) NOT NULL, wait_info NVARCHAR(4000) NULL, locks XML NULL, transaction_id BIGINT NULL, tran_start_time DATETIME NULL, tran_log_writes NVARCHAR(4000) NULL, open_tran_count SMALLINT NULL, sql_command XML NULL, sql_handle VARBINARY(64) NULL, statement_start_offset INT NULL, statement_end_offset INT NULL, sql_text XML NULL, plan_handle VARBINARY(64) NULL, query_plan XML NULL, blocking_session_id SMALLINT NULL, blocked_session_count SMALLINT NULL, percent_complete REAL NULL, host_name sysname NULL, login_name sysname NOT NULL, database_name sysname NULL, program_name sysname NULL, additional_info XML NULL, start_time DATETIME NOT NULL, login_time DATETIME NULL, last_request_start_time DATETIME NULL, PRIMARY KEY CLUSTERED (session_id, request_id, recursion) WITH (IGNORE_DUP_KEY = ON), UNIQUE NONCLUSTERED (transaction_id, session_id, request_id, recursion) WITH (IGNORE_DUP_KEY = ON) );
IF @return_schema = 0 BEGIN; --Disable unnecessary autostats on the table CREATE STATISTICS s_session_id ON #sessions (session_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_id ON #sessions (request_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_transaction_id ON #sessions (transaction_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_session_number ON #sessions (session_number) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_status ON #sessions (status) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_start_time ON #sessions (start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_last_request_start_time ON #sessions (last_request_start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_recursion ON #sessions (recursion) WITH SAMPLE 0 ROWS, NORECOMPUTE;
DECLARE @recursion SMALLINT; SET @recursion = CASE @delta_interval WHEN 0 THEN 1 ELSE -1 END;
DECLARE @first_collection_ms_ticks BIGINT; DECLARE @last_collection_start DATETIME; DECLARE @sys_info BIT; SET @sys_info = ISNULL(CONVERT(BIT, SIGN(OBJECT_ID('sys.dm_os_sys_info'))), 0);
--Used for the delta pull REDO:;
IF @get_locks = 1 AND @recursion = 1 AND @output_column_list LIKE '%|[locks|]%' ESCAPE '|' BEGIN; SELECT y.resource_type, y.database_name, y.object_id, y.file_id, y.page_type, y.hobt_id, y.allocation_unit_id, y.index_id, y.schema_id, y.principal_id, y.request_mode, y.request_status, y.session_id, y.resource_description, y.request_count, s.request_id, s.start_time, CONVERT(sysname, NULL) AS object_name, CONVERT(sysname, NULL) AS index_name, CONVERT(sysname, NULL) AS schema_name, CONVERT(sysname, NULL) AS principal_name, CONVERT(NVARCHAR(2048), NULL) AS query_error INTO #locks FROM ( SELECT sp.spid AS session_id, CASE sp.status WHEN 'sleeping' THEN CONVERT(INT, 0) ELSE sp.request_id END AS request_id, CASE sp.status WHEN 'sleeping' THEN sp.last_batch ELSE COALESCE(req.start_time, sp.last_batch) END AS start_time, sp.dbid FROM sys.sysprocesses AS sp OUTER APPLY ( SELECT TOP(1) CASE WHEN ( sp.hostprocess > '' OR r.total_elapsed_time < 0 ) THEN r.start_time ELSE DATEADD ( ms, 1000 * (DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())) / 500) - DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())), DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE()) ) END AS start_time FROM sys.dm_exec_requests AS r WHERE r.session_id = sp.spid AND r.request_id = sp.request_id ) AS req WHERE --Process inclusive filter 1 = CASE WHEN @filter <> '' THEN CASE @filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @filter) = 0 OR sp.spid = CONVERT(SMALLINT, @filter) THEN 1 ELSE 0 END WHEN 'program' THEN CASE WHEN sp.program_name LIKE @filter THEN 1 ELSE 0 END WHEN 'login' THEN CASE WHEN sp.loginame LIKE @filter THEN 1 ELSE 0 END WHEN 'host' THEN CASE WHEN sp.hostname LIKE @filter THEN 1 ELSE 0 END WHEN 'database' THEN CASE WHEN DB_NAME(sp.dbid) LIKE @filter THEN 1 ELSE 0 END ELSE 0 END ELSE 1 END --Process exclusive filter AND 0 = CASE WHEN @not_filter <> '' THEN CASE @not_filter_type WHEN 'session' THEN CASE WHEN sp.spid = CONVERT(SMALLINT, @not_filter) THEN 1 ELSE 0 END WHEN 'program' THEN CASE WHEN sp.program_name LIKE @not_filter THEN 1 ELSE 0 END WHEN 'login' THEN CASE WHEN sp.loginame LIKE @not_filter THEN 1 ELSE 0 END WHEN 'host' THEN CASE WHEN sp.hostname LIKE @not_filter THEN 1 ELSE 0 END WHEN 'database' THEN CASE WHEN DB_NAME(sp.dbid) LIKE @not_filter THEN 1 ELSE 0 END ELSE 0 END ELSE 0 END AND ( @show_own_spid = 1 OR sp.spid <> @@SPID ) AND ( @show_system_spids = 1 OR sp.hostprocess > '' ) AND sp.ecid = 0 ) AS s INNER HASH JOIN ( SELECT x.resource_type, x.database_name, x.object_id, x.file_id, CASE WHEN x.page_no = 1 OR x.page_no % 8088 = 0 THEN 'PFS' WHEN x.page_no = 2 OR x.page_no % 511232 = 0 THEN 'GAM' WHEN x.page_no = 3 OR (x.page_no - 1) % 511232 = 0 THEN 'SGAM' WHEN x.page_no = 6 OR (x.page_no - 6) % 511232 = 0 THEN 'DCM' WHEN x.page_no = 7 OR (x.page_no - 7) % 511232 = 0 THEN 'BCM' WHEN x.page_no IS NOT NULL THEN '*' ELSE NULL END AS page_type, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id, x.request_mode, x.request_status, x.session_id, x.request_id, CASE WHEN COALESCE(x.object_id, x.file_id, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id) IS NULL THEN NULLIF(resource_description, '') ELSE NULL END AS resource_description, COUNT(*) AS request_count FROM ( SELECT tl.resource_type + CASE WHEN tl.resource_subtype = '' THEN '' ELSE '.' + tl.resource_subtype END AS resource_type, COALESCE(DB_NAME(tl.resource_database_id), N'(null)') AS database_name, CONVERT ( INT, CASE WHEN tl.resource_type = 'OBJECT' THEN tl.resource_associated_entity_id WHEN tl.resource_description LIKE '%object_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('object_id = ', tl.resource_description) + 12), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('object_id = ', tl.resource_description) + 12), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('object_id = ', tl.resource_description) + 12) ) ) ELSE NULL END ) AS object_id, CONVERT ( INT, CASE WHEN tl.resource_type = 'FILE' THEN CONVERT(INT, tl.resource_description) WHEN tl.resource_type IN ('PAGE', 'EXTENT', 'RID') THEN LEFT(tl.resource_description, CHARINDEX(':', tl.resource_description)-1) ELSE NULL END ) AS file_id, CONVERT ( INT, CASE WHEN tl.resource_type IN ('PAGE', 'EXTENT', 'RID') THEN SUBSTRING ( tl.resource_description, CHARINDEX(':', tl.resource_description) + 1, COALESCE ( NULLIF ( CHARINDEX(':', tl.resource_description, CHARINDEX(':', tl.resource_description) + 1), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX(':', tl.resource_description) + 1) ) ELSE NULL END ) AS page_no, CASE WHEN tl.resource_type IN ('PAGE', 'KEY', 'RID', 'HOBT') THEN tl.resource_associated_entity_id ELSE NULL END AS hobt_id, CASE WHEN tl.resource_type = 'ALLOCATION_UNIT' THEN tl.resource_associated_entity_id ELSE NULL END AS allocation_unit_id, CONVERT ( INT, CASE WHEN /*TODO: Deal with server principals*/ tl.resource_subtype <> 'SERVER_PRINCIPAL' AND tl.resource_description LIKE '%index_id or stats_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23) ) ) ELSE NULL END ) AS index_id, CONVERT ( INT, CASE WHEN tl.resource_description LIKE '%schema_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('schema_id = ', tl.resource_description) + 12), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('schema_id = ', tl.resource_description) + 12), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('schema_id = ', tl.resource_description) + 12) ) ) ELSE NULL END ) AS schema_id, CONVERT ( INT, CASE WHEN tl.resource_description LIKE '%principal_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('principal_id = ', tl.resource_description) + 15), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('principal_id = ', tl.resource_description) + 15), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('principal_id = ', tl.resource_description) + 15) ) ) ELSE NULL END ) AS principal_id, tl.request_mode, tl.request_status, tl.request_session_id AS session_id, tl.request_request_id AS request_id,
/*TODO: Applocks, other resource_descriptions*/ RTRIM(tl.resource_description) AS resource_description, tl.resource_associated_entity_id /*********************************************/ FROM ( SELECT request_session_id, CONVERT(VARCHAR(120), resource_type) COLLATE Latin1_General_Bin2 AS resource_type, CONVERT(VARCHAR(120), resource_subtype) COLLATE Latin1_General_Bin2 AS resource_subtype, resource_database_id, CONVERT(VARCHAR(512), resource_description) COLLATE Latin1_General_Bin2 AS resource_description, resource_associated_entity_id, CONVERT(VARCHAR(120), request_mode) COLLATE Latin1_General_Bin2 AS request_mode, CONVERT(VARCHAR(120), request_status) COLLATE Latin1_General_Bin2 AS request_status, request_request_id FROM sys.dm_tran_locks ) AS tl ) AS x GROUP BY x.resource_type, x.database_name, x.object_id, x.file_id, CASE WHEN x.page_no = 1 OR x.page_no % 8088 = 0 THEN 'PFS' WHEN x.page_no = 2 OR x.page_no % 511232 = 0 THEN 'GAM' WHEN x.page_no = 3 OR (x.page_no - 1) % 511232 = 0 THEN 'SGAM' WHEN x.page_no = 6 OR (x.page_no - 6) % 511232 = 0 THEN 'DCM' WHEN x.page_no = 7 OR (x.page_no - 7) % 511232 = 0 THEN 'BCM' WHEN x.page_no IS NOT NULL THEN '*' ELSE NULL END, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id, x.request_mode, x.request_status, x.session_id, x.request_id, CASE WHEN COALESCE(x.object_id, x.file_id, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id) IS NULL THEN NULLIF(resource_description, '') ELSE NULL END ) AS y ON y.session_id = s.session_id AND y.request_id = s.request_id OPTION (HASH GROUP);
--Disable unnecessary autostats on the table CREATE STATISTICS s_database_name ON #locks (database_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_object_id ON #locks (object_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_hobt_id ON #locks (hobt_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_allocation_unit_id ON #locks (allocation_unit_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_index_id ON #locks (index_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_schema_id ON #locks (schema_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_principal_id ON #locks (principal_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_id ON #locks (request_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_start_time ON #locks (start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_resource_type ON #locks (resource_type) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_object_name ON #locks (object_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_schema_name ON #locks (schema_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_page_type ON #locks (page_type) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_mode ON #locks (request_mode) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_status ON #locks (request_status) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_resource_description ON #locks (resource_description) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_index_name ON #locks (index_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_principal_name ON #locks (principal_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; END;
DECLARE @sql VARCHAR(MAX), @sql_n NVARCHAR(MAX);
SET @sql = CONVERT(VARCHAR(MAX), '') + 'DECLARE @blocker BIT; SET @blocker = 0; DECLARE @i INT; SET @i = 2147483647;
DECLARE @sessions TABLE ( session_id SMALLINT NOT NULL, request_id INT NOT NULL, login_time DATETIME, last_request_end_time DATETIME, status VARCHAR(30), statement_start_offset INT, statement_end_offset INT, sql_handle BINARY(20), host_name NVARCHAR(128), login_name NVARCHAR(128), program_name NVARCHAR(128), database_id SMALLINT, memory_usage INT, open_tran_count SMALLINT, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'wait_type NVARCHAR(32), wait_resource NVARCHAR(256), wait_time BIGINT, ' ELSE '' END + 'blocked SMALLINT, is_user_process BIT, cmd VARCHAR(32), PRIMARY KEY CLUSTERED (session_id, request_id) WITH (IGNORE_DUP_KEY = ON) );
DECLARE @blockers TABLE ( session_id INT NOT NULL PRIMARY KEY WITH (IGNORE_DUP_KEY = ON) );
BLOCKERS:;
INSERT @sessions ( session_id, request_id, login_time, last_request_end_time, status, statement_start_offset, statement_end_offset, sql_handle, host_name, login_name, program_name, database_id, memory_usage, open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'wait_type, wait_resource, wait_time, ' ELSE '' END + 'blocked, is_user_process, cmd ) SELECT TOP(@i) spy.session_id, spy.request_id, spy.login_time, spy.last_request_end_time, spy.status, spy.statement_start_offset, spy.statement_end_offset, spy.sql_handle, spy.host_name, spy.login_name, spy.program_name, spy.database_id, spy.memory_usage, spy.open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'spy.wait_type, CASE WHEN spy.wait_type LIKE N''PAGE%LATCH_%'' OR spy.wait_type = N''CXPACKET'' OR spy.wait_type LIKE N''LATCH[_]%'' OR spy.wait_type = N''OLEDB'' THEN spy.wait_resource ELSE NULL END AS wait_resource, spy.wait_time, ' ELSE '' END + 'spy.blocked, spy.is_user_process, spy.cmd FROM ( SELECT TOP(@i) spx.*, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'ROW_NUMBER() OVER ( PARTITION BY spx.session_id, spx.request_id ORDER BY CASE WHEN spx.wait_type LIKE N''LCK[_]%'' THEN 1 ELSE 99 END, spx.wait_time DESC, spx.blocked DESC ) AS r ' ELSE '1 AS r ' END + 'FROM ( SELECT TOP(@i) sp0.session_id, sp0.request_id, sp0.login_time, sp0.last_request_end_time, LOWER(sp0.status) AS status, CASE WHEN sp0.cmd = ''CREATE INDEX'' THEN 0 ELSE sp0.stmt_start END AS statement_start_offset, CASE WHEN sp0.cmd = N''CREATE INDEX'' THEN -1 ELSE COALESCE(NULLIF(sp0.stmt_end, 0), -1) END AS statement_end_offset, sp0.sql_handle, sp0.host_name, sp0.login_name, sp0.program_name, sp0.database_id, sp0.memory_usage, sp0.open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'CASE WHEN sp0.wait_time > 0 AND sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_type ELSE NULL END AS wait_type, CASE WHEN sp0.wait_time > 0 AND sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_resource ELSE NULL END AS wait_resource, CASE WHEN sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_time ELSE 0 END AS wait_time, ' ELSE '' END + 'sp0.blocked, sp0.is_user_process, sp0.cmd FROM ( SELECT TOP(@i) sp1.session_id, sp1.request_id, sp1.login_time, sp1.last_request_end_time, sp1.status, sp1.cmd, sp1.stmt_start, sp1.stmt_end, MAX(NULLIF(sp1.sql_handle, 0x00)) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS sql_handle, sp1.host_name, MAX(sp1.login_name) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS login_name, sp1.program_name, sp1.database_id, MAX(sp1.memory_usage) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS memory_usage, MAX(sp1.open_tran_count) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS open_tran_count, sp1.wait_type, sp1.wait_resource, sp1.wait_time, sp1.blocked, sp1.hostprocess, sp1.is_user_process FROM ( SELECT TOP(@i) sp2.spid AS session_id, CASE sp2.status WHEN ''sleeping'' THEN CONVERT(INT, 0) ELSE sp2.request_id END AS request_id, MAX(sp2.login_time) AS login_time, MAX(sp2.last_batch) AS last_request_end_time, MAX(CONVERT(VARCHAR(30), RTRIM(sp2.status)) COLLATE Latin1_General_Bin2) AS status, MAX(CONVERT(VARCHAR(32), RTRIM(sp2.cmd)) COLLATE Latin1_General_Bin2) AS cmd, MAX(sp2.stmt_start) AS stmt_start, MAX(sp2.stmt_end) AS stmt_end, MAX(sp2.sql_handle) AS sql_handle, MAX(CONVERT(sysname, RTRIM(sp2.hostname)) COLLATE SQL_Latin1_General_CP1_CI_AS) AS host_name, MAX(CONVERT(sysname, RTRIM(sp2.loginame)) COLLATE SQL_Latin1_General_CP1_CI_AS) AS login_name, MAX ( CASE WHEN blk.queue_id IS NOT NULL THEN N''Service Broker database_id: '' + CONVERT(NVARCHAR, blk.database_id) + N'' queue_id: '' + CONVERT(NVARCHAR, blk.queue_id) ELSE CONVERT ( sysname, RTRIM(sp2.program_name) ) END COLLATE SQL_Latin1_General_CP1_CI_AS ) AS program_name, MAX(sp2.dbid) AS database_id, MAX(sp2.memusage) AS memory_usage, MAX(sp2.open_tran) AS open_tran_count, RTRIM(sp2.lastwaittype) AS wait_type, RTRIM(sp2.waitresource) AS wait_resource, MAX(sp2.waittime) AS wait_time, COALESCE(NULLIF(sp2.blocked, sp2.spid), 0) AS blocked, MAX ( CASE WHEN blk.session_id = sp2.spid THEN ''blocker'' ELSE RTRIM(sp2.hostprocess) END ) AS hostprocess, CONVERT ( BIT, MAX ( CASE WHEN sp2.hostprocess > '''' THEN 1 ELSE 0 END ) ) AS is_user_process FROM ( SELECT TOP(@i) session_id, CONVERT(INT, NULL) AS queue_id, CONVERT(INT, NULL) AS database_id FROM @blockers
UNION ALL
SELECT TOP(@i) CONVERT(SMALLINT, 0), CONVERT(INT, NULL) AS queue_id, CONVERT(INT, NULL) AS database_id WHERE @blocker = 0
UNION ALL
SELECT TOP(@i) CONVERT(SMALLINT, spid), queue_id, database_id FROM sys.dm_broker_activated_tasks WHERE @blocker = 0 ) AS blk INNER JOIN sys.sysprocesses AS sp2 ON sp2.spid = blk.session_id OR ( blk.session_id = 0 AND @blocker = 0 ) ' + CASE WHEN ( @get_task_info = 0 AND @find_block_leaders = 0 ) THEN 'WHERE sp2.ecid = 0 ' ELSE '' END + 'GROUP BY sp2.spid, CASE sp2.status WHEN ''sleeping'' THEN CONVERT(INT, 0) ELSE sp2.request_id END, RTRIM(sp2.lastwaittype), RTRIM(sp2.waitresource), COALESCE(NULLIF(sp2.blocked, sp2.spid), 0) ) AS sp1 ) AS sp0 WHERE @blocker = 1 OR (1=1 ' + --inclusive filter CASE WHEN @filter <> '' THEN CASE @filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @filter) <> 0 THEN 'AND sp0.session_id = CONVERT(SMALLINT, @filter) ' ELSE '' END WHEN 'program' THEN 'AND sp0.program_name LIKE @filter ' WHEN 'login' THEN 'AND sp0.login_name LIKE @filter ' WHEN 'host' THEN 'AND sp0.host_name LIKE @filter ' WHEN 'database' THEN 'AND DB_NAME(sp0.database_id) LIKE @filter ' ELSE '' END ELSE '' END + --exclusive filter CASE WHEN @not_filter <> '' THEN CASE @not_filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @not_filter) <> 0 THEN 'AND sp0.session_id <> CONVERT(SMALLINT, @not_filter) ' ELSE '' END WHEN 'program' THEN 'AND sp0.program_name NOT LIKE @not_filter ' WHEN 'login' THEN 'AND sp0.login_name NOT LIKE @not_filter ' WHEN 'host' THEN 'AND sp0.host_name NOT LIKE @not_filter ' WHEN 'database' THEN 'AND DB_NAME(sp0.database_id) NOT LIKE @not_filter ' ELSE '' END ELSE '' END + CASE @show_own_spid WHEN 1 THEN '' ELSE 'AND sp0.session_id <> @@spid ' END + CASE WHEN @show_system_spids = 0 THEN 'AND sp0.hostprocess > '''' ' ELSE '' END + CASE @show_sleeping_spids WHEN 0 THEN 'AND sp0.status <> ''sleeping'' ' WHEN 1 THEN 'AND ( sp0.status <> ''sleeping'' OR sp0.open_tran_count > 0 ) ' ELSE '' END + ') ) AS spx ) AS spy WHERE spy.r = 1; ' + CASE @recursion WHEN 1 THEN 'IF @@ROWCOUNT > 0 BEGIN; INSERT @blockers ( session_id ) SELECT TOP(@i) blocked FROM @sessions WHERE NULLIF(blocked, 0) IS NOT NULL
EXCEPT
SELECT TOP(@i) session_id FROM @sessions; ' +
CASE WHEN ( @get_task_info > 0 OR @find_block_leaders = 1 ) THEN 'IF @@ROWCOUNT > 0 BEGIN; SET @blocker = 1; GOTO BLOCKERS; END; ' ELSE '' END + 'END; ' ELSE '' END + 'SELECT TOP(@i) @recursion AS recursion, x.session_id, x.request_id, DENSE_RANK() OVER ( ORDER BY x.session_id ) AS session_number, ' + CASE WHEN @output_column_list LIKE '%|[dd hh:mm:ss.mss|]%' ESCAPE '|' THEN 'x.elapsed_time ' ELSE '0 ' END + 'AS elapsed_time, ' + CASE WHEN ( @output_column_list LIKE '%|[dd hh:mm:ss.mss (avg)|]%' ESCAPE '|' OR @output_column_list LIKE '%|[avg_elapsed_time|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.avg_elapsed_time / 1000 ' ELSE 'NULL ' END + 'AS avg_elapsed_time, ' + CASE WHEN @output_column_list LIKE '%|[physical_io|]%' ESCAPE '|' OR @output_column_list LIKE '%|[physical_io_delta|]%' ESCAPE '|' THEN 'x.physical_io ' ELSE 'NULL ' END + 'AS physical_io, ' + CASE WHEN @output_column_list LIKE '%|[reads|]%' ESCAPE '|' OR @output_column_list LIKE '%|[reads_delta|]%' ESCAPE '|' THEN 'x.reads ' ELSE '0 ' END + 'AS reads, ' + CASE WHEN @output_column_list LIKE '%|[physical_reads|]%' ESCAPE '|' OR @output_column_list LIKE '%|[physical_reads_delta|]%' ESCAPE '|' THEN 'x.physical_reads ' ELSE '0 ' END + 'AS physical_reads, ' + CASE WHEN @output_column_list LIKE '%|[writes|]%' ESCAPE '|' OR @output_column_list LIKE '%|[writes_delta|]%' ESCAPE '|' THEN 'x.writes ' ELSE '0 ' END + 'AS writes, ' + CASE WHEN @output_column_list LIKE '%|[tempdb_allocations|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tempdb_allocations_delta|]%' ESCAPE '|' THEN 'x.tempdb_allocations ' ELSE '0 ' END + 'AS tempdb_allocations, ' + CASE WHEN @output_column_list LIKE '%|[tempdb_current|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tempdb_current_delta|]%' ESCAPE '|' THEN 'x.tempdb_current ' ELSE '0 ' END + 'AS tempdb_current, ' + CASE WHEN @output_column_list LIKE '%|[CPU|]%' ESCAPE '|' OR @output_column_list LIKE '%|[CPU_delta|]%' ESCAPE '|' THEN 'x.CPU ' ELSE '0 ' END + 'AS CPU, ' + CASE WHEN @outp