DECLARE @job_id UNIQUEIDENTIFIER; DECLARE @step_id INT;
DECLARE agent_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT s.session_id, agent_nodes.agent_node.value('(job_id/text())[1]', 'uniqueidentifier') AS job_id, agent_nodes.agent_node.value('(step_id/text())[1]', 'int') AS step_id FROM #sessions AS s CROSS APPLY s.additional_info.nodes('//agent_job_info') AS agent_nodes (agent_node) WHERE s.recursion = 1 OPTION (KEEPFIXED PLAN);
