 WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, tempdb_current_delta), 1), 19)) ' ELSE 'tempdb_current_delta ' END + 'ELSE NULL ' + 'END AS tempdb_current_delta, ' + --CPU_delta 'CASE ' + 'WHEN ' + 'first_request_start_time = last_request_start_time ' + 'AND num_events = 2 ' + 'THEN ' + 'CASE ' + 'WHEN ' + 'thread_CPU_delta > CPU_delta ' + 'AND thread_CPU_delta > 0 ' + 'THEN ' + CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, thread_CPU_delta + CPU_delta))) OVER() - LEN(CONVERT(VARCHAR, thread_CPU_delta))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, thread_CPU_delta), 1), 19)) ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, thread_CPU_delta), 1), 19)) ' ELSE 'thread_CPU_delta ' END + 'WHEN CPU_delta >= 0 THEN ' + CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, thread_CPU_delta + CPU_delta))) OVER() - LEN(CONVERT(VARCHAR, CPU_delta))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, CPU_delta), 1), 19)) ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, CPU_delta), 1), 19)) ' ELSE 'CPU_delta ' END + 'ELSE NULL ' + 'END ' + 'ELSE ' + 'NULL ' + 'END AS CPU_delta, ' + --context_switches_delta 'CASE ' + 'WHEN ' + 'first_request_start_time = last_request_start_time ' + 'AND num_events = 2 ' + 'AND context_switches_delta >= 0 ' + 'THEN ' + CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, context_switches_delta))) OVER() - LEN(CONVERT(VARCHAR, context_switches_delta))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, context_switches_delta), 1), 19)) ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, context_switches_delta), 1), 19)) ' ELSE 'context_switches_delta ' END + 'ELSE NULL ' + 'END AS context_switches_delta, ' + --used_memory_delta 'CASE ' + 'WHEN ' + 'first_request_start_time = last_request_start_time ' + 'AND num_events = 2 ' + 'AND used_memory_delta >= 0 ' + 'THEN ' + CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, used_memory_delta))) OVER() - LEN(CONVERT(VARCHAR, used_memory_delta))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, used_memory_delta), 1), 19)) ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, used_memory_delta), 1), 19)) ' ELSE 'used_memory_delta ' END + 'ELSE NULL ' + 'END AS used_memory_delta, ' ELSE '' END + --tasks CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, tasks))) OVER() - LEN(CONVERT(VARCHAR, tasks))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, tasks), 1), 19)) AS ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, tasks), 1), 19)) ' ELSE '' END + 'tasks, ' + 'status, ' + 'wait_info, ' + 'locks, ' + 'tran_start_time, ' + 'LEFT(tran_log_writes, LEN(tran_log_writes) - 1) AS tran_log_writes, ' + --open_tran_count CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, open_tran_count))) OVER() - LEN(CONVERT(VARCHAR, open_tran_count))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, open_tran_count), 1), 19)) AS ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, open_tran_count), 1), 19)) AS ' ELSE '' END + 'open_tran_count, ' + --sql_command CASE @format_output WHEN 0 THEN 'REPLACE(REPLACE(CONVERT(NVARCHAR(MAX), sql_command), ''<?query --''+CHAR(13)+CHAR(10), ''''), CHAR(13)+CHAR(10)+''--?>'', '''') AS ' ELSE '' END + 'sql_command, ' + --sql_text CASE @format_output WHEN 0 THEN 'REPLACE(REPLACE(CONVERT(NVARCHAR(MAX), sql_text), ''<?query --''+CHAR(13)+CHAR(10), ''''), CHAR(13)+CHAR(10)+''--?>'', '''') AS ' ELSE '' END + 'sql_text, ' + 'query_plan, ' + 'blocking_session_id, ' + --blocked_session_count CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, blocked_session_count))) OVER() - LEN(CONVERT(VARCHAR, blocked_session_count))) + LEFT(CONVERT(CHAR(22), CONVERT(MONEY, blocked_session_count), 1), 19)) AS ' WHEN 2 THEN 'CONVERT(VARCHAR, LEFT(CONVERT(CHAR(22), CONVERT(MONEY, blocked_session_count), 1), 19)) AS ' ELSE '' END + 'blocked_session_count, ' + --percent_complete CASE @format_output WHEN 1 THEN 'CONVERT(VARCHAR, SPACE(MAX(LEN(CONVERT(VARCHAR, CONVERT(MONEY, percent_complete), 2))) OVER() - LEN(CONVERT(VARCHAR, CONVERT(MONEY, percent_complete), 2))) + CONVERT(CHAR(22), CONVERT(MONEY, percent_complete), 2)) AS ' WHEN 2 THEN 'CONVERT(VARCHAR, CONVERT(CHAR(22), CONVERT(MONEY, blocked_session_count), 1)) AS ' ELSE '' END + 'percent_complete, ' + 'host_name, ' + 'login_name, ' + 'database_name, ' + 'program_name, ' + 'additional_info, ' + 'start_time, ' + 'login_time, ' + 'CASE ' + 'WHEN status = N''sleeping'' THEN NULL ' + 'ELSE request_id ' + 'END AS request_id, ' + 'GETDATE() AS collection_time ' --End inner column list ) + --Derived table and INSERT specification CONVERT ( VARCHAR(MAX), 'FROM ' + '( ' + 'SELECT TOP(2147483647) ' + '*, ' + 'CASE ' + 'MAX ' + '( ' + 'LEN ' + '( ' + 'CONVERT ' + '( ' + 'VARCHAR, ' + 'CASE ' + 'WHEN elapsed_time < 0 THEN ' + '(-1 * elapsed_time) / 86400 ' + 'ELSE ' + 'elapsed_time / 86400000 ' + 'END ' + ') ' + ') ' + ') OVER () ' + 'WHEN 1 THEN 2 ' + 'ELSE ' + 'MAX ' + '( ' + 'LEN ' + '( ' + 'CONVERT ' + '( ' + 'VARCHAR, ' + 'CASE ' + 'WHEN elapsed_time < 0 THEN ' + '(-1 * elapsed_time) / 86400 ' + 'ELSE ' + 'elapsed_time / 86400000 ' + 'END ' + ') ' + ') ' + ') OVER () ' + 'END AS max_elapsed_length, ' + CASE WHEN @output_column_list LIKE '%|_delta|]%' ESCAPE '|' THEN 'MAX(physical_io * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(physical_io * recursion) OVER (PARTITION BY session_id, request_id) AS physical_io_delta, ' + 'MAX(reads * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(reads * recursion) OVER (PARTITION BY session_id, request_id) AS reads_delta, ' + 'MAX(physical_reads * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(physical_reads * recursion) OVER (PARTITION BY session_id, request_id) AS physical_reads_delta, ' + 'MAX(writes * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(writes * recursion) OVER (PARTITION BY session_id, request_id) AS writes_delta, ' + 'MAX(tempdb_allocations * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(tempdb_allocations * recursion) OVER (PARTITION BY session_id, request_id) AS tempdb_allocations_delta, ' + 'MAX(tempdb_current * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(tempdb_current * recursion) OVER (PARTITION BY session_id, request_id) AS tempdb_current_delta, ' + 'MAX(CPU * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(CPU * recursion) OVER (PARTITION BY session_id, request_id) AS CPU_delta, ' + 'MAX(thread_CPU_snapshot * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(thread_CPU_snapshot * recursion) OVER (PARTITION BY session_id, request_id) AS thread_CPU_delta, ' + 'MAX(context_switches * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(context_switches * recursion) OVER (PARTITION BY session_id, request_id) AS context_switches_delta, ' + 'MAX(used_memory * recursion) OVER (PARTITION BY session_id, request_id) + ' + 'MIN(used_memory * recursion) OVER (PARTITION BY session_id, request_id) AS used_memory_delta, ' + 'MIN(last_request_start_time) OVER (PARTITION BY session_id, request_id) AS first_request_start_time, ' ELSE '' END + 'COUNT(*) OVER (PARTITION BY session_id, request_id) AS num_events ' + 'FROM #sessions AS s1 ' + CASE WHEN @sort_order = '' THEN '' ELSE 'ORDER BY ' + @sort_order END + ') AS s ' + 'WHERE ' + 's.recursion = 1 ' + ') x ' + 'OPTION (KEEPFIXED PLAN); ' + '' + CASE @return_schema WHEN 1 THEN 'SET @schema = ' + '''CREATE TABLE <table_name> ( '' + ' + 'STUFF ' + '( ' + '( ' + 'SELECT ' + ''','' + ' + 'QUOTENAME(COLUMN_NAME) + '' '' + ' + 'DATA_TYPE + ' + 'CASE ' + 'WHEN DATA_TYPE LIKE ''%char'' THEN ''('' + COALESCE(NULLIF(CONVERT(VARCHAR, CHARACTER_MAXIMUM_LENGTH), ''-1''), ''max'') + '') '' ' + 'ELSE '' '' ' + 'END + ' + 'CASE IS_NULLABLE ' + 'WHEN ''NO'' THEN ''NOT '' ' + 'ELSE '''' ' + 'END + ''NULL'' AS [text()] ' + 'FROM tempdb.INFORMATION_SCHEMA.COLUMNS ' + 'WHERE ' + 'TABLE_NAME = (SELECT name FROM tempdb.sys.objects WHERE object_id = OBJECT_ID(''tempdb..#session_schema'')) ' + 'ORDER BY ' + 'ORDINAL_POSITION ' + 'FOR XML ' + 'PATH('''') ' + '), + ' + '1, ' + '1, ' + ''''' ' + ') + ' + ''')''; ' ELSE '' END --End derived table and INSERT specification );
SET @sql_n = CONVERT(NVARCHAR(MAX), @sql);
EXEC sp_executesql @sql_n, N'@schema VARCHAR(MAX) OUTPUT', @schema OUTPUT; END;

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 20.02.2019 (1.0)
-- Description:       .
				       powershell.
				      xp_cmdshell.
*/
BEGIN
	SET NOCOUNT ON;
	DECLARE @cmd VARCHAR(7999),@ErrMsg nvarchar(3000),@rc int;
	set @cmd = N'cmd /k chcp 1251 && echo | echo '+COALESCE(LTRIM(@msg),'-')+CASE WHEN (@overwrite = 1) THEN ' > ' ELSE ' >> ' END +RTRIM(@file);
--	SET @cmd = RTRIM('echo ' + COALESCE(LTRIM(@msg),'-') + CASE WHEN (@overwrite = 1) THEN ' > ' ELSE ' >> ' END + RTRIM(@file))
	EXEC @rc=[master].dbo.xp_cmdshell @cmd , no_output
	if @rc=1
	BEGIN
		set @ErrMsg=N'     '+@file;
		RAISERROR(@ErrMsg,11,1) WITH LOG;
	END;
	SET NOCOUNT OFF;
END

CREATE PROC dbo.[usp_WriteToFile]
	@msg VARCHAR(7999),
	@file VARCHAR(300),
	@overwrite BIT = 0
AS

BEGIN SET NOCOUNT ON; declare @time datetime;
/* " "! */ --set @time=getdate(); --if @zabbix=1 --begin -- select 'BusyQuery' as [ForZabbix], S.login_name, Q.SPID, Q.command, Q.wait_type, datediff(hour,start_time,@time) as RunningHours -- From -- ( -- select session_id as SPID,DB_NAME(database_id) as DB, start_time,status,command,blocking_session_id, -- wait_type,wait_time,last_wait_type,wait_resource, -- cpu_time,reads,writes,logical_reads,row_count,query_hash -- from sys.dm_exec_requests -- where status<>'background' -- and command<>'TASK MANAGER' -- and datediff(hour,start_time,@time) >= @busy_hours -- )Q -- inner join sys.dm_exec_sessions S -- on Q.SPID=S.session_id -- order by start_time --end --else if @blocks=1 BEGIN /* ! ! : ! DMV.
*/ if object_id('tempdb..#blk_collect') is not null drop table #blk_collect; SELECT blk_rs.session_id as spid, blk_rs.command, blk_rs.[status], blk_rs.start_time, blk_rs.blocking_session_id as BlkBy, blk_rs.wait_type, blk_rs.wait_time, blk_rs.wait_resource, blk_rs.[TEXT] as [SQLTEXT], HASHBYTES('SHA2_256',(HASHBYTES('SHA2_256',LEFT(blk_rs.[TEXT],4000))+HASHBYTES('SHA2_256',RIGHT(blk_rs.[TEXT],4000))+cast(LEN(blk_rs.[TEXT]) as varbinary(30)))) AS [SQLHandle], blk_rs.DB, dm_ss.host_name as Host, dm_ss.login_name as [Login], dm_ss.program_name as Program, dm_ss.host_process_id as Host_pid, CASE WHEN blk_rs.transaction_isolation_level IS NULL then dm_ss.transaction_isolation_level WHEN blk_rs.transaction_isolation_level = 0 then dm_ss.transaction_isolation_level ELSE blk_rs.transaction_isolation_level END AS trn_iso_lvl, statement_start, statement_end INTO #blk_collect FROM ( -- () SELECT session_id, command, [status], start_time, blocking_session_id, wait_type, wait_time, wait_resource, DB_NAME(database_id) DB, t.TEXT, sql_handle as SQLHandle, transaction_isolation_level, statement_start_offset as statement_start, statement_end_offset as statement_end FROM sys.dm_exec_requests CROSS apply sys.dm_exec_sql_text(sql_handle) AS t WHERE blocking_session_id > 0 UNION -- () SELECT session_id, command, [status], start_time, NULL, wait_type, wait_time, wait_resource, DB_NAME(database_id) DB, t.TEXT, sql_handle as SQLHandle, transaction_isolation_level, statement_start_offset as statement_start, statement_end_offset as statement_end FROM sys.dm_exec_requests CROSS apply sys.dm_exec_sql_text(sql_handle) AS t WHERE session_id IN (SELECT blocking_session_id FROM sys.dm_exec_requests WHERE blocking_session_id > 0) AND blocking_session_id=0 AND (@only_sleep_tran=0) UNION -- () SELECT session_id, NULL, 'sleeping', NULL, NULL, NULL, NULL, NULL, NULL as DB, t.TEXT, most_recent_sql_handle as SQLHandle, NULL as transaction_isolation_level, NULL as statement_start, NULL as statement_end FROM sys.dm_exec_connections CROSS apply sys.dm_exec_sql_text(most_recent_sql_handle) AS t WHERE session_id IN (SELECT blocking_session_id FROM sys.dm_exec_requests WHERE blocking_session_id not in (select session_id from sys.dm_exec_requests)) ) blk_rs LEFT JOIN sys.dm_exec_sessions as dm_ss ON blk_rs.session_id=dm_ss.session_id;
IF (@collect_sql=0) BEGIN IF (@get_count=0) SELECT spid, command, [status], start_time, BlkBy, wait_type, wait_time, wait_resource, [SQLTEXT], CASE WHEN [statement_start] > 0 THEN CASE [statement_end] WHEN -1 THEN SUBSTRING([SQLTEXT], ([statement_start]/2) + 1, 2147483647) ELSE SUBSTRING([SQLTEXT], ([statement_start]/2) + 1, ([statement_end] - [statement_start])/2) END ELSE CASE [statement_end] WHEN -1 THEN RTRIM(LTRIM([SQLTEXT])) ELSE LEFT([SQLTEXT], ([statement_end]/2) +1) END END AS [Exec_Statement], /*[SQLHandle],*/ DB, Host, [Login], Program, Host_pid, trn_iso_lvl FROM #blk_collect; ELSE SELECT Count_Big(*) as cnt FROM #blk_collect WHERE BlkBy is Not null and BlkBy<>0;
END ELSE BEGIN SET @time=GETDATE(); /* sputnik.awr.blk_handle_collect sputnik.awr.sql_text_collect blk_handle_collect - , . sql_text_collect - . */ IF OBJECT_ID('sputnik.awr.blk_handle_collect') IS NOT NULL AND OBJECT_ID('sputnik.awr.sql_text_collect') IS NOT NULL BEGIN SET XACT_ABORT ON; BEGIN TRAN INSERT INTO [awr].[blk_handle_collect] ( [tt], SPID, command, [status], start_time, BlkBy, wait_type, wait_time, wait_resource, [SQLHandle], DB, Host, [Login], Program, Host_pid, trn_iso_lvl, [statement_start],[statement_end] ) SELECT @time as [tt], SPID, command, [status], start_time, BlkBy, wait_type, wait_time, wait_resource, [SQLHandle], DB, Host, [Login], Program, Host_pid, trn_iso_lvl, [statement_start],[statement_end] FROM #blk_collect;
MERGE [awr].[sql_text_collect] as target_table USING ( SELECT DISTINCT @time,[SQLHandle], [SQLText], COUNT(*) OVER (PARTITION BY [SQLHandle]) FROM #blk_collect ) AS source_table ([tt],[SQLHandle], [SQLText], [NumLocks]) ON (target_table.[SQLHandle]=source_table.[SQLHandle]) WHEN NOT MATCHED THEN INSERT([tt],[SQLHandle], [SQLText], [NumLocks]) VALUES(source_table.[tt],source_table.[SQLHandle],source_table.[SQLText],source_table.[NumLocks]) WHEN MATCHED THEN UPDATE SET target_table.[tt] = source_table.[tt], target_table.[NumLocks] += source_table.[NumLocks] ; COMMIT; SET XACT_ABORT OFF; END ELSE PRINT(' : awr.blk_handle_collect awr.sql_text_collect !'); END
-- : --select -- BS.host_name as BlockHost, BS.login_name as BlockLogin,Q.blocking_session_id as SPID_Block, BInfo.start_time as BlockStart, -- BInfo.status as BlockStatus, BInfo.command as BlockCommand, -- S.host_name as WaitHost,S.login_name as WaitLogin,Q.session_id as WaitSPID,DB_NAME(Q.database_id) as DB, -- Q.start_time as WaitStart, Q.status as WaitStatus, Q.command as WaitCommand
--from sys.dm_exec_requests Q --inner join sys.dm_exec_sessions S -- on Q.session_id=S.session_id --left join sys.dm_exec_requests BInfo -- on Q.blocking_session_id=BInfo.session_id --left join sys.dm_exec_sessions BS -- on BInfo.session_id=BS.session_id --where -- Q.blocking_session_id<>0 END else BEGIN /*------- ( 2.00) select getdate() as ServerTime,S.host_name,S.login_name,S.login_time,Q.* From ( select session_id as SPID,DB_NAME(database_id) as DB, start_time,status,command,blocking_session_id, wait_type,wait_time,last_wait_type,wait_resource, cpu_time,reads,writes,logical_reads,row_count,query_hash from sys.dm_exec_requests where status<>'sleeping' and status<>'background' and datediff(minute,start_time,@time) >= @busy_minutes )Q inner join sys.dm_exec_sessions S on Q.SPID=S.session_id order by start_time */
/* ( 2.00)! sp_whoisactive SQL Server 2012 (.. T-SQL). */ if object_id('tempdb..#qinfo_collect') is not null drop table #qinfo_collect;
select S.host_name as [Host],S.program_name as Program, IIF(S.login_name='',(select name from sys.server_principals where [sid]=S.Security_id),S.login_name) as [Login],
S.login_time, S.open_transaction_count as Open_Tran, Q.* into #qinfo_collect From ( select q.session_id as SPID,DB_NAME(q.database_id) as DB, start_time,status, command,blocking_session_id as blk_by, percent_complete as perc_complete, wait_type,wait_time,wait_resource, cast(cast(total_elapsed_time as decimal(15,3))/1000.000 as decimal(15,3)) as RunTime_sec, cast(cast(CPU_Time as decimal(14,2))/1000.000 as decimal(15,3)) as CPU_sec, cast(cast(granted_query_memory as decimal(14,2))/128.00 as decimal(14,2)) as MemoryMb,reads as IO_Reads,writes as IO_Writes,Logical_Reads,row_count as [RowCount], st.text as SQLText, HASHBYTES('SHA2_256',(HASHBYTES('SHA2_256',LEFT(st.[TEXT],4000))+HASHBYTES('SHA2_256',RIGHT(st.[TEXT],4000))+cast(LEN(st.[TEXT]) as varbinary(30)))) AS [SQLHandle], query_hash as SQLHash, q.statement_start_offset as [statement_start] , q.statement_end_offset as [statement_end], SUM(COALESCE(tsu.user_objects_alloc_page_count,0)+COALESCE(tsu.internal_objects_alloc_page_count,0)-COALESCE(tsu.user_objects_dealloc_page_count,0)-COALESCE(tsu.internal_objects_dealloc_page_count,0)) over (partition by tsu.session_id,tsu.request_id) as tempdb_current_query, SUM(COALESCE(tsu.user_objects_alloc_page_count,0)+COALESCE(tsu.internal_objects_alloc_page_count,0)) over (partition by tsu.session_id,tsu.request_id) as tempdb_allocation_query, SUM(COALESCE(ssu.user_objects_alloc_page_count,0)+COALESCE(ssu.internal_objects_alloc_page_count,0)-COALESCE(ssu.user_objects_dealloc_page_count,0)-COALESCE(ssu.internal_objects_dealloc_page_count,0)) over (partition by ssu.session_id) as tempdb_current_session, SUM(COALESCE(ssu.user_objects_alloc_page_count,0)+COALESCE(ssu.internal_objects_alloc_page_count,0)) over (partition by ssu.session_id) as tempdb_allocation_session from sys.dm_exec_requests q CROSS APPLY sys.dm_exec_sql_text(q.sql_handle) AS st left join sys.dm_db_task_space_usage tsu on q.request_id=tsu.request_id and q.session_id=tsu.session_id left join sys.dm_db_session_space_usage ssu on q.session_id=ssu.session_id where [status] NOT IN('background'/*,'sleeping'*/) and q.session_id<>@@SPID )Q inner join sys.dm_exec_sessions S on Q.SPID=S.session_id WHERE -- AlwaysOn Windows- (S.program_name <> 'Microsoft Windows Operating System' AND Q.wait_type<>'SP_SERVER_DIAGNOSTICS_SLEEP' OR Q.wait_type IS NULL OR S.program_name IS NULL) AND (@only_sleep_tran=0) UNION select s.host_name as [Host], s.program_name as [Program], IIF(S.login_name='',(select name from sys.server_principals where [sid]=S.Security_id),S.login_name) as [Login], s.login_time, s.open_transaction_count as Open_Tran, s.session_id as SPID, DB_NAME(s.database_id) as DB, s.last_request_start_time as start_time, s.[status], NULL as command, NULL as blk_by, NULL as perc_complete, 'BUSY_SLEEPING_TASK' as wait_type, NULL as wait_time, 'Open_Tran>0' wait_resource, cast(DATEDIFF(SECOND,s.last_request_start_time,s.last_request_end_time) as decimal(15,3)) as RunTime_sec, cast(cast(s.CPU_Time as decimal(14,2))/1000.000 as decimal(15,3)) as CPU_sec, cast(cast(s.memory_usage as decimal(14,2))/128.00 as decimal(14,2)) as MemoryMb, s.reads as IO_Reads,s.writes as IO_Writes,s.Logical_Reads,s.row_count as [RowCount],
sqltext.text as SQLText, HASHBYTES('SHA2_256',(HASHBYTES('SHA2_256',LEFT(sqltext.[TEXT],4000))+HASHBYTES('SHA2_256',RIGHT(sqltext.[TEXT],4000))+cast(LEN(sqltext.[TEXT]) as varbinary(30)))) AS [SQLHandle], NULL as SQLHash, NULL as [statement_start] , NULL as [statement_end], NULL as tempdb_current_query, NULL as tempdb_allocation_query, SUM(COALESCE(ssu.user_objects_alloc_page_count,0)+COALESCE(ssu.internal_objects_alloc_page_count,0)-COALESCE(ssu.user_objects_dealloc_page_count,0)-COALESCE(ssu.internal_objects_dealloc_page_count,0)) over (partition by ssu.session_id) as tempdb_current_session, SUM(COALESCE(ssu.user_objects_alloc_page_count,0)+COALESCE(ssu.internal_objects_alloc_page_count,0)) over (partition by ssu.session_id) as tempdb_allocation_session from sys.dm_exec_sessions s inner join sys.dm_exec_connections c on c.session_id=s.session_id cross apply sys.dm_exec_sql_text(c.most_recent_sql_handle) as sqltext left join sys.dm_db_session_space_usage ssu on s.session_id=ssu.session_id where (s.open_transaction_count>0 and s.[status]='sleeping');
SET @time=GETDATE(); IF @zabbix=1 BEGIN /* " "! */ DECLARE @MachineName varchar(300)=CAST(SERVERPROPERTY('MachineName') as varchar(128)), @InstanceName varchar(300)=COALESCE(CAST(SERVERPROPERTY('InstanceName') as varchar(128)),''), @SQLServer varchar(300), @busy_minutes_zabbix smallint;; SET @SQLServer=LOWER(@MachineName+CASE WHEN @InstanceName > '' THEN '\' ELSE '' END + @InstanceName); SELECT @busy_minutes_zabbix=CASE WHEN CHARINDEX('-bi-',@SQLServer)>0 THEN 8*60 WHEN EXISTS (select top 1 database_id from sys.databases where name IN ('pegasus2008ms','pegasus2008bb')) THEN 1*60 WHEN (EXISTS (select top 1 database_id from sys.databases where name like 'pegasus2008%') AND CHARINDEX('dev',@SQLServer)=0 AND CHARINDEX('test',@SQLServer)=0 AND CHARINDEX('qc',@SQLServer)=0) THEN 2*60 ELSE 4*60 END; --SELECT 'BusyQuery' as ForZabbix, [Login] as login_name, SPID, command, wait_type, datediff(hour,start_time,@time) as RunningHours SELECT TOP 1 CASE WHEN Count_Big(*)>=1 THEN 'BusyQuery' ELSE 'Its OK' END as ForZabbix, Count_Big(*) as cnt, sputnik.info.uf_FormatTime(SUM(datediff(second,start_time,@time))) as SumDuration, sputnik.info.uf_FormatTime(MAX(datediff(second,start_time,@time))) as MaxDuration FROM #qinfo_collect WHERE datediff(minute,start_time,@time) >= @busy_minutes_zabbix and (wait_type<>'SP_SERVER_DIAGNOSTICS_SLEEP' OR wait_type IS NULL) END ELSE IF @collect_sql=0 IF @get_count=0 SELECT /*@time as [tt],*/ SPID, SQLText, CASE WHEN [statement_start] > 0 THEN CASE [statement_end] WHEN -1 THEN SUBSTRING([SQLTEXT], ([statement_start]/2) + 1, 2147483647) ELSE SUBSTRING([SQLTEXT], ([statement_start]/2) + 1, ([statement_end] - [statement_start])/2) END ELSE CASE [statement_end] WHEN -1 THEN RTRIM(LTRIM([SQLTEXT])) ELSE LEFT([SQLTEXT], ([statement_end]/2) +1) END END AS [Exec_Statement], --CAST('<?query -- --'+SQLText+' ----?>' as xml) as SQLText, [Host], [Program], [Login], login_time, [Open_Tran], DB, [start_time], [status], command, blk_by, perc_complete, wait_type, IIF(wait_type='BUSY_SLEEPING_TASK',cast(DATEDIFF(SECOND,[start_time],@time) as bigint)*1000,wait_time) as wait_time, wait_resource, RunTime_sec, CPU_sec,MemoryMb, IO_Reads, IO_Writes, Logical_Reads, [RowCount], tempdb_current_query, tempdb_allocation_query, tempdb_current_session, tempdb_allocation_session /*, SQLHandle, SQLHash*/ FROM #qinfo_collect WHERE ((wait_type='BUSY_SLEEPING_TASK' and DATEDIFF(SECOND,[start_time],@time)>=1) OR wait_type<>'BUSY_SLEEPING_TASK' OR wait_type IS NULL) AND (@busy_minutes IS NULL OR @busy_minutes=0 OR DATEDIFF(MINUTE,[start_time],@time)>=@busy_minutes)
ORDER BY [start_time]; ELSE SELECT Count_Big(*) as cnt FROM #qinfo_collect WHERE ((wait_type='BUSY_SLEEPING_TASK' and DATEDIFF(SECOND,[start_time],@time)>=1) OR wait_type<>'BUSY_SLEEPING_TASK' OR wait_type IS NULL) AND (@busy_minutes IS NULL OR @busy_minutes=0 OR DATEDIFF(MINUTE,[start_time],@time)>=@busy_minutes);
ELSE BEGIN /* sputnik.awr.sql_handle_collect sputnik.awr.sql_text_collect sql_handle_collect - , . sql_text_collect - . */ IF OBJECT_ID('sputnik.awr.sql_handle_collect') IS NOT NULL AND OBJECT_ID('sputnik.awr.sql_text_collect') IS NOT NULL BEGIN SET XACT_ABORT ON; BEGIN TRAN INSERT INTO [awr].[sql_handle_collect] ( [tt], [Host], [Program], [Login], login_time, [Open_Tran], SPID, DB, [start_time], [status], command, blk_by, perc_complete, wait_type, wait_time, wait_resource, RunTime_sec, CPU_sec,MemoryMb, IO_Reads, IO_Writes, Logical_Reads, [RowCount], [SQLHandle], [statement_start], [statement_end], tempdb_current_query, tempdb_allocation_query, tempdb_current_session, tempdb_allocation_session ) SELECT @time as [tt], [Host], [Program], [Login], login_time, [Open_Tran], SPID, DB, [start_time], [status], command, blk_by, perc_complete, wait_type, IIF(wait_type='BUSY_SLEEPING_TASK',cast(DATEDIFF(SECOND,[start_time],@time) as bigint)*1000,wait_time) as wait_time, wait_resource, RunTime_sec, CPU_sec,MemoryMb, IO_Reads, IO_Writes, Logical_Reads, [RowCount], SQLHandle, [statement_start], [statement_end], tempdb_current_query, tempdb_allocation_query, tempdb_current_session, tempdb_allocation_session FROM #qinfo_collect WHERE ((wait_type='BUSY_SLEEPING_TASK' and DATEDIFF(SECOND,[start_time],@time)>=1) OR wait_type<>'BUSY_SLEEPING_TASK' OR wait_type IS NULL);
MERGE [awr].[sql_text_collect] as target_table USING ( SELECT DISTINCT @time,[SQLHandle], [SQLText], COUNT(*) OVER (PARTITION BY [SQLHandle]) FROM #qinfo_collect WHERE ((wait_type='BUSY_SLEEPING_TASK' and DATEDIFF(SECOND,[start_time],@time)>=1) OR wait_type<>'BUSY_SLEEPING_TASK' OR wait_type IS NULL) ) AS source_table ([tt],[SQLHandle], [SQLText], [NumRuns]) ON (target_table.[SQLHandle]=source_table.[SQLHandle]) WHEN NOT MATCHED THEN INSERT([tt],[SQLHandle], [SQLText], [NumRuns]) VALUES(source_table.[tt],source_table.[SQLHandle],source_table.[SQLText],source_table.[NumRuns]) WHEN MATCHED THEN UPDATE SET target_table.[tt] = source_table.[tt], target_table.[NumRuns] += source_table.[NumRuns] ; COMMIT; SET XACT_ABORT OFF; END ELSE PRINT(' : awr.sql_handle_collect awr.sql_text_collect !'); END END END

BEGIN
	declare @Return int=0;
	SET @File='DIR /b "'+@File+'"';
	EXEC @Return = master.dbo.xp_cmdshell @File, NO_OUTPUT

	return CASE @Return WHEN 0 THEN 1 ELSE 0 END
END

BEGIN declare @Return varchar(20); --: --select @seconds/(3600*24) as dd,(@seconds%(3600*24))/3600 as hh,((@seconds%(3600*24))%3600) / 60 as mm, @seconds % 60 as ss
SET @Return=CASE WHEN @seconds is null THEN '' ELSE CASE WHEN @seconds/(3600*24)>=1 THEN RIGHT (CONVERT(varchar(6), @seconds/(3600*24)),3)+ 'd:' ELSE '' END + RIGHT ('0' + CONVERT(varchar(6), (@seconds%(3600*24))/3600),2) + ':' + RIGHT('0' + CONVERT(varchar(2), ((@seconds%(3600*24))%3600) / 60), 2) + ':' + RIGHT('0' + CONVERT(varchar(2), @seconds % 60), 2) END;
return @Return END

/* ============================================= -- Author: (sqland1c) -- Create date: 01.12.2017 -- Description: () , , , . -
-- Update:
-- ============================================= */ CREATE FUNCTION info.uf_FormatTime ( @seconds bigint ) RETURNS varchar(20) AS

BEGIN DECLARE @WeekDay TINYINT, @MonthDay TINYINT; DECLARE @T TABLE (LocalDir NVARCHAR(300), NetDir NVARCHAR(300), LocalDays INT, NetDays INT, Ord tinyint);
SET @WeekDay=info.uf_GetWeekDay(@DD); SET @MonthDay=DATEPART ( DAY , @DD );
INSERT INTO @T (LocalDir, NetDir, LocalDays, NetDays, Ord) SELECT TOP 1 LocalDir, NetDir, LocalDays, NetDays, 1 as Ord FROM backups.BackConfWeekly WHERE Kind=@BT and DBName=@DB and MonthDay=@MonthDay UNION ALL SELECT TOP 1 LocalDir, NetDir, LocalDays, NetDays, 2 as Ord FROM backups.BackConfWeekly WHERE Kind=@BT and DBName=@DB and WeekDay=@WeekDay UNION ALL SELECT TOP 1 LocalDir, NetDir, LocalDays, NetDays, 3 as Ord FROM backups.BackConf WHERE DBName = @DB AND Kind = @BT;
-- ( !): @OnlyFull usp_RunBack -- Full BackConfWeekly ! IF NOT EXISTS (SELECT LocalDir FROM @T WHERE LocalDir IS NOT NULL) AND @BT='Full' BEGIN INSERT INTO @T (LocalDir, NetDir, LocalDays, NetDays, Ord) SELECT TOP 1 LocalDir, NetDir, LocalDays, NetDays, 1 AS Ord FROM backups.BackConfWeekly WHERE Kind='Full' and DBName=@DB and MonthDay BETWEEN 1 AND 31 UNION ALL SELECT TOP 1 LocalDir, NetDir, LocalDays, NetDays, 2 AS Ord FROM backups.BackConfWeekly WHERE Kind='Full' and DBName=@DB and WeekDay BETWEEN 1 AND 7; END;
INSERT @ReturnTable (LocalDir, NetDir, LocalDays, NetDays) SELECT LocalDir, NetDir, LocalDays, NetDays FROM @T WHERE Ord=(SELECT MIN(Ord) FROM @T) ;
RETURN; END

/* ============================================= -- Author: (sqland1c) -- Create date: 25.04.2014 -- Description: , .
-- Update: 31.07.2014 (1.1) , , ( @OnlyFull usp_RunBack).
-- ============================================= */ CREATE FUNCTION info.uf_GetBackConf ( @DB NVARCHAR(100), --Database Name @BT VARCHAR(4), --BackupType @DD DATETIME2(2) --Date of Backup ) RETURNS @ReturnTable TABLE ( LocalDir NVARCHAR(300), NetDir NVARCHAR(300), LocalDays int, NetDays int ) AS

/* ============================================= -- Author: (sqland1c) -- Create date: 17.02.2014 -- Description: .
-- Update:
-- ============================================= */ CREATE FUNCTION info.uf_GetWeekDay ( @date datetime ) RETURNS int AS

BEGIN
	SET NOCOUNT ON;
	declare @Return tinyint=0;
	Declare @DIR NVARCHAR(550);
	declare @TableCheck TABLE ([File Exists] bit, [File is a Directory] bit, [Parent Directory Exists] bit);
	--   ;
	INSERT INTO @TableCheck
	EXEC xp_fileexist @Catalog;
	SELECT @Return=[File is a Directory] FROM @TableCheck;
	if @Return=0
		SELECT @Return=[Parent Directory Exists] FROM @TableCheck;
	--,       !	
	if @Return=0
	begin
		SET @DIR='MKDIR '+@Catalog;
		exec @Return= xp_cmdshell @DIR , NO_OUTPUT;
		IF @Return=0 
			SET @Return=1;
		ELSE
			SET @Return=2;
	end
	else
		if @Return=0
			SET @Return=2;
	SELECT @Catalog AS 'Dir', @Return AS 'Exist';
END

