CREATE PROCEDURE info.usp_CheckCatalog
(
	@Catalog nvarchar(500)
)
AS

begin set nocount on; declare @SQLServer nvarchar(510); exec sputnik.info.usp_GetHostname @Servername=@SQLServer OUT; /* select @sqlserver as ServerName, DB_NAME(database_id) as DBName, mirroring_role_desc as Role, mirroring_state_desc as State, mirroring_partner_instance as PartnerServerName from sys.database_mirroring where mirroring_guid is not null and (@zabbix=0 or (mirroring_state_desc not in ('SYNCHRONIZING','SYNCHRONIZED'))) */ -- ANSI_WARNINGS ! SET ANSI_WARNINGS OFF; DECLARE @T_Jobs TABLE (SQLServerName sql_variant, [Job] NVARCHAR(200), [Step] NVARCHAR(1), Info CHAR(1), RUN_STATUS VARCHAR(30), DateTimeRun DateTime, Duration VARCHAR(10)); IF @ResState is null insert into @T_Jobs exec sputnik.info.usp_JobMonitor @Activity=1;
Declare @T_Result TABLE (servername nvarchar(1000), DBName nvarchar(2000), [Role] nvarchar(100), [State] nvarchar(100), PartnerServerName nvarchar(1000)); insert into @T_Result(servername,DBName,[Role],[State],PartnerServerName) SELECT @sqlserver as ServerName, DB.DB as DBName, CASE WHEN alwayson_ag.[Role] IS NOT NULL AND alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS<>@sqlserver THEN N'AlwaysON '+QUOTENAME(alwayson_ag.[Role] COLLATE Cyrillic_General_CI_AS) WHEN alwayson_ag.[Role] IS NOT NULL AND alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS=@sqlserver THEN N'AlwaysON [PRIMARY]' WHEN Mir.mirroring_state_desc IS NULL THEN N'PRIMARY' ELSE Mir.mirroring_role_desc END AS [Role], CASE WHEN alwayson_ag.[Role] IS NOT NULL AND alwayson_ag.health=N'HEALTHY' THEN alwayson_ag.sync_state WHEN alwayson_ag.[Role] IS NOT NULL AND alwayson_ag.health<>N'HEALTHY' THEN alwayson_ag.sync_state+' '+QUOTENAME(alwayson_ag.health COLLATE Cyrillic_General_CI_AS) WHEN Mir.mirroring_state_desc IS NULL AND (Jobs.[RUN_STATUS] IS NULL OR Jobs.[RUN_STATUS] COLLATE Cyrillic_General_CI_AS='Successfully') THEN 'Candidate' WHEN Mir.mirroring_state_desc IS NULL AND Jobs.[RUN_STATUS] IS NOT NULL THEN 'Setup '+Jobs.RUN_STATUS COLLATE Cyrillic_General_CI_AS ELSE Mir.mirroring_state_desc END AS [State], CASE WHEN alwayson_ag.[Role] IS NOT NULL AND (alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS=@sqlserver AND alwayson_ag.[PartnerReplica] COLLATE Cyrillic_General_CI_AS <> alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS) THEN N'SECONDARY: '+QUOTENAME(alwayson_ag.[PartnerReplica] COLLATE Cyrillic_General_CI_AS) WHEN alwayson_ag.[Role] IS NOT NULL AND (alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS<>@sqlserver OR alwayson_ag.[PartnerReplica] COLLATE Cyrillic_General_CI_AS = alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS) THEN N'PRIMARY: '+QUOTENAME(alwayson_ag.[PrimaryReplica] COLLATE Cyrillic_General_CI_AS) --WHEN alwayson_ag.[Role] IS NOT NULL AND alwayson_ag.[Role] COLLATE Cyrillic_General_CI_AS<>'PRIMARY' THEN N'Primary: '+QUOTENAME(alwayson_ag.PrimaryReplica) WHEN Mir.mirroring_state_desc IS NULL THEN '' ELSE Mir.mirroring_partner_instance END AS [PartnerServerName] FROM ( SELECT DB --, CASE WHEN [Log]=0 THEN 'XLog' ELSE 'Candidate' END AS [Status] FROM sputnik.info.vGetQuickBackConf WHERE [state]='ONLINE' AND RecoveryModel='FULL' AND DB NOT IN ('master','msdb','model') -- , Full Recovery Model ( sputnik). . : --AND (([Full]=1 OR [FullWeekly]=1) AND ([Log]=1 OR DB IN (SELECT DBName FROM sputnik.backups.BackConf WHERE [Kind] IN ('XLog', 'Log_Secondary')))) ) DB LEFT JOIN sys.database_mirroring Mir ON DB.DB=DB_NAME(Mir.database_id) LEFT JOIN ( SELECT DB_NAME(ag_db.database_id) as DB, ISNULL(arstates.role_desc, '') AS [Role], ISNULL(AR.replica_server_name, '') as [PartnerReplica], ISNULL(agstates.primary_replica, '') AS [PrimaryReplica], ag_db.synchronization_state_desc as sync_state, ag_db.synchronization_health_desc as health FROM sys.dm_hadr_database_replica_states ag_db LEFT JOIN sys.dm_hadr_availability_group_states as agstates on ag_db.group_id=agstates.group_id LEFT JOIN sys.dm_hadr_availability_replica_states AS arstates ON ag_db.replica_id = arstates.replica_id and ag_db.group_id=arstates.group_id LEFT JOIN sys.availability_replicas as AR ON ag_db.replica_id=AR.replica_id and ag_db.group_id=AR.group_id --WHERE ag_db.is_local=1 OR -- ISNULL(AR.replica_server_name, '') COLLATE Cyrillic_General_CI_AS<>@sqlserver ) as alwayson_ag ON DB.DB=alwayson_ag.DB LEFT JOIN (select REPLACE([Job],'RM ','') as DB,[RUN_STATUS] from @T_Jobs where [Job] LIKE 'RM %') as Jobs ON DB.DB=Jobs.DB-- AND DB.[Status]='XLog' WHERE (Mir.mirroring_state_desc IS NOT NULL OR alwayson_ag.[Role] IS NOT NULL OR DB.DB NOT IN (select DBNameTarget from sputnik.lse.SourceConfig)) AND (@zabbix=0 or (Mir.mirroring_state_desc not in ('SYNCHRONIZING','SYNCHRONIZED'))); if @ResState is null or @dbfilter is null select * from @T_Result where (DBName = @dbfilter or @dbfilter is null); else begin declare @restemp nvarchar(100);
select top 1 @restemp='mi '+CASE WHEN [State] IN ('SYNCHRONIZING','SYNCHRONIZED') THEN '1' ELSE '2' END from @T_Result where (DBName = @dbfilter) and [State]<>'Candidate' and [Role] not like 'AlwaysON %';
if @restemp is null select top 1 @restemp='ao 2' from @T_Result where (DBName = @dbfilter) and [Role] like 'AlwaysON %' and [state] NOT IN ('SYNCHRONIZING','SYNCHRONIZED'); if @restemp is null select top 1 @restemp='ao 1' from @T_Result where (DBName = @dbfilter) and [Role] like 'AlwaysON %' and [state] IN ('SYNCHRONIZING','SYNCHRONIZED'); if @restemp is null select top 1 @restemp='0' from @T_Result where (DBName = @dbfilter) and [State]='Candidate';
set @ResState=COALESCE(@restemp,'-1'); end
end

BEGIN
	SET NOCOUNT ON;
	create table #DriveSpaceLeft (Drive varchar(10),
								  [MB Free] bigint )

	insert #DriveSpaceLeft (Drive, [MB Free])
	   EXEC master.dbo.xp_fixeddrives;

	create table #DrivesWithIssue(Drive varchar(10),
								  [MB Free] bigint )

	insert into #DrivesWithIssue
	  select Drive, [MB Free] from #DriveSpaceLeft
	  where [MB Free] < 10*1024

	drop table #DriveSpaceLeft

	declare @cnt int  
	select @cnt=COUNT(1) from #DrivesWithIssue
	if (@cnt > 0)
	begin

		declare @strsubject varchar(100)
		select @strsubject='Check drive space on ' + @@SERVERNAME

		declare @tableHTML  nvarchar(max);
		set @tableHTML =
			N'<H1>Drives with less that 10GB Free  - ' + @@SERVERNAME + '</H1>' +
			N'<table border="1">' +
			N'<tr><th>Drive</th>' +
			N'<th>MB Free</th></tr>' +
			CAST ( ( SELECT td = [Drive], '',
							td = [MB Free]
					  FROM #DrivesWithIssue
					  FOR XML PATH('tr'), TYPE 
			) AS NVARCHAR(MAX) ) +
			N'</table>' ;

		 EXEC msdb.dbo.sp_send_dbmail
		 --@from_address='test@test.com',
		 @recipients='dba-info@ntsmail.ru',
		 @subject = @strsubject,
		 @body = @tableHTML,
		 @body_format = 'HTML' ,
		 @profile_name='sql-info'
	end

	drop table #DrivesWithIssue;
END

BEGIN
	SET NOCOUNT ON;
	create table #MemoryInfo (
		[Total Memory MB] bigint NOT NULL,
		[Available Memory MB] bigint NOT NULL,
		[% Memory Free] decimal(5,2) NOT NULL)

	insert into #MemoryInfo
	SELECT total_physical_memory_kb/1024 as "Total Memory MB",
		   available_physical_memory_kb/1024 as "Available Memory MB",
		   available_physical_memory_kb/(total_physical_memory_kb*1.0)*100 AS "% Memory Free"
	FROM sys.dm_os_sys_memory;

	declare @memfree float  
	select @memfree=[Available Memory MB] from #MemoryInfo    
	if (@memfree < 700)
	begin

		declare @strsubject varchar(100)
		select @strsubject='Check memory usage on ' + @@SERVERNAME

		declare @tableHTML  nvarchar(max);
		set @tableHTML =
			N'<H1>Server Memory Information - ' + @@SERVERNAME +'</H1>' +
			N'<table border="1">' +
			N'<tr><th>TotalMemory MB</th><th>Available Memory MB</th>' +
			N'<th>% Memory Free</th></tr>' +
			CAST ( ( SELECT td = [Total Memory MB], '',
							td = [Available Memory MB], '',
							td = [% Memory Free]
					  FROM #MemoryInfo
					  FOR XML PATH('tr'), TYPE 
			) AS NVARCHAR(MAX) ) +
			N'</table>' ;

		 EXEC msdb.dbo.sp_send_dbmail
		 --@from_address='test@test.com',
		 @recipients='dba-info@ntsmail.ru',
		 @subject = @strsubject,
		 @body = @tableHTML,
		 @body_format = 'HTML' ,
		 @profile_name='sql-info'
	end

	drop table #MemoryInfo;
END

BEGIN SET NOCOUNT ON; declare @sql NVARCHAR(MAX), @dblist_fmt NVARCHAR(MAX); -- SQL Server: declare @servername nvarchar(510); exec sputnik.info.usp_GetHostname @Servername=@Servername OUT; IF @DBList > N'' AND @DBList IS NOT NULL BEGIN --11.04.17 @DBList ( ); set @dblist_fmt = 'N'''+REPLACE(@DBList, @DBList_delimeter,''' , N''')+''''; set @dblist_fmt = REPLACE(@dblist_fmt,'N'''' , ',''); set @dblist_fmt = REPLACE(@dblist_fmt,' , N''''',''); END CREATE TABLE #x(DB Nvarchar(2000)); SET @sql = N'SELECT name FROM sys.databases WHERE 1=1' + CASE WHEN @dblist_fmt IS NOT NULL THEN ' AND name IN (' + @dblist_fmt + ')' ELSE '' END; INSERT #x EXEC sp_executesql @sql;
IF (@GetFreeSpaceMb=1 /*OR @GetAllSizeGb=1*/) and (@DBFilter is null OR @DBFilter='') set @DBFilter='tempdb'; -- TempDB : if @GetFreeSpaceMb=1 and @DBFilter='tempdb' begin select cast(cast(cntr_value as numeric(19,2))/(1024.00*1024.00) as numeric(19,2)) as FreeSpaceGb from sys.dm_os_performance_counters where counter_name='Free Space in tempdb (KB)'; return; end -- if @GetLogFilesSize=1 or @GetLogFilesSizeUsed=1 begin declare @logsize_res numeric(19,2); ;with cte_src AS ( select QUOTENAME(@DBFilter)+' Log Files Size (Gb)' as counter_name, case rtrim(counter_name) when 'Log File(s) Size (KB)' then '_Total' when 'Log File(s) Used Size (KB)' then '_Used' end as instance_name, cast(cast(cntr_value as numeric(19,2))/(1024.00*1024.00) as numeric(19,2)) as value from sys.dm_os_performance_counters where counter_name like 'Log File(s)% Size (KB)%' and rtrim(instance_name)=@DBFilter ) select @logsize_res=[value] from cte_src where instance_name=CASE WHEN @GetLogFilesSize=1 THEN '_Total' ELSE '_Used' END ; IF @ReturnNum is not null set @ReturnNum=@logsize_res; else select @logsize_res as [Gb];
return; end
-- : sys.master_files sys.Database_Files( ). IF OBJECT_ID('tempdb..#FSizeAll') is not null DROP TABLE #FSizeAll; CREATE TABLE #FSizeAll (
DB Nvarchar(2000), [DBID] int, F_ID int, -- File_ID FName SYSNAME, FType NVARCHAR(60), FState nvarchar(60), FG NVARCHAR(900), SizeMb DECIMAL(12,2), MaxSizeMb DECIMAL(12,2), FreeSpaceMB DECIMAL(12,2), LogBusy nvarchar(60), DBState nvarchar(60), DBCreateDate datetime, DBReadOnly bit, DBRecoveryModel nvarchar(60), DBOwner nvarchar(128) );
-- sys.master_files INSERT INTO #FSizeAll ([DBID], DB, F_ID, FName, FType, [FState], FG, SizeMb, MaxSizeMB, FreeSpaceMB, LogBusy, [DBState], DBCreateDate, DBReadOnly, DBRecoveryModel,DBOwner) SELECT DBinfo.database_id AS [DBID], DBinfo.name AS DB, mf.[file_id] as F_ID, mf.name as FName, type_desc AS FType, mf.[state_desc] AS [FState], NULL as FG, cast(size/128.0 as decimal(12,2)) as SizeMB, CASE WHEN mf.max_size=-1 THEN 0 WHEN mf.type_desc='LOG' and mf.max_size=268435456 THEN 0 ELSE cast(mf.max_size/128.0 as decimal(12,2)) END as MaxSizeMB, NULL AS FreeSpaceMb, CASE WHEN mf.type_desc<>'LOG' THEN '' WHEN DBinfo.log_reuse_wait_desc='NOTHING' THEN '' ELSE DBinfo.log_reuse_wait_desc END AS LogBusy, DBinfo.state_desc AS [DBState], DBinfo.create_date AS DBCreateDate, DBinfo.is_read_only AS DBReadOnly, DBinfo.recovery_model_desc AS DBRecoveryModel, suser_sname(DBinfo.owner_sid) as DBOwner FROM sys.master_files as mf left join sys.databases as DBinfo ON mf.database_id=DBinfo.database_id inner join #x as DBList ON DBinfo.[name]=DBList.DB WHERE (DBinfo.name=@DBFilter OR @DBFilter is null) AND (DBinfo.name NOT LIKE '201%(%)%' and DBinfo.name NOT LIKE 'S201%(%)%') ;
IF @GetAllSizeGb=1 BEGIN Select TOP 1 cast(SUM(SizeMB)/1024.0 as decimal(12,3)) AS AllSizeGb From #FSizeAll; IF @ReturnNum is not null Select TOP 1 @ReturnNum=cast(SUM(SizeMB)/1024.0 as decimal(12,3)) From #FSizeAll; return; END
-- sys.database_files ( ) -- #FSizeAll! DECLARE @db Nvarchar(2000), @Str NVARCHAR(MAX); DECLARE Db CURSOR FOR SELECT DISTINCT DB AS name FROM #FSizeAll F WHERE [FState]='ONLINE' AND [DBState]='ONLINE' OPEN Db FETCH NEXT FROM Db INTO @db WHILE @@FETCH_STATUS=0 BEGIN SELECT @Str=N'USE ['+@db+N']; DECLARE @DBFiles TABLE (F_ID int, FName nvarchar(128), FG nvarchar(128), FreeSpaceMb decimal(19,2), SizeMb decimal(19,2),MaxSizeMB decimal(19,2)); INSERT INTO @DBFiles (F_ID, FName, FG, FreeSpaceMb, SizeMb, MaxSizeMB) SELECT [file_id] as F_ID, name as FName, ISNULL(FILEGROUP_NAME(data_space_id),'''') as FG, CASE WHEN type_desc=''FILESTREAM'' THEN 0.00 ELSE cast(size/128.0 as decimal(19,2)) - cast(FILEPROPERTY(name,''SpaceUsed'')/128.0 as decimal(19,2)) END AS FreeSpaceMb, cast(size/128.0 as decimal(12,2)) as SizeMb, CASE WHEN max_size=-1 THEN 0 WHEN type_desc=''LOG'' and max_size=268435456 THEN 0 ELSE cast(max_size/128.0 as decimal(19,2)) END as MaxSizeMB FROM sys.database_files; -- sys.database_files: UPDATE Upd SET Upd.FG=F.FG, Upd.FreeSpaceMb=F.FreeSpaceMb, Upd.SizeMb=F.SizeMb, Upd.MaxSizeMB=F.MaxSizeMB FROM #FSizeAll Upd INNER JOIN @DBFiles F ON Upd.F_ID=F.F_ID WHERE Upd.DB='''+@db+N''' ; -- , sys.database_files DELETE Del FROM #FSizeAll Del LEFT JOIN @DBFiles F ON Del.F_ID=F.F_ID WHERE Del.DB='''+@db+N''' AND F.F_ID IS NULL ;'; EXEC sp_executesql @Str; FETCH NEXT FROM Db INTO @db END CLOSE Db; DEALLOCATE Db; -- : . -- ( ).
IF @details=0 IF @GetFreeSpaceMb=0 select distinct @Servername as SQLServerName, DBSize.DB, DBSize.DBCreateDate as CreateDate, DBSize.DBState as [State], DBSize.DBReadOnly as [ReadOnly], ISNULL(Mrr.mirroring_role_desc,'') as MirroringRole, cast(SUM(SizeMB) OVER (PARTITION BY DBSize.[DBID])/1024.0 as decimal(12,3)) AS AllSizeGb, DBSize.DBRecoveryModel as RecoveryModel, DBSize.FType as FilesType, SUM(DBSize.SizeMb) OVER (PARTITION BY DBSize.[DBID], DBSize.FType ) AS SizeMb, SUM(DBSize.FreeSpaceMB) OVER (PARTITION BY DBSize.[DBID], DBSize.FType ) AS FreeSpaceMB, CASE WHEN MIN(DBSize.MaxSizeMB) OVER (PARTITION BY DBSize.[DBID], DBSize.FType )=0.00 THEN 0.00 ELSE SUM(DBSize.MaxSizeMB) OVER (PARTITION BY DBSize.[DBID], DBSize.FType ) END AS MaxSizeMB, DBSize.LogBusy, DBSize.DBOwner from #FSizeAll as DBSize left join sys.database_mirroring Mrr ON Mrr.database_id=DBSize.[DBID] and mirroring_guid is not null order by DBSize.DB,DBSize.FType ELSE BEGIN Select distinct CAST((SUM(FreeSpaceMb) over (partition by DB, FType))/1024.00 as numeric(19,2)) as FreeSpaceGb From #FSizeAll Where FType='ROWS'; IF @ReturnNum is not null Select TOP 1 @ReturnNum=CAST((SUM(FreeSpaceMb) over (partition by DB, FType))/1024.000 as numeric(19,3)) From #FSizeAll Where FType='ROWS'; END ELSE select distinct @Servername as SQLServerName, DBSize.DB, DBSize.DBCreateDate AS CreateDate, DBSize.DBState AS [State], DBSize.DBReadOnly AS [ReadOnly], ISNULL(Mrr.mirroring_role_desc,'') as MirroringRole, cast(SUM(SizeMB) OVER (PARTITION BY DBSize.[DBID])/1024.0 as decimal(12,3)) AS AllSizeGb, DBSize.DBRecoveryModel AS RecoveryModel, DBSize.FType as FilesType, DBSize.FG, DBSize.FName, DBSize.FState, SUM(DBSize.SizeMb) OVER (PARTITION BY DBSize.[DBID], DBSize.FName ) AS SizeMb, SUM(DBSize.FreeSpaceMB) OVER (PARTITION BY DBSize.[DBID], DBSize.FName ) AS FreeSpaceMB, SUM(DBSize.MaxSizeMB) OVER (PARTITION BY DBSize.[DBID], DBSize.FName ) AS MaxSizeMB, DBSize.LogBusy, DBSize.DBOwner from #FSizeAll as DBSize left join sys.database_mirroring Mrr ON Mrr.database_id=DBSize.[DBID] and mirroring_guid is not null order by DBSize.DB,DBSize.FG,DBSize.FName END

/* ============================================= -- Author: (sqland1c) -- Create date: 09.09.2015 (1.0) -- Description: Logical Disk xEvents xe_DiskInfo ( 15 .) xEvents xe_DiskInfo . ( free_megabytes cur_disk_queue_len) xEvents (Logical Disk) ( ) ( 4 ) free_megabytes cur_disk_queue_len ( )
-- Update: 23.09.2015 (1.03) Extended Events "xe_DiskInfo" ! NULL - xEvents . 28.09.2015 (1.04) xEvents [xe_DiskInfo] usp_pfc_collect, . xml % numeric(19,2) numeric(38,2). 19.10.2015 (1.05) : (.. ). -- ============================================= */ CREATE PROCEDURE info.usp_get_DiskMetr
AS

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 04.08.2014
-- Description:	              (  @BackupFullID).
				 ,     . ,         LSE 
				  :     ,  ,   @FilterBackupID.
				
-- Update:		21.10.2015 (1.03)
				  @top -      .
				   @GetBackupFile -      BackupFile     (    ).
-- ============================================= */
CREATE PROCEDURE info.usp_GetChainLogs
	@DBName NVARCHAR(300),
	@BackupFullID INT = NULL,
	@FilterBackupID INT = NULL,
	@top INT = NULL,
	@ToDate DATETIME2(2) = NULL,
	@fromcopy bit = 0,
	@GetBackupFile bit = 0
AS

BEGIN set nocount ON; declare @Temp TABLE (Drive varchar(5),MB_Free decimal(12,3));
insert into @Temp exec xp_fixeddrives
IF @GetMaxFree=0 select Drive, cast (MB_Free / 1024.000 as decimal (12,3)) as GB_Free from @Temp where (@DiskFilter is null OR Drive=@DiskFilter) ELSE SELECT TOP 1 @MaxFreeDrive=Drive FROM @Temp WHERE MB_Free= (SELECT MAX(MB_Free) FROM @Temp); END

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 18.02.2014 (1.0)
-- Description:	                !
				
-- Update:		
				06.04.2014 (1.01)
				  @GetMaxFree -   1,     @MaxFreeDrive   ,
				    .
				17.09.2015 (1.02)
				  @DiskFilter       (   1  )
-- ============================================= */
CREATE PROCEDURE info.usp_GetDrives 
	@GetMaxFree bit = 0, 
	@MaxFreeDrive char(1)=NULL OUTPUT,
	@DiskFilter varchar(1)=NULL
AS

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 03.08.2016 (1.0)
-- Description:	 ,               !
				     (info.usp_GetDrives)    
				  ( ,   ).
-- Update:		07.11.2016 (1.050)
				   @DiskFilter -     ().
-- ============================================= */
CREATE PROCEDURE info.usp_GetDrives2 
	@Details bit=1,
	@DiskFilter varchar(1)=NULL
AS

BEGIN
	set nocount on;
	if is_srvrolemember('sysadmin')=1
	begin
		declare @output table (line varchar(255));
		insert @output(line) 
		exec xp_cmdshell 'powershell "[System.Net.Dns]::GetHostName()"';
		select top 1 @Hostname=[line] from @output where [line] is not null;
	end
	else
		set @Hostname=cast(serverproperty('MachineName') as nvarchar(255));
	set @Instancename=COALESCE( cast(SERVERPROPERTY('InstanceName') as nvarchar(255)),'');
	set @Servername=COALESCE(@Hostname,'')+CASE WHEN @InstanceName > '' THEN '\' ELSE '' END +@Instancename;
END

	/* =============================================
	-- Author:		  (sqland1c)
	-- Create date: 23.02.2018
	-- Description:	     (Hostname).  Full computername,         15 ,      (  netbios computername)!

	-- Update:		23.03.2018 (1.010)
					      sysadmin.  ,       (  xp_cmdshell).
	-- ============================================= */
	CREATE PROC [info].[usp_GetHostname] 
		@Hostname nvarchar(255)='' OUTPUT,
		@Instancename nvarchar(255)='' OUTPUT,
		@Servername nvarchar(510)='' OUTPUT
	AS

BEGIN
	SET NOCOUNT ON;
	DECLARE @T TABLE (Output NVARCHAR(300));
	DECLARE @str NVARCHAR(20);
	INSERT INTO @T
	EXEC xp_cmdshell 'IPCONFIG';
	SELECT TOP 1 @str=SUBSTRING(Output,CHARINDEX(':',Output)+1,20) FROM @T
	WHERE Output LIKE '   IPv4%'
	SELECT LTRIM(@str) as IP;
END

BEGIN
	SET NOCOUNT ON;
	SET DATEFORMAT YMD;
	DECLARE @GD DATETIME2(0)=SYSDATETIME(), @Num TINYINT;
	declare @SQLServer nvarchar(510);
	declare @cmd nvarchar(max);
	exec sputnik.info.usp_GetHostname @Servername=@SQLServer OUT;

	IF @DateStart IS NULL AND @DateEnd IS NULL
	BEGIN
		SET @DateEnd=@GD;
		SET @DateStart=DATEADD(HOUR,-@DateLastNHours,@GD);
	END
	ELSE
	BEGIN
		IF @Convert_tz_InFilters=1
		BEGIN
			SET @DateEnd=COALESCE(DATEADD(minute,datepart(TZoffset,SYSDATETIMEOFFSET())-@tt_tz_min,@DateEnd),@GD);
			SET @DateStart=COALESCE(DATEADD(minute,datepart(TZoffset,SYSDATETIMEOFFSET())-@tt_tz_min,@DateStart),CAST('1900-01-01' AS DATETIME2(0)));
		END 				
		ELSE
		BEGIN
			SET @DateEnd=COALESCE(@DateEnd,@GD);
			SET @DateStart=COALESCE(@DateStart,CAST('1900-01-01' AS DATETIME2(0)));	
		END
	END
	DECLARE @TLogNum TABLE ([Archive #] TINYINT, [Date] DATETIME2(0), [Log File Size (Byte)] bigint);
	INSERT INTO @TLogNum EXEC sys.xp_enumerrorlogs;
	DECLARE N CURSOR FOR
	SELECT [Archive #] as Num
	FROM @TLogNum
	WHERE [Date]>=(SELECT COALESCE(MAX([Date]),@DateStart) FROM @TLogNum WHERE [Date]<=@DateStart)
		AND [Date]<=(SELECT COALESCE(MIN([Date]),@DateEnd) FROM @TLogNum WHERE [Date]>=@DateEnd);
	OPEN N;
	FETCH NEXT FROM N INTO @Num;
	IF OBJECT_ID('tempdb.dbo.#TLog') IS NOT NULL
		DROP TABLE #TLog;
	CREATE TABLE #TLog (LogDate DATETIME, ProccessInfo VARCHAR(50),[Text] NVARCHAR(4000));
	WHILE @@FETCH_STATUS=0
	BEGIN
		IF @FilterOR=0
			INSERT INTO #TLog
			EXEC master.dbo.xp_readerrorlog @Num, 1, @Filter1, @Filter2, @DateStart, @DateEnd;
		ELSE
		BEGIN
			INSERT INTO #TLog
			EXEC master.dbo.xp_readerrorlog @Num, 1, @Filter1, null, @DateStart, @DateEnd;
			INSERT INTO #TLog
			EXEC master.dbo.xp_readerrorlog @Num, 1, @Filter2, null, @DateStart, @DateEnd;		
		END	
		FETCH NEXT FROM N INTO @Num;
	END
	CLOSE N;
	DEALLOCATE N;

	SET @cmd = '
	SELECT TOP ('+cast(@TOP as varchar(20))+') 
		'''+@SQLServer+''' AS ServerName, 
		'+CASE 

			WHEN (@GroupByText=0 OR @GroupByText IS NULL) AND @Convert_tz_InResults=1 THEN 'ISNULL(DATEADD(minute,'+cast(@tt_tz_min as varchar(20))+'-datepart(TZoffset,SYSDATETIMEOFFSET()),LogDate),LogDate)'
			WHEN (@GroupByText=0 OR @GroupByText IS NULL) AND @Convert_tz_InResults<>1 THEN 'LogDate'
			WHEN (@GroupByText<>0 AND @GroupByText IS NOT NULL) AND @Convert_tz_InResults=1 THEN 'max(ISNULL(DATEADD(minute,'+cast(@tt_tz_min as varchar(20))+'-datepart(TZoffset,SYSDATETIMEOFFSET()),LogDate),LogDate)) over (partition by [Text])'
			WHEN (@GroupByText<>0 AND @GroupByText IS NOT NULL) AND @Convert_tz_InResults<>1 THEN 'Max(LogDate) over (partition by [Text])'
		END +' AS LogDate,
		'+CASE 

			WHEN (@GroupByText=0 OR @GroupByText IS NULL) AND @Convert_tz_InResults=1 THEN 'CONVERT(VARCHAR(23),ISNULL(DATEADD(minute,'+cast(@tt_tz_min as varchar(20))+'-datepart(TZoffset,SYSDATETIMEOFFSET()),LogDate),LogDate),121)'
			WHEN (@GroupByText=0 OR @GroupByText IS NULL) AND @Convert_tz_InResults<>1 THEN 'CONVERT(VARCHAR(23),LogDate,121)'
			WHEN (@GroupByText<>0 AND @GroupByText IS NOT NULL) AND @Convert_tz_InResults=1 THEN 'CONVERT(VARCHAR(23),max(ISNULL(DATEADD(minute,'+cast(@tt_tz_min as varchar(20))+'-datepart(TZoffset,SYSDATETIMEOFFSET()),LogDate),LogDate)) over (partition by [Text]),121)'
			WHEN (@GroupByText<>0 AND @GroupByText IS NOT NULL) AND @Convert_tz_InResults<>1 THEN 'CONVERT(VARCHAR(23),Max(LogDate) over (partition by [Text]),121)'
		END +' AS LogDate_str,
		'+CASE 
			WHEN (@GroupByText=0 OR @GroupByText IS NULL) THEN 'ProccessInfo,'
			ELSE 'COUNT(*) over (partition by [Text]) as ProccessInfo,'
		END+' 
		'+case
			when @WithRowNum=1 and (@GroupByText=0 OR @GroupByText IS NULL) then 'ROW_NUMBER() OVER (PARTITION BY CONVERT(VARCHAR(19),LogDate,121),ProccessInfo ORDER BY LogDate DESC) AS RowNum' 
			else 'NULL AS RowNum' 
		end+', 
		[Text]
	FROM #TLog
	WHERE 1=1
		'+CASE WHEN @FilterNotLike IS NULL THEN '' ELSE 'AND [Text] NOT LIKE '''+@FilterNotLike+'''' END+'
		'+CASE WHEN @FilterSource IS NULL THEN '' ELSE 'AND [ProccessInfo] LIKE '''+@FilterSource+'''' END+'
		'+CASE WHEN @FilterSourceNotLike IS NULL THEN '' ELSE 'AND [ProccessInfo] NOT LIKE '''+@FilterSourceNotLike+'''' END+' 
	ORDER BY LogDate '+@SortAZ+';';	
	--PRINT (@cmd);
	EXEC (@cmd);
END

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 15.03.2015
-- Description:	      SQL Server.
				        ().
				  !
				@Filter1  @Filter2 -       !
				@FilterOR -  ,   @Filter1  @Filter2   OR (),  AND ().
				@FilterNotLike -        (  %).
				@DateStart  @DateEnd -       (       ).
				@DateLastNHours -      (    @DateLastNHours ).
				@TOP -     -  (  4000).
				       .
-- Update:		20.03.2015 (1.05)
				   @FilterSource  @FilterSourceNotLike    
				  ( ProccessInfo).      (  ).
				25.03.2015 (1.10)
				      ROW_NUMBER     (     ).
				       1  (  ,    1).
				,       !
				   @WithRowNum,     ROW_NUMBER  .   ,        ROW_NUMBER.
				19.01.2016 (1.15)
				  : @tt_tz_min -     ,      (,   ).
				@Convert_tz_InFilters -     @DateStart  @DateEnd          (    @tt_tz_min).
				@Convert_tz_InResults -  ,     LogDate,            @tt_tz_min .
				19.10.2017 (1.200)
				  : @GroupByText -    .
				 .     #TLog.
				  ServerName    :    +   ().
				13.03.2018 (1.202)
				     SQL    info.usp_getHostname	
				31.01.2019 (1.250)
				   @SortAZ-   .
				    :        sql.
-- ============================================= */
CREATE PROCEDURE info.usp_GetSqlLog
	@Filter1 NVARCHAR(200) = NULL,
	@Filter2 NVARCHAR(200) = NULL,
	@FilterOR BIT = 0,
	@FilterNotLike NVARCHAR(200) = NULL,
	@FilterSource NVARCHAR(75) = NULL,
	@FilterSourceNotLike NVARCHAR(75) = NULL,
	@DateStart DATETIME2(0) = NULL,
	@DateEnd DATETIME2(0) = NULL,
	@DateLastNHours SMALLINT = 6,
	@TOP INT = 4000,
	@WithRowNum BIT=0,
	@tt_tz_min SMALLINT = NULL,
	@Convert_tz_InFilters BIT=1,
	@Convert_tz_InResults BIT=1,
	@GroupByText BIT=0,
	@SortAZ NVARCHAR(10) = 'DESC'
AS

BEGIN SET NOCOUNT ON; IF @GetStatus=1 BEGIN IF @JobName is null BEGIN SELECT ' , @JobName' as RUN_STATUS; RETURN; END SET @Activity=1; END IF @Lite=1 AND @Activity=0 SET @Activity=1;
IF @Activity=0 BEGIN SELECT SERVERPROPERTY('ServerName') as SQLServerName, J.name as Job, H.step_name as Step, H.[message] as Info, CASE H.run_status WHEN 0 THEN 'Failed' WHEN 1 THEN 'Successfully' WHEN 2 THEN 'Re-run' WHEN 3 THEN 'Canceled by user' END AS RUN_STATUS, MSDB.dbo.agent_datetime(run_date, run_time) AS [DateTimeRun], Duration=STUFF(STUFF(RIGHT('000000' + CONVERT(VARCHAR(6),H.run_duration),6 ),3,0,':'),6,0,':') FROM ( SELECT job_id,name FROM msdb.dbo.sysjobs WHERE [enabled]=1 AND name<>'syspolicy_purge_history' ) J INNER JOIN msdb.dbo.sysjobhistory H ON J.job_id=H.job_id and H.run_status IN (0,3) -- . and step_id>0 WHERE DATEDIFF(day,convert(datetime2(2),cast(H.run_date as varchar(8))),SYSDATETIME())<=1 -- END ELSE BEGIN --Job Activity Monitor DECLARE @TJ TABLE (SQLServerName varchar(128), Job nvarchar(2000), Step nvarchar(2000), Info nvarchar(max), RUN_STATUS varchar(50), [DateTimeRun] datetime, Duration varchar(100), Duration_min numeric(19,2)); WITH TT AS ( SELECT J.name as Job, J.job_id, J.[enabled], H.Step_name as Step, H.[message] as Info, H.run_status, MSDB.dbo.agent_datetime(run_date, run_time) AS [DateTimeRun], H.run_duration FROM ( SELECT job_id,name,[enabled] FROM msdb.dbo.sysjobs WHERE name<>'syspolicy_purge_history' --and [enabled]=1 AND (name LIKE @JobName OR @JobName is null) ) J LEFT JOIN msdb.dbo.sysjobhistory H ON J.job_id=H.job_id and step_id>0 AND (H.run_status BETWEEN 0 AND 3) ) INSERT INTO @TJ (SQLServerName,Job,Step,Info,RUN_STATUS,[DateTimeRun],Duration,Duration_min) SELECT DISTINCT CAST(SERVERPROPERTY('ServerName') as varchar(128)) as SQLServerName, GMAX.Job, CASE WHEN ja.job_id IS NOT NULL THEN ja.step_name ELSE DET.Step END AS Step, CASE WHEN ja.job_id IS NOT NULL THEN NULL ELSE DET.Info END AS Info, CASE WHEN ja.job_id IS NOT NULL THEN 'Running' WHEN DET.run_status=0 THEN 'Failed' WHEN DET.run_status=1 THEN 'Successfully' WHEN DET.run_status=2 THEN 'Re-run' WHEN DET.run_status=3 THEN 'Canceled by user' END AS RUN_STATUS, CASE WHEN ja.job_id IS NOT NULL THEN ja.start_execution_date ELSE DET.DateTimeRun END AS [DateTimeRun], CASE WHEN ja.job_id IS NOT NULL THEN RIGHT('00'+CAST ((DATEDIFF(SECOND, ja.start_execution_date, SYSDATETIME())/3600) AS VARCHAR (2)),2) +':'+ RIGHT('00'+CAST ((DATEDIFF(SECOND, ja.start_execution_date, SYSDATETIME()) % 3600) / 60 AS VARCHAR(2)),2) +':'+ RIGHT('00'+CAST (((DATEDIFF(SECOND, ja.start_execution_date, SYSDATETIME()) % 3600) % 60 ) % 60 AS VARCHAR(2)),2) ELSE STUFF(STUFF(RIGHT('000000' + CONVERT(VARCHAR(6),DET.run_duration),6 ),3,0,':'),6,0,':') END AS Duration, CASE WHEN ja.job_id IS NOT NULL THEN CAST((CAST(DATEDIFF(SECOND, ja.start_execution_date, SYSDATETIME()) AS NUMERIC(19,2))) / 60 AS NUMERIC(19,2)) ELSE CAST(((DET.run_duration/10000) * 60) as numeric(19,2))+ --Hours CAST((DET.run_duration/100%100 ) as numeric(19,2))+ --Mins CAST((DET.run_duration%100/60.00) as numeric(19,2)) --Secs END AS Duration_min FROM ( SELECT job_id, Step, [enabled], Job, MAX(DateTimeRun) AS LastDateTimeRun FROM TT GROUP BY job_id, Step, [enabled], Job )GMAX LEFT JOIN TT DET ON GMAX.job_id=DET.job_id AND GMAX.Step=DET.Step AND GMAX.LastDateTimeRun=DET.DateTimeRun -- Job : LEFT JOIN ( SELECT ja.job_id, ja.start_execution_date, -- ISNULL(last_executed_step_id,0)+1 AS current_executed_step_id, ja.last_executed_step_date, Js.step_name FROM msdb.dbo.sysjobactivity ja LEFT JOIN msdb.dbo.sysjobhistory jh ON ja.job_history_id = jh.instance_id JOIN msdb.dbo.sysjobsteps js ON ja.job_id = js.job_id AND ISNULL(ja.last_executed_step_id,0)+1 = js.step_id WHERE ja.session_id = (SELECT TOP 1 session_id FROM msdb.dbo.syssessions ORDER BY agent_start_date DESC) AND start_execution_date is not null AND stop_execution_date is null )ja ON GMAX.job_id=ja.job_id WHERE ((GMAX.[enabled]=1 AND DET.run_status IS NOT NULL) OR ja.job_id IS NOT NULL) OR (@OnlyEnabled=0)
IF @GetStatus=1 SELECT TOP 1 RUN_STATUS FROM @TJ WHERE Job LIKE @JobName; ELSE IF @Lite=1 SELECT Job, Step, RUN_STATUS, Duration_min FROM @TJ; ELSE SELECT SQLServerName,Job,Step,Info,RUN_STATUS,[DateTimeRun],Duration FROM @TJ;
END END

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 02.06.2014
-- Description:	
			       (Job) SQL Server Agent 
			  :     @Activity ( ),         @OldDay  (  1);
			   @Activity=1,      Job Activity Monitor.
-- Update:
				03.06.2014 (1.01)
				    ().     Duration.
				04.06.2014 (1.02)
				   [message]  Alias Info.
				15.08.2014 (1.1)
				  @Activity=1  ,      Job    !
				 ,    ,    Duration  ,    !
				   [DateTimeRun]     !
				    Job Activity Monitor!
				19.08.2014 (1.12)
				      Job Activity Monitor.
				22.08.2014 (1.13)
				   DISTINCT      Job Activity Monitor.
				  ,            (Step).
				11.08.2015 (1.20)
				        
				19.01.2017 (1.300)
				   @JobName -    .
				   @GetStatus -   1  RUN_STATUS.    @JobName   !
				19.04.2017 (1.316)
				  @JobName     %% (..  LIKE).
				   @Lite.  4 : Job, Step, RUN_STATUS, Duration_min.    @Activity=1.
				   @OnlyEnabled.   1.    Jobs.   0,     (  ).
-- ============================================= */
CREATE PROCEDURE info.usp_JobMonitor
	@OldDay tinyint = 1,
	@Activity bit = 0,
	@JobName nvarchar(2000)=null,
	@GetStatus bit=0,
	@Lite bit=0,
	@OnlyEnabled bit=1
AS

/* =============================================
-- Author:		  (sqland1c)
-- Create date: 03.08.2015
-- Description:	    . 
				        (     @interval_sec)!
				 @interval_sec=0,      .
				     awr  sputnik,    dmv!
				    REads, Duration  Text .
				 zabbix  ,   -     .
				   @Kill  ,      
				(   Messages   KILL   ).
-- Update:		17.09.2015 (1.02)
				  @get_runtime_metr        max, all, top5, avg.
				27.01.2016 (1.03)
				  -  OLAP .
				10.03.2016 (1.04)
				 -      ,   .
				 IF @interval_sec=0 -     (tt=@last_tt)
				  -   ! 
-- ============================================= */
CREATE PROCEDURE info.usp_SQLMon
	@Filter_Reads INT = 100000,
	@Filter_DurSec INT = 30,
	@Filter_Text NVARCHAR(500) = NULL,
	@interval_sec SMALLINT = 60,
	@zabbix BIT = 0,
	@kill BIT = 0,
	@get_runtime_metr BIT = 0
AS

BEGIN
	set nocount on;
	select [vl] from sputnik.info.vtempusing where pr='internal_mb';
END

BEGIN
	set nocount on;
	select [vl] from sputnik.info.vtempusing where pr='rowver_mb';
END

BEGIN
	set nocount on;
	select [vl] from sputnik.info.vtempusing where pr='total_mb';
END

BEGIN
	set nocount on;
	select [vl] from sputnik.info.vtempusing where pr='user_mb';
END

SET NOCOUNT ON;
if @pp=1 and @execute=0--and @DBName is null
begin

	declare @DlgHandle UNIQUEIDENTIFIER, @MsgRequest XML;
	declare @tt table (db nvarchar(300));
	declare @count_q_pp int, @dbname_cur nvarchar(300);
	--     
	insert into @tt
	select distinct DBNameTarget as db
	from sputnik.lse.TargetConfig
	where [suspend]=0 AND [InitDate] is not null
		AND (DBNameTarget=@DBName OR @DBName IS NULL);

	--  ,    :
	-- ,    usp_ExecProcessor    
	IF NOT EXISTS(select queue_id from sys.dm_broker_activated_tasks where procedure_name='[pp].[usp_ExecProcessor]')
	BEGIN			
		--       (      )!
		insert into @tt
		select TOP 1 DBNameTarget as db
		from sputnik.lse.TargetConfig
		where [suspend]=0 AND [InitDate] is null
			AND (DBNameTarget=@DBName OR @DBName IS NULL);
	END

	if exists(select top 1 db from @tt)
	begin
		declare LSE cursor for
		select cast('<DB>'+db+'</DB>' as xml) as DB,
			db as DBname
		from @tt;
		--   LSE!
		BEGIN DIALOG CONVERSATION @DlgHandle
			FROM SERVICE [pp_Commander]
			TO SERVICE N'pp_Processor'
			ON CONTRACT [pp_lse_Contract]
			WITH ENCRYPTION = OFF;
		open LSE;
		fetch next from LSE into @MsgRequest, @dbname_cur;
		while @@FETCH_STATUS=0
		begin
			--29.03.2016  :       !
			--     ,      !
			;WITH XMLNAMESPACES
			('http://pecom.ru/pegasExchange/types/' as ns),
			Q AS 
			(
				select CAST(message_body as XML) as msg_xml
				from pp.ProcessorQueue
				where message_type_name='pp_lse_Request'
			)
			select @count_q_pp=count(*)
			from Q
			where msg_xml.exist(N'(/db/text()[.=sql:variable("@dbname_cur")])')=1;

			--30.03.2016   -     
			-- Service Broker    
			if OBJECT_ID('tempdb..#broker_tasks') is not null
				DROP TABLE #broker_tasks;
			select 
				br_t.spid, s.login_time, s.status as Session_Status, s.open_transaction_count as Open_tran_cnt, DB_NAME(s.database_id) as DB,
				r.status as command_status, r.command as command_type, r.percent_complete as [%],
				--r.sql_handle, r.statement_start_offset, r.statement_end_offset,
				--sql_text.text as sql_text,
				(SELECT TOP 1 SUBSTRING(sql_text.text,r.statement_start_offset / 2+1 , 
				  ((CASE WHEN r.statement_end_offset = -1 THEN (LEN(CONVERT(nvarchar(max),sql_text.text)) * 2) 
					 ELSE r.statement_end_offset 
					END)  - r.statement_start_offset) / 2+1))  AS sql_statement
			INTO #broker_tasks
			from sys.dm_broker_activated_tasks br_t
			left join sys.dm_exec_sessions s
				on s.session_id = br_t.spid
			left join sys.dm_exec_requests r
				on r.session_id =s.session_id
			CROSS APPLY sys.dm_exec_sql_text(r.sql_handle) AS sql_text
			where 
				br_t.procedure_name='[pp].[usp_ExecProcessor]'
				and r.command LIKE 'RESTORE %'
			--select * from #broker_tasks
			--if not exists(select spid from #broker_tasks where sql_statement like '%^['+'Booh2014'+'^]%' ESCAPE '^')
			--	select 1;
			IF (@count_q_pp=0 OR @count_q_pp IS NULL) 
				AND not exists(select spid from #broker_tasks where sql_statement like '%^['+@dbname_cur+'^]%' ESCAPE '^')
			BEGIN
				--   !
				SEND ON CONVERSATION @DlgHandle
					MESSAGE TYPE [pp_lse_Request]
					(@MsgRequest);
			END				
			fetch next from LSE into @MsgRequest, @dbname_cur;
		end	
		close LSE;
		deallocate LSE;
	end
	return 1;
end
--      ,        .
declare @ServerSource nvarchar(300), @DBNameSource nvarchar(300), @DBNameTarget nvarchar(300), @FromCopy bit, @BackupID int, @ConfigID smallint, @CatalogFilesDB nvarchar(800), @CatalogLogFiles nvarchar(800), @StandBy_File nvarchar(500), @UseFreshDiffBack bit;
declare LSE cursor for
select distinct c.id as ConfigID, c.CatalogFilesDB, c.CatalogLogFiles,
	case when c.InitBackupHS_id<h.MaxBackupHS_id then h.MaxBackupHS_id
		else c.InitBackupHS_id
	end BackupID
from sputnik.lse.TargetConfig c
left join (select distinct config_id, max(BackupHS_id) over (partition by config_id) as MaxBackupHS_id from sputnik.lse.HS) h
	on c.id=h.config_id		
where [Suspend]=0 AND [InitDate] IS NOT NULL AND (DBNameTarget=@DBName OR @DBName IS NULL);
open LSE;
fetch next from LSE into @ConfigID, @CatalogFilesDB, @CatalogLogFiles, @BackupID;
while @@FETCH_STATUS=0
begin
	exec sputnik.lse.usp_RunRolling @ConfigID=@ConfigID, @BackupID=@BackupID, @MoveFilesTo=@CatalogFilesDB, @MoveLogFilesTo=@CatalogLogFiles, @pp=@pp;
	fetch next from LSE into @ConfigID, @CatalogFilesDB, @CatalogLogFiles, @BackupID;
end
close LSE;
deallocate LSE;
--  ,   !
declare LSE cursor for
select ServerSource, DBNameSource, DBNameTarget, FromCopy, CatalogFilesDB, CatalogLogFiles, StandBy_File, COALESCE(UseFreshDiffBack,0) as UseFreshDiffBack 
from sputnik.lse.TargetConfig
where [InitDate] is null AND (DBNameTarget=@DBName OR @DBName IS NULL);
open LSE;
fetch next from LSE into @ServerSource, @DBNameSource, @DBNameTarget, @FromCopy, @CatalogFilesDB, @CatalogLogFiles, @StandBy_File, @UseFreshDiffBack;
while @@FETCH_STATUS=0
begin
	--      (    !)
	if @CatalogFilesDB is NULL and @DBNameSource is not null
	begin
		DECLARE @MaxDrive CHAR(1);
		exec sputnik.info.usp_GetDrives @GetMaxFree=1, @MaxFreeDrive=@MaxDrive OUTPUT;
		SET @CatalogFilesDB=@MaxDrive+':\DATA\lse\'+@DBNameTarget+'\';
		UPDATE sputnik.lse.TargetConfig
		SET CatalogFilesDB=@CatalogFilesDB, CatalogLogFiles=NULL 
		WHERE DBNameTarget=@DBNameTarget;
	end
	exec sputnik.backups.usp_GC2 
		@ServerSource=@ServerSource, @DBNameSource=@DBNameSource, 
		@DBNameTarget=@DBNameTarget, @FromCopy=@FromCopy, @MoveFilesTo=@CatalogFilesDB, @MoveLogFilesTo=@CatalogLogFiles,
		@NoRecovery=1,@RunNewBackIfNeed=1, @FreshBack=@UseFreshDiffBack,@lse=1, @pp=@pp, @StandBy_File=@StandBy_File, @RunNewDiffBackIfNeed=@UseFreshDiffBack;
	fetch next from LSE into @ServerSource, @DBNameSource, @DBNameTarget, @FromCopy, @CatalogFilesDB, @CatalogLogFiles, @StandBy_File, @UseFreshDiffBack;
end
close LSE;
deallocate LSE;

SET NOCOUNT ON;
DECLARE @LocalServer NVARCHAR(510);
exec sputnik.info.usp_GetHostname @Servername=@LocalServer OUT;
--         lse
declare @ServerSource nvarchar(300), @DBNameSource nvarchar(300), @DBNameTarget nvarchar(300), @FromCopy bit, @CatalogFilesDB nvarchar(800), @CatalogLogFiles nvarchar(800), @StandBy_File nvarchar(500)=null,@user_access_desc nvarchar(50), @state_desc nvarchar(100);
select  @ServerSource=ServerSource, @DBNameSource=DBNameSource, @DBNameTarget=DBNameTarget, @FromCopy=FromCopy, @CatalogFilesDB=CatalogFilesDB, @CatalogLogFiles=CatalogLogFiles, @StandBy_File=StandBy_File
from sputnik.lse.TargetConfig 
where ID=@ConfigID AND [Suspend]=0 AND [InitDate] IS NOT NULL;

--     !
DECLARE @ChainBack TABLE (BackupFile NVARCHAR(800), BackupType VARCHAR(4), ID INT, BackupDate DATETIME2(2));	
IF @ServerSource IS NULL OR @ServerSource=@LocalServer
	INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
		EXEC sputnik.info.usp_GetChainLogs @DBName=@DBNameSource, @BackupFullID=null, @FilterBackupID=@BackupID, @ToDate=null, @fromcopy=@fromcopy;
ELSE
BEGIN
	DECLARE @strP VARCHAR(300);
	SET @strP='@ToDate=null, @BackupFullID=null, @FilterBackupID='+CAST(@BackupID AS NVARCHAR(40))+', @fromcopy='+CAST(@fromcopy AS VARCHAR(1));
	INSERT INTO @ChainBack (BackupFile, BackupType, ID, BackupDate)
		EXEC ('	SELECT *
				FROM OPENQUERY(['+@ServerSource+'], '' EXEC sputnik.info.usp_GetChainLogs @DBName=N'''''+@DBNameSource+''''','+@strP+';'')
			 ');
END
--    .
declare @str nvarchar(800);
DECLARE @BF nvarchar(800), @BT varchar(4), @RestoreStart datetime2(2), @BackupLogID int, @CheckFile bit, @BackupDate datetime2(2);
DECLARE RE CURSOR FOR
	SELECT TOP (@Maxi) BackupFile, BackupType, ID, sputnik.info.uf_CheckFile(BackupFile) as CheckFile, BackupDate
	FROM @ChainBack
	--ORDER BY ID;
OPEN RE;
declare @cur_cnt int, @cur_i int = 1;
SET @cur_cnt=@@CURSOR_ROWS;
FETCH NEXT FROM RE INTO @BF, @BT, @BackupLogID, @CheckFile, @BackupDate;
WHILE @@FETCH_STATUS=0
BEGIN

	if @CheckFile=0
	begin
		if @FromCopy=1
		begin
			begin try
				SET @strP='@DBFilter=N'''''+@DBNameSource+''''', @FilterBackupID='+CAST(@BackupID AS NVARCHAR(40))+', @Force=1';
				EXEC ('
					  EXEC(''
							EXEC sputnik.backups.usp_CopyBack '+@strP+';
					  '') AT ['+@ServerSource+']
				');
			end try
			begin catch
				set @str=N'   [usp_RunRolling]:      '+@BF+' (='+CAST(@BackupID AS NVARCHAR(40))+')!    !';
				if @pp=0
					PRINT(@str);
				RAISERROR(@str,11,1) WITH LOG
			end catch
		end
		else
		begin
			set @str=N'   [usp_RunRolling]:     '+@BF+' !       SQL Server  !';
			if @pp=0
				PRINT(@str);
			RAISERROR(@str,11,1) WITH LOG
		end				
	end
	begin try
		set @RestoreStart=sysdatetime();
		IF @cur_i<@cur_cnt
			EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
				@DBNameTarget=@DBNameTarget, 
				@FromLog=@BF,
				@MoveFilesTo=@CatalogFilesDB,
				@MoveLogFilesTo=@CatalogLogFiles,
				@NoRecovery=1,
				@NoFileList=1,
				@NoStats=@pp
				--,@StandBy_File=@StandBy_File
				,@NoSetMultiUser=1
			;
		ELSE
			EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
				@DBNameTarget=@DBNameTarget, 
				@FromLog=@BF,
				@MoveFilesTo=@CatalogFilesDB,
				@MoveLogFilesTo=@CatalogLogFiles,
				@NoRecovery=1,
				@NoFileList=1,
				@NoStats=@pp
				,@StandBy_File=@StandBy_File
				,@NoSetMultiUser=1
			;
		insert into sputnik.lse.HS (config_id, BackupHS_id, StartRestore, CompleteRestore)
		values(@ConfigID, @BackupLogID, @RestoreStart, sysdatetime());
	end try
	begin catch
		--      ,      MULTI_USER
		--   StandBy
		select top 1 @user_access_desc=user_access_desc, @state_desc=state_desc 
		from sys.databases
		where name=@DBNameTarget;
		IF @user_access_desc='SINGLE_USER' AND @state_desc='ONLINE'
			EXEC('ALTER DATABASE ['+@DBNameTarget+'] SET MULTI_USER;');
		--IF @state_desc<>'ONLINE' AND @StandBy_File IS NOT NULL
		--	EXEC [sputnik].[backups].[usp_RestoreDB_simple] 
		--		@DBNameTarget=@DBNameTarget, 
		--		@NoRecovery=1,
		--		@NoFileList=1,
		--		@NoStats=@pp,
		--		@StandBy_File=@StandBy_File,
		--		@ContinueRecovery=1;	
		set @str=N'   [usp_RunRolling]:      '+@BF+'    ['+@DBNameTarget+']!  : '+ERROR_MESSAGE();
		if @pp=0
			PRINT(@str);
		RAISERROR(@str,11,1) WITH LOG;
	end catch
	set @cur_i+=1;
	FETCH NEXT FROM RE INTO @BF, @BT, @BackupLogID, @CheckFile, @BackupDate;
END
close RE;
deallocate RE;
--      MULTI_USER...   StandBy
select top 1 @user_access_desc=user_access_desc, @state_desc=state_desc 
from sys.databases
where name=@DBNameTarget;
IF @user_access_desc='SINGLE_USER' AND @state_desc='ONLINE'
	EXEC('ALTER DATABASE ['+@DBNameTarget+'] SET MULTI_USER;');

BEGIN
	SET NOCOUNT ON;
	DECLARE @errmsg NVARCHAR(600);
	IF EXISTS(select server_id from sys.servers where data_source=@ServerSource) OR @ServerSource IS NULL 
	BEGIN
		IF @ServerSource IS NULL
			SET @ServerSource=CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(500));
		DECLARE @ServerTarget nvarchar(500)=CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(500));
		IF @ForceDelete=1
		BEGIN
			declare @res bit;
			begin try
				EXEC('
							DELETE FROM ['+@ServerSource+'].sputnik.lse.SourceConfig
							WHERE ServerTarget='''+@ServerTarget+''' AND DBNameTarget='''+@DBNameTarget+''';
							--   :
							DELETE FROM ['+@ServerSource+'].sputnik.lse.SourceConfig
							WHERE ServerTarget=(select [data_source] from ['+@ServerSource+'].master.sys.servers where is_linked=0);
							DELETE FROM ['+@ServerSource+'].sputnik.lse.TargetConfig
							WHERE ServerSource=(select [data_source] from ['+@ServerSource+'].master.sys.servers where is_linked=0);
							DELETE DelTab FROM ['+@ServerSource+'].sputnik.lse.HS DelTab
							LEFT JOIN ['+@ServerSource+'].sputnik.lse.TargetConfig tc
							ON DelTab.config_id=tc.id
							WHERE tc.id IS NULL;
						');
				begin tran
					DELETE 
					FROM [lse].[hs]
					WHERE config_id IN (SELECT id FROM [lse].[TargetConfig] WHERE DBNameTarget=@DBNameTarget);
					DELETE 
					FROM [lse].[TargetConfig]
					WHERE DBNameTarget=@DBNameTarget;
					--   :
					DELETE FROM lse.SourceConfig
					WHERE ServerTarget=(select [data_source] from sys.servers where is_linked=0);
					DELETE FROM lse.TargetConfig
					WHERE ServerSource=(select [data_source] from sys.servers where is_linked=0);
					DELETE DelTab FROM lse.HS DelTab
					LEFT JOIN lse.TargetConfig tc
					ON DelTab.config_id=tc.id
					WHERE tc.id IS NULL;

				commit tran;
				set @res=1;
			end try
			begin catch
				IF @@TRANCOUNT > 0 
					rollback;
				set @res=0;
				set @errmsg=N'     lse. : '+ERROR_MESSAGE();
				print(@errmsg);
			end catch
			return @res;
		END;
		declare @t table (id smallint);
		INSERT INTO @t (id)
		SELECT id FROM sputnik.lse.TargetConfig WHERE DBNameTarget=@DBNameTarget
		IF EXISTS(select id from @t)
		BEGIN
			begin try
				IF @ReInit=1
				begin
					UPDATE sputnik.lse.TargetConfig
					SET InitDate=NULL, InitBackupHS_id=NULL, [Suspend]=0, UseFreshDiffBack=@UseFreshDiffBack
					WHERE DBNameTarget=@DBNameTarget;
					IF @CatalogFilesDB IS NOT NULL
						UPDATE sputnik.lse.TargetConfig
						SET CatalogFilesDB=@CatalogFilesDB,
							CatalogLogFiles=@CatalogLogFiles
						WHERE DBNameTarget=@DBNameTarget;
				end
				IF @FromCopy IS NOT NULL
					UPDATE sputnik.lse.TargetConfig
					SET FromCopy=@FromCopy
					WHERE DBNameTarget=@DBNameTarget;
				IF @Suspend IS NOT NULL
					UPDATE sputnik.lse.TargetConfig
					SET [Suspend]=@Suspend
					WHERE DBNameTarget=@DBNameTarget;	
				IF @StandBy_File IS NOT NULL
					UPDATE sputnik.lse.TargetConfig
					SET [StandBy_File]=@StandBy_File
					WHERE DBNameTarget=@DBNameTarget;
			end try
			begin catch
				set @errmsg=N'    lse (ReInit). : '+ERROR_MESSAGE();
				print(@errmsg);
				return 0;
			end catch
		END;
		ELSE
		BEGIN
			begin try
				exec('
						DELETE FROM ['+@ServerSource+'].sputnik.lse.SourceConfig
							WHERE ServerTarget='''+@ServerTarget+''' AND DBNameTarget='''+@DBNameTarget+''';
						INSERT INTO ['+@ServerSource+'].sputnik.lse.SourceConfig (ServerTarget, DBNameSource, DBNameTarget)
							VALUES ('''+@ServerTarget+''', '''+@DBNameSource+''' ,'''+@DBNameTarget+''');
				');
				INSERT INTO sputnik.lse.TargetConfig (ServerSource, DBNameSource, DBNameTarget, FromCopy, CatalogFilesDB, CatalogLogFiles, [StandBy_File], UseFreshDiffBack)
				VALUES (@ServerSource, @DBNameSource,@DBNameTarget,@FromCopy, @CatalogFilesDB, @CatalogLogFiles, @StandBy_File, @UseFreshDiffBack);
			end try
			begin catch
				set @errmsg=N'     lse. : '+ERROR_MESSAGE();
				print(@errmsg);
				return 0;
			end catch
		END	
		return 1;
	END;
	ELSE
		return -1;
END

	/* =============================================
	-- Author:		  (sqland1c)
	-- Create date: 04.08.2014 (1.0)
	-- Description:     Log Shipping Easy ( ,    ).
					: @ServerSource -    (   ), 
					@DBNameSource -   , 
					@DBNameTarget -    (     ).
					@FromCopy -       :       .
					@ForceDelete -  1,    !
					@ReInit -   ,        (      ).
					@CatalogFilesDB -   ()     .
					@Suspend -  /.   1,          .

					 :   ,        Linked Server   Primary!
					        Linked Server!     Linked Server -  LSE   
					   SQL Server!
					  @ForceDelete -    ,      .
					     (  ).       FromCopy  .     
					@ReInit=1 ,     (     [TargetConfig].
					      1.
	-- Update:		
					08.10.2014 (1.05)
					         print!        :
					     -,   .
					    lse  :      -,   .
					          ROLLBACK (@@TRANCOUNT).
					      - (    ).
					19.03.2015 (1.06)
					      @StandBy_File -   StandBy (read-only)   .
					25.07.2016 (1.07)
					      @CatalogLogFiles -    -   .
					01.02.2017 (1.075)
					            .
					13.11.2017 (1.080)
					   @UseFreshDiffBack -    ""  .,      .,    !
	-- ============================================= */
	CREATE PROCEDURE [lse].[usp_SetLseConf]
		@ServerSource nvarchar(300) = null,
		@DBNameSource nvarchar(500) = null,
		@DBNameTarget nvarchar(500),
		@FromCopy bit = null,
		@ForceDelete bit = 0,
		@ReInit bit = 0,
		@CatalogFilesDB nvarchar(800) = null,
		@CatalogLogFiles nvarchar(800) = null,
		@Suspend bit = null,
		@StandBy_File nvarchar(600)=null,
		@UseFreshDiffBack bit = 1
	AS

SET NOCOUNT ON;
DECLARE @DlgHandle UNIQUEIDENTIFIER, @Msg VARBINARY(MAX), @MsgType sysname, @StrErr NVARCHAR(3000);
WHILE (1=1)
BEGIN
	 WAITFOR
		(	RECEIVE TOP(1)
				@DlgHandle = conversation_handle,
				@MsgType   = message_type_name,
				@Msg	   = message_body
			FROM [pp].[CommanderQueue]
	), TIMEOUT 1000;		--   1 .  .
	IF (@@ROWCOUNT = 0)
	BEGIN
		IF EXISTS(SELECT conversation_id FROM sys.conversation_endpoints WHERE conversation_handle=@DlgHandle AND state<>'CD')
			END CONVERSATION @DlgHandle;
		BREAK;
	END
	--     ,    !
	IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
		END CONVERSATION @DlgHandle;
	ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
		END CONVERSATION @DlgHandle;
END

SET NOCOUNT ON;
DECLARE @DlgHandle UNIQUEIDENTIFIER, @Msg VARBINARY(MAX), @MsgType sysname, @StrErr NVARCHAR(3000),@RunningBackupCount smallint;
WHILE (1=1)
BEGIN
	 DECLARE @DB NVARCHAR(300);

	 WAITFOR
		(	RECEIVE TOP(1)
				@DlgHandle = conversation_handle,
				@MsgType   = message_type_name,
				@Msg	   = message_body
			FROM [pp].ProcessorQueue
	), TIMEOUT 1000;		--   1 .  .
	IF (@@ROWCOUNT = 0)
	BEGIN
		IF EXISTS(SELECT conversation_id FROM sys.conversation_endpoints WHERE conversation_handle=@DlgHandle AND state<>'CD')
			END CONVERSATION @DlgHandle;
		BREAK;
	END
	--  lse   !
	IF @MsgType='pp_lse_Request'
	BEGIN
		begin try
			--         (  XML).
			SET	@DB = CAST(@Msg as XML).value('(/DB)[1]', 'NVARCHAR(300)');
			--   [lse].[usp_RollForwardRecovery]
			--       @pp=1 (     )
			--    @execute=1
			EXEC [sputnik].[lse].[usp_RollForwardRecovery] @DBName=@DB, @pp=1, @execute=1;
		end try
		begin catch
			SET @StrErr=N'   [lse].[usp_RollForwardRecovery]   [pp].usp_ExecProcessor.  : '+ERROR_MESSAGE();
			END CONVERSATION @DlgHandle WITH ERROR = 10 DESCRIPTION=@StrErr;
		end catch
	END
	--  backups   !
	IF @MsgType='pp_back_Request'
	BEGIN
		begin try
			DECLARE @TypeBack VARCHAR(4); -- !
			--            (  XML).
			SET	@DB = CAST(@Msg as XML).value('(/backups/db)[1]', 'NVARCHAR(300)');
			SET	@TypeBack = CAST(@Msg as XML).value('(/backups/type)[1]', 'VARCHAR(4)');
			--     [usp_StartBackup]   
			--  @pp=1 -       ! 
			EXEC [sputnik].[backups].[usp_StartBackup] @DBFilter=@DB, @type=@TypeBack, @pp=1;
		end try
		begin catch
			SET @StrErr=N'   [backups].[usp_StartBackup]   [pp].usp_ExecProcessor.  : ['+@DB+'];  : ['+@TypeBack+'].  : '+ERROR_MESSAGE();
			--       ,       !
			--       SQL Server ( print)    (break):
			PRINT(@StrErr);
			BREAK;
			--END CONVERSATION @DlgHandle WITH ERROR = 10 DESCRIPTION=@StrErr;
		end catch
	END
	--     ,   !
	ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
		END CONVERSATION @DlgHandle;
	ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
		END CONVERSATION @DlgHandle;
END

SET NOCOUNT ON;
DECLARE @DlgHandle UNIQUEIDENTIFIER, @Msg VARBINARY(MAX), @MsgType sysname, @StrErr NVARCHAR(3000),@RunningBackupCount smallint;
WHILE (1=1)
BEGIN
	 DECLARE @DB NVARCHAR(300);

	 WAITFOR
		(	RECEIVE TOP(1)
				@DlgHandle = conversation_handle,
				@MsgType   = message_type_name,
				@Msg	   = message_body
			FROM [pp].ProcessorQueueHard
	), TIMEOUT 1000;		--   1 .  .
	IF (@@ROWCOUNT = 0)
	BEGIN
		IF EXISTS(SELECT conversation_id FROM sys.conversation_endpoints WHERE conversation_handle=@DlgHandle AND state<>'CD')
			END CONVERSATION @DlgHandle;
		BREAK;
	END
	--  lse   !
	IF @MsgType='pp_lse_Request'
	BEGIN
		begin try
			--         (  XML).
			SET	@DB = CAST(@Msg as XML).value('(/DB)[1]', 'NVARCHAR(300)');
			--   [lse].[usp_RollForwardRecovery]
			--       @pp=1 (     )
			EXEC [sputnik].[lse].[usp_RollForwardRecovery] @DBName=@DB, @pp=1;
		end try
		begin catch
			SET @StrErr=N'   [lse].[usp_RollForwardRecovery]   [pp].usp_ExecProcessor.  : '+ERROR_MESSAGE();
			END CONVERSATION @DlgHandle WITH ERROR = 10 DESCRIPTION=@StrErr;
		end catch
	END
	--  backups   !
	IF @MsgType='pp_back_Request'
	BEGIN
		begin try
			DECLARE @TypeBack VARCHAR(4); -- !
			--            (  XML).
			SET	@DB = CAST(@Msg as XML).value('(/backups/db)[1]', 'NVARCHAR(300)');
			SET	@TypeBack = CAST(@Msg as XML).value('(/backups/type)[1]', 'VARCHAR(4)');
			--     [usp_StartBackup]   
			--  @pp=1 -       ! 
			EXEC [sputnik].[backups].[usp_StartBackup] @DBFilter=@DB, @type=@TypeBack, @pp=1;
		end try
		begin catch
			SET @StrErr=N'   [backups].[usp_StartBackup]   [pp].usp_ExecProcessor.  : ['+@DB+'];  : ['+@TypeBack+'].  : '+ERROR_MESSAGE();
			--       ,       !
			--       SQL Server ( print)    (break):
			PRINT(@StrErr);
			BREAK;
			--END CONVERSATION @DlgHandle WITH ERROR = 10 DESCRIPTION=@StrErr;
		end catch
	END
	--     ,   !
	ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
		END CONVERSATION @DlgHandle;
	ELSE IF @MsgType=N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
		END CONVERSATION @DlgHandle;
END
